INSERT INTO `tbl_product` (`productId`, `productName`, `catId`, `brandId`, `product_description`, `type`, `price`, `image`, `status`) VALUES
(1, 'Masstel Fami 60 4G', 1, 6, '<p><strong>Masstel Fami 60&nbsp;l&agrave; chiếc điện thoại&nbsp;cơ bản cực kỳ ph&ugrave; hợp d&agrave;nh cho c&aacute;c bậc phụ huynh khi sở hữu thiết kế m&agrave;n h&igrave;nh lớn, b&agrave;n ph&iacute;m số r&otilde; r&agrave;ng, danh bạ lưu 500 số điện thoại v&agrave; sở hữu tới hai đ&egrave;n pin si&ecirc;u s&aacute;ng. Sản phẩm được trang bị loa lớn v&agrave; c&ocirc;ng nghệ 4G kết nối nhanh, ổn định.</strong></p>\r\n<p><img id=\"Masstel Fami 60 4G 11\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/AnhNQ/2210/masstel-fami-60-4g-1.jpg\" alt=\"Masstel Fami 60 4G 1\" /></p>\r\n<h3><strong>C&ocirc;ng nghệ 4G cho kết nối ổn định</strong></h3>\r\n<p>Fami 60 tương th&iacute;ch với SIM 4G v&agrave; tận dụng tốt những lợi thế của mạng di động thế hệ thứ tư nhằm đảm bảo duy tr&igrave; kết nối ổn định, cho chất lượng đ&agrave;m thoại r&otilde; r&agrave;ng, xuy&ecirc;n suốt. Chiếc điện thoại phổ th&ocirc;ng n&agrave;y được trang bị loa ngo&agrave;i to, r&otilde;, đem lại chất lượng &acirc;m thanh trung thực m&agrave; vẫn tiết kiệm pin hiệu quả.</p>\r\n<p>Một lợi thế nữa về yếu tố nghe gọi l&agrave; Masstel Fami 60 c&ograve;n hỗ trợ 2 SIM 2 s&oacute;ng v&agrave; tương th&iacute;ch với mọi nh&agrave; mạng đang hoạt động tại Việt Nam như Viettel, MobiFone v&agrave; VinaPhone.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/AnhNQ/2210/masstel-fami-60-4g-2.jpg\" alt=\"C&ocirc;ng nghệ 4G cho kết nối ổn định 1\" /></p>\r\n<h3><strong>Chất lượng cuộc gọi r&otilde; n&eacute;t</strong></h3>\r\n<p>Với Masstel Fami 60, chất lượng cuộc đ&agrave;m thoại sẽ được n&acirc;ng cao hơn nhờ VoLTE. Đảm bảo truyền tải r&otilde; n&eacute;t &acirc;m thanh của người n&oacute;i ở đầu d&acirc;y b&ecirc;n n&agrave;y tới người nghe b&ecirc;n kia, đồng thời duy tr&igrave; t&iacute;nh ổn định trong suốt qu&aacute; tr&igrave;nh thực hiện cuộc gọi. Mọi thanh &acirc;m đều được t&aacute;i hiện r&otilde; r&agrave;ng v&agrave; nhanh ch&oacute;ng, đ&aacute;p ứng tốt nhu cầu của c&aacute;c kh&aacute;ch h&agrave;ng lớn tuổi.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/AnhNQ/2210/masstel-fami-60-4g-3.jpg\" alt=\"Chất lượng cuộc gọi r&otilde; n&eacute;t 1\" /></p>\r\n<h3><strong>Đ&agrave;m thoại hơn 8 tiếng, pin chờ 17 ng&agrave;y</strong></h3>\r\n<p>Một trong những thế mạnh nổi bật nhất của Masstel Fami 60 l&agrave; khả năng duy tr&igrave; pin. Cụ thể, nh&agrave; sản xuất Việt Nam đem đến cho thiết bị vi&ecirc;n pin dung lượng lớn 2.000 mAh c&oacute; thể đạt thời gian chờ chạm ngưỡng 17 ng&agrave;y li&ecirc;n tục kh&ocirc;ng cần sạc &ndash; cực kỳ ph&ugrave; hợp để sử dụng trong những chuyến đi xa v&agrave; kh&ocirc;ng đ&ograve;i hỏi chủ nh&acirc;n chiếc điện thoại phải nạp năng lượng mỗi ng&agrave;y như smartphone. Với vi&ecirc;n pin ấn tượng n&agrave;y, Fami 60 dễ d&agrave;ng duy tr&igrave; thời lượng đ&agrave;m thoại l&ecirc;n đến 8 tiếng.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/AnhNQ/2210/masstel-fami-60-4g-4.jpg\" alt=\"Đ&agrave;m thoại hơn 8 tiếng, pin chờ 17 ng&agrave;y 2\" /></p>\r\n<h3><strong>Kiểu d&aacute;ng bền bỉ, thanh lịch</strong></h3>\r\n<p>Sử dụng thiết kế tinh tế với m&agrave;n h&igrave;nh ngang rộng 2 inch, Masstel Fami 60 tạo dấu ấn về t&iacute;nh thẩm mỹ khi được trang bị mặt k&iacute;nh cong 2.5D tạo chiều s&acirc;u cho kh&ocirc;ng gian hiển thị h&igrave;nh ảnh. Khung viền kim loại chạy quanh th&acirc;n m&aacute;y đem đến n&eacute;t đẹp sang trọng, ấn tượng. Sản phẩm đem đến c&aacute;c t&ugrave;y chọn m&agrave;u sang trọng, cao cấp như V&agrave;ng v&agrave; Đen. B&agrave;n ph&iacute;m nổi với c&aacute;c chữ số được in to, r&otilde; r&agrave;ng tạo điều kiện thuận lợi để người d&ugrave;ng lớn tuổi quan s&aacute;t để thao t&aacute;c.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/AnhNQ/2210/masstel-fami-60-4g-5.jpg\" alt=\"Kiểu d&aacute;ng bền bỉ, thanh lịch 3\" /></p>\r\n<h3><strong>Ph&iacute;m th&ocirc;ng b&aacute;o SOS rất cần thiết</strong></h3>\r\n<p>L&agrave; sản phẩm được thiết kế v&agrave; chế t&aacute;c d&agrave;nh ri&ecirc;ng cho c&aacute;c bậc phụ huynh,&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai/masstel\">Masstel</a>&nbsp;đ&atilde; trang bị th&ecirc;m cho chiếc điện thoại Fami 60 một ph&iacute;m chức năng SOS chuy&ecirc;n dụng. Ph&iacute;m bấm n&agrave;y c&oacute; t&aacute;c dụng b&aacute;o động trong c&aacute;c t&iacute;nh huống khẩn cấp v&agrave; được đặt ở ngay giữa c&aacute;c ph&iacute;m điều hướng ở vị tr&iacute; dễ thấy nhất. Khi được k&iacute;ch hoạt, ph&iacute;m SOS sẽ ra lệnh cho Fami 60 li&ecirc;n lạc qua số điện thoại khẩn cấp thiết lập trước trong m&aacute;y, th&ocirc;ng b&aacute;o cho người th&acirc;n biết về t&igrave;nh trạng của chủ nh&acirc;n thiết bị.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/AnhNQ/2210/masstel-fami-60-4g-6.jpg\" alt=\"Ph&iacute;m th&ocirc;ng b&aacute;o SOS rất cần thiết \" /></p>\r\n<h3><strong>Hỗ trợ tới hai đ&egrave;n pin LED soi s&aacute;ng</strong></h3>\r\n<p>Đ&egrave;n pin lu&ocirc;n l&agrave; chức năng cần thiết tr&ecirc;n một thiết bị di động d&agrave;nh cho người lớn tuổi, Masstel Fami 60 đ&aacute;p ứng rất tốt nhu cầu chiếu s&aacute;ng của kh&aacute;ch h&agrave;ng khi trang bị tới hai đ&egrave;n pin LED c&oacute; tầm chiếu s&aacute;ng xa v&agrave; độ s&aacute;ng cao, r&otilde; r&agrave;ng. Một ph&iacute;m tắt/bật được bố tr&iacute; ngay tr&ecirc;n th&acirc;n m&aacute;y cho ph&eacute;p người d&ugrave;ng dễ d&agrave;ng bật l&ecirc;n để quan s&aacute;t trong đ&ecirc;m tối.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/AnhNQ/2210/masstel-fami-60-4g-7.jpg\" alt=\"Hỗ trợ tới hai đ&egrave;n pin LED soi s&aacute;ng 1\" /></p>', 0, '750000', 'a3b8271c34.jpg', 1),
(2, 'Masstel Izi 55 4G', 1, 6, '<p><strong>Với mức gi&aacute; phải chăng,&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai/masstel-izi-55-4g\">Masstel Fami IZI 55 4G</a>&nbsp;đem đến giải ph&aacute;p li&ecirc;n lạc tiện dụng v&agrave; đặc biệt ph&ugrave; hợp d&agrave;nh cho những kh&aacute;ch h&agrave;ng lớn tuổi nhờ thiết kế bền bỉ v&agrave; giao diện dễ sử dụng. Sản phẩm c&oacute; m&agrave;n h&igrave;nh lớn r&otilde; n&eacute;t, được trang bị đ&egrave;n pin si&ecirc;u s&aacute;ng soi r&otilde; trong đ&ecirc;m tối v&agrave; sở hữu danh bạ lưu trữ tới 500 số điện thoại.</strong></p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/AnhNQ/2210/masstel-fami-izi-55-4g%20(1).jpg\" alt=\"Kết nối ổn định với c&ocirc;ng nghệ 4G 1\" /></p>\r\n<h3><strong>Kết nối ổn định với c&ocirc;ng nghệ 4G</strong></h3>\r\n<p>Trải nghiệm nghe gọi v&agrave; nhắn tin m&agrave; Masstel IZI 55 4G đem tới hết sức ổn định v&agrave; r&otilde; r&agrave;ng khi sản phẩm hỗ trợ c&ocirc;ng nghệ kết nối 4G mạnh mẽ. Mọi cuộc đ&agrave;m thoại được thực hiện tr&ecirc;n chiến điện thoại n&agrave;y đều rất r&otilde; r&agrave;ng, sắc sảo. Chất lượng &acirc;m thanh cuộc gọi cực độ trung thực. Nhờ đ&oacute;, sản phẩm đ&aacute;p ứng xuất sắc nhu cầu cơ bản v&agrave; quan trọng nhất m&agrave; c&aacute;c bậc phụ huynh cần đến ở một thiết bị li&ecirc;n lạc di động.</p>\r\n<p><img id=\"Thiết kế bền bỉ v&agrave; chống chịu lực tốt 11\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/AnhNQ/2210/masstel-fami-izi-55-4g%20(2).jpg\" alt=\"Thiết kế bền bỉ v&agrave; chống chịu lực tốt 1\" /></p>\r\n<h3><strong>Thiết kế bền bỉ v&agrave; chống chịu lực tốt</strong></h3>\r\n<p>Masstel IZI 55 4G được trang bị khung vỏ rất bền, ng&ocirc;n ngữ thiết kế m&agrave; sản phẩm đem đến dễ g&acirc;y thiện cảm cho cho người d&ugrave;ng ngay từ lần đầu ti&ecirc;n nh&igrave;n ngắm. Khung viền kim loại bao quanh th&acirc;n m&aacute;y kh&ocirc;ng chỉ tạo th&agrave;nh điểm nhấn thẩm mỹ m&agrave; c&ograve;n gi&uacute;p sản phẩm chống chịu lực tốt hơn khi kết hợp c&ugrave;ng chất liệu nhựa ABS si&ecirc;u bền.&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai/masstel\">Masstel</a>&nbsp;đ&atilde; tiến h&agrave;nh thử nghiệm để chắc chắn IZI 55 c&oacute; thể vận h&agrave;nh tốt ngay cả trong điều kiện nhiệt độ m&ocirc;i trường thay đổi mạnh.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/AnhNQ/2210/masstel-fami-izi-55-4g%20(3).jpg\" alt=\"Thiết kế bền bỉ v&agrave; chống chịu lực tốt 2\" /></p>\r\n<h3><strong>M&agrave;n h&igrave;nh 2.4 inch giao diện th&acirc;n thiện</strong></h3>\r\n<p>Ẩn b&ecirc;n dưới mặt k&iacute;nh cong 2.5D được thiết kế theo dạng tai thỏ đầy t&iacute;nh thẩm mỹ l&agrave; kh&ocirc;ng gian hiển thị 2.4 inch m&agrave;n h&igrave;nh m&agrave;u rất th&acirc;n thiện. Giao diện người d&ugrave;ng của sản phẩm được thiết kế với cỡ chữ to, r&otilde; n&eacute;t. C&aacute;c biểu tượng t&iacute;nh năng đều được Masstel tạo h&igrave;nh lớn v&agrave; m&ocirc; tả sắc sảo dễ nhận biết, hỗ trợ nhiều cho qu&aacute; tr&igrave;nh thao t&aacute;c của người d&ugrave;ng lớn tuổi.</p>\r\n<p>Kh&ocirc;ng chỉ vậy, b&agrave;n ph&iacute;m chữ số lớn cũng l&agrave; một lợi thế lớn để bạn c&acirc;n nhắc chọn mua sản phẩm&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai/masstel\">Masstel</a>&nbsp;n&agrave;y như m&oacute;n qu&agrave; tặng &yacute; nghĩa v&agrave; thiết thực cho &ocirc;ng b&agrave;, cha mẹ.</p>\r\n<p><img id=\"M&agrave;n h&igrave;nh 2.4 inch giao diện th&acirc;n thiện 11\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/AnhNQ/2210/masstel-fami-izi-55-4g%20(6).jpg\" alt=\"M&agrave;n h&igrave;nh 2.4 inch giao diện th&acirc;n thiện 1\" /></p>\r\n<h3><strong>Duy tr&igrave; cuộc gọi suốt 8 tiếng li&ecirc;n tục</strong></h3>\r\n<p>Việc gọi điện tr&ecirc;n Masstel IZI 55 4G sẽ được thực hiện xuy&ecirc;n suốt trong thời gian d&agrave;i tối đa l&agrave; t&aacute;m tiếng. Để đảm bảo điều n&agrave;y, nh&agrave; sản xuất đem đến cho IZI 55 4G vi&ecirc;n pin dung lượng lớn 1.800 mAh &ndash; th&ocirc;ng số cực kỳ l&yacute; tưởng nếu so với với những mẫu&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai\">điện thoại</a>&nbsp;cơ bản c&ugrave;ng ph&acirc;n kh&uacute;c. Masstel cho biết, nếu chỉ t&iacute;nh ri&ecirc;ng thời gian chờ th&igrave; sản phẩm c&oacute; thể duy tr&igrave; tối đa 15 ng&agrave;y kh&ocirc;ng ngừng nghỉ.</p>\r\n<p><img id=\"Đ&egrave;n pin si&ecirc;u s&aacute;ng, k&iacute;ch hoạt qua ph&iacute;m tắt 11\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/AnhNQ/2210/masstel-fami-izi-55-4g%20(5).jpg\" alt=\"Đ&egrave;n pin si&ecirc;u s&aacute;ng, k&iacute;ch hoạt qua ph&iacute;m tắt 1\" /></p>\r\n<h3><strong>Đ&egrave;n pin si&ecirc;u s&aacute;ng, k&iacute;ch hoạt qua ph&iacute;m tắt</strong></h3>\r\n<p>Kh&aacute;c với c&aacute;c sản phẩm c&oacute; nhiều t&iacute;nh năng cầu kỳ nhưng phức tạp, Masstel IZI 55 4G đề cao t&iacute;nh thực dụng v&agrave; trang bị nhiều t&iacute;nh năng cần thiết d&agrave;nh cho người lớn tuổi. Trong đ&oacute; nổi bật nhất l&agrave; đ&egrave;n pin được trang bị ở cạnh tr&ecirc;n của m&aacute;y c&oacute; thể chiếu s&aacute;ng tốt trong tầm nh&igrave;n xa. Kh&ocirc;ng chỉ vậy, nh&agrave; sản xuất c&ograve;n trang bị th&ecirc;m ph&iacute;m bật/tắt nhanh tiện lợi c&oacute; thể k&iacute;ch hoạt đ&egrave;n pin nhanh ch&oacute;ng.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/AnhNQ/2210/masstel-fami-izi-55-4g%20(8).jpg\" alt=\"Duy tr&igrave; cuộc gọi suốt 8 tiếng li&ecirc;n tục 1\" /></p>\r\n<h3><strong>Danh bạ lưu 500 số điện thoại</strong></h3>\r\n<p>Bộ nhớ trong của IZI 50 4G được x&acirc;y dựng để c&oacute; thể lưu trữ to&agrave;n bộ danh bạ với 500 số điện thoại b&ecirc;n trong. Đ&acirc;y l&agrave; lợi thế gi&uacute;p sản phẩm tận dụng tốt c&ocirc;ng nghệ 2 SIM 2 s&oacute;ng để nghe gọi, nhắn tin v&agrave; duy tr&igrave; ưu đ&atilde;i tốt từ c&aacute;c nh&agrave; mạng lớn như Viettel, VinaPhone hoặc MobiFone.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/AnhNQ/2210/masstel-fami-izi-55-4g%20(7)(1).jpg\" alt=\"Danh bạ lưu 500 số điện thoại 1\" /></p>', 0, '690000', 'e1a861fe3e.jpg', 1),
(3, 'Nokia C21 Plus', 1, 7, '<p><strong>Khoe d&aacute;ng vẻ tinh tế với độ bền đạt ti&ecirc;u chuẩn Ch&acirc;u &Acirc;u, chiếc&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai\">điện thoại</a>&nbsp;Nokia C21 Plus đem lại những trải nghiệm vượt trội so với tầm gi&aacute;. Sản phẩm được trang bị vi&ecirc;n pin dung lượng lớn 5.050 mAh, sở hữu m&agrave;n h&igrave;nh 6.5 inch chuẩn HD+ hứa hẹn đem lại những khung h&igrave;nh sống động đ&aacute;p ứng trọn vẹn mọi nội dung giải tr&iacute;.</strong></p>\r\n<p align=\"center\"><img id=\"M&ocirc; tả sản phẩm Nokia C21 Plus 11\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/BaoPK/TechAwards/mo-ta-san-pham-nokia-c21-plus-1.JPG\" alt=\"M&ocirc; tả sản phẩm Nokia C21 Plus 1\" /></p>\r\n<h3><strong>Tận hưởng những khung h&igrave;nh chất lượng</strong></h3>\r\n<p><a href=\"https://fptshop.com.vn/dien-thoai/nokia-c21-plus\">Nokia C21 Plus</a>&nbsp;được trang bị m&agrave;n h&igrave;nh giọt nước rộng 6.5 inch chuẩn HD+ đem đến những khu&ocirc;n h&igrave;nh cực kỳ ch&acirc;n thực v&agrave; sống động. Tr&ecirc;n kh&ocirc;ng gian hiển thị rộng lớn, bạn sẽ thoải m&aacute;i tận hưởng những nội dung từ giải tr&iacute;, học tập, l&agrave;m việc, lướt web v&agrave; mạng x&atilde; hội. Vi&ecirc;n pin khủng được thiết kế để c&oacute; thể đ&aacute;p ứng trọn vẹn nhu cầu sử dụng năng lượng từ m&agrave;n h&igrave;nh lớn.</p>\r\n<p align=\"center\"><img id=\"Tận hưởng những khung h&igrave;nh chất lượng 11\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/BaoPK/TechAwards/mo-ta-san-pham-nokia-c21-plus-7.JPG\" alt=\"Tận hưởng những khung h&igrave;nh chất lượng 1\" /></p>\r\n<h3><strong>Bảo mật ch&iacute;nh x&aacute;c v&agrave; nhanh ch&oacute;ng nhất</strong></h3>\r\n<p>Nh&agrave; sản xuất Phần Lan cam đoan sẽ n&acirc;ng cấp hệ thống phần mềm bảo mật tr&ecirc;n Nokia C21 Plus trong hai năm với những bản cập nhật mỗi qu&yacute;, được gửi tới từ trung t&acirc;m dữ liệu hiện đại. To&agrave;n bộ c&aacute;c t&iacute;nh năng li&ecirc;n quan đến cơ chế bảo mật người d&ugrave;ng như cảm biến v&acirc;n tay, mở kh&oacute;a khu&ocirc;n mặt đều đảm bảo vận h&agrave;nh ch&iacute;nh x&aacute;c với tốc độ nhanh nhạy tối đa.</p>\r\n<p><img id=\"Bảo mật ch&iacute;nh x&aacute;c v&agrave; nhanh ch&oacute;ng nhất 11\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/BaoPK/TechAwards/mo-ta-san-pham-nokia-c21-plus-5.JPG\" alt=\"Bảo mật ch&iacute;nh x&aacute;c v&agrave; nhanh ch&oacute;ng nhất 1\" /></p>\r\n<h3><strong>Kế thừa độ bền đặc trưng của Nokia</strong></h3>\r\n<p>Kho&aacute;c l&ecirc;n m&igrave;nh thiết kế thanh lịch với lớp vỏ polycarbonate, Nokia C21 Plus kế thừa tinh thần bền bỉ l&agrave;m n&ecirc;n t&ecirc;n tuổi cho những chiếc&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai/nokia\">điện thoại Nokia</a>&nbsp;trước kia. Sản phẩm được kiểm định kỹ c&agrave;ng về khả năng chịu t&aacute;c động trước ngoại lực h&agrave;ng ng&agrave;y th&ocirc;ng qua c&aacute;c b&agrave;i kiểm tra thực tế lặp lại nhiều lần. Do đ&oacute;, bạn sẽ an t&acirc;m hơn về độ bền v&agrave; khả năng phục vụ của sản phẩm xuy&ecirc;n suốt v&ograve;ng đời trải nghiệm chiếc điện thoại.</p>\r\n<p><img id=\"Kế thừa độ bền đặc trưng của Nokia 11\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/BaoPK/TechAwards/mo-ta-san-pham-nokia-c21-plus-2.JPG\" alt=\"Kế thừa độ bền đặc trưng của Nokia 1\" /></p>\r\n<h3><strong>Thời lượng pin từ hai đến ba ng&agrave;y</strong></h3>\r\n<p>Phụ tr&aacute;ch cung ứng năng lượng vận h&agrave;nh cho hệ thống l&agrave; vi&ecirc;n pin dung lượng 5.050&nbsp; mAh. Nhờ sở hữu vi&ecirc;n pin lớn, sản phẩm dễ d&agrave;ng k&eacute;o d&agrave;i thời lượng sử dụng từ hai đến ba ng&agrave;y sau mỗi lần sạc đầy. Bạn sẽ thoải m&aacute;i thực hiện mọi t&aacute;c vụ th&ocirc;ng thường như lướt web, chơi game, xem video, gọi điện v&agrave; sử dụng mạng x&atilde; hội m&agrave; kh&ocirc;ng lo hết pin khi cầm trong tay Nokia C21 Plus.</p>\r\n<p><img id=\"Thời lượng pin từ hai đến ba ng&agrave;y 11\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/BaoPK/TechAwards/mo-ta-san-pham-nokia-c21-plus-6.JPG\" alt=\"Thời lượng pin từ hai đến ba ng&agrave;y 1\" /></p>\r\n<h3><strong>Camera k&eacute;p cho khu&ocirc;n h&igrave;nh ho&agrave;n hảo</strong></h3>\r\n<p>Tr&ecirc;n mặt lưng, Nokia C21 Plus sở hữu hệ thống camera k&eacute;p 13MP + 2MP sắc n&eacute;t kết hợp c&ugrave;ng đ&egrave;n flash chất lượng cao. Nhờ đ&oacute;, sản phẩm c&oacute; thể ghi lại mọi khoảnh khắc ấn tượng lướt qua trong cuộc sống của bạn th&ocirc;ng qua những khung h&igrave;nh, thước phim thực sự ho&agrave;n hảo.&nbsp;<a href=\"https://fptshop.com.vn/tin-tuc/danh-gia/chup-anh-hdr-la-gi-20912\">C&ocirc;ng nghệ HDR</a>&nbsp;m&agrave; Nokia đem đến cho ph&eacute;p C21 Plus thể hiện tốt c&aacute;c nội dung ở cả những khung cảnh s&aacute;ng v&agrave; tối.</p>\r\n<p><img id=\"Camera k&eacute;p cho khu&ocirc;n h&igrave;nh ho&agrave;n hảo 11\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/BaoPK/TechAwards/mo-ta-san-pham-nokia-c21-plus-3.JPG\" alt=\"Camera k&eacute;p cho khu&ocirc;n h&igrave;nh ho&agrave;n hảo 1\" /></p>\r\n<h3><strong>Trải nghiệm phần mềm mượt m&agrave;, thời thượng</strong></h3>\r\n<p>Khi đến tay người d&ugrave;ng, chiếc điện thoại Nokia C21 Plus đ&atilde; được c&agrave;i đặt sẵn hệ điều h&agrave;nh Android 11 (phi&ecirc;n bản Go) mới nhất. Chủ nh&acirc;n thiết bị sẽ thoải m&aacute;i tận hưởng những tiện &iacute;ch h&agrave;ng đầu của nền tảng do Google ph&aacute;t triển với giao diện thuần Android, rất trực quan, đơn giản, dễ d&ugrave;ng v&agrave; nịnh mắt. Bạn sẽ tập trung hơn v&agrave;o những ứng dụng cơ bản cần thiết v&agrave; tiết kiệm được nhiều kh&ocirc;ng gian lưu trữ cho c&aacute;c ứng dụng v&agrave; nội dung kh&aacute;c m&agrave; m&igrave;nh y&ecirc;u th&iacute;ch.</p>\r\n<p><img id=\"Trải nghiệm phần mềm mượt m&agrave;, thời thượng 11\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/BaoPK/TechAwards/mo-ta-san-pham-nokia-c21-plus-4.JPG\" alt=\"Trải nghiệm phần mềm mượt m&agrave;, thời thượng 1\" /></p>', 0, '2990000', '6e6ed770f3.jpg', 1),
(4, 'Nokia G10', 1, 7, '<p><strong>Với thiết kế thanh lịch v&agrave; độ bền bỉ l&agrave;m n&ecirc;n t&ecirc;n tuổi của c&aacute;c thiết bị&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai/nokia\">Nokia</a>, chiếc điện thoại&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai/nokia-g10\">Nokia G10</a>&nbsp;sẽ c&ugrave;ng bạn đồng h&agrave;nh trong nhiều năm tới. Sản phẩm được trang bị camera vượt trội tầm gi&aacute;, hỗ trợ bạn bắt trọn vẹn c&aacute;c khoảnh khắc ấn tượng trong cuộc sống. Ngo&agrave;i ra, dung lượng pin d&agrave;i l&acirc;u cũng gi&uacute;p bạn giảm bớt c&aacute;c băn khoăn về thời lượng trong qu&aacute; tr&igrave;nh trải nghiệm.</strong></p>\r\n<p><img id=\"Nokia G10 4G-64GB 11\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/BaoPK/lenovo-thinkpad-e460/nokia-g10-4g-64gb-1.JPG\" alt=\"Nokia G10 4G-64GB 1\" /></p>\r\n<h3><strong>Ngắm nh&igrave;n cuộc sống qua m&agrave;n h&igrave;nh 6.5 inch</strong></h3>\r\n<p>Nokia G10 sở hữu thế mạnh về kh&ocirc;ng gian hiển thị khi nh&agrave; sản xuất trang bị cho chiếc&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai/tren-60-inch\">smartphone m&agrave;n h&igrave;nh lớn</a>&nbsp;6.52 inch độ ph&acirc;n giải HD+ (720 x 1600 pixels). C&ocirc;ng nghệ hiện đại m&agrave; Nokia đem tới gi&uacute;p kh&ocirc;ng gian trải nghiệm h&igrave;nh ảnh của G10 c&oacute; độ s&aacute;ng cao v&agrave; hết sức sắc n&eacute;t.</p>\r\n<p>Khi m&agrave; mọi t&aacute;c vụ thường ng&agrave;y trong cuộc sống như học tập, l&agrave;m việc v&agrave; giải tr&iacute; đều được thực hiện qua smartphone, lợi thế về m&agrave;n h&igrave;nh sẽ gi&uacute;p bạn c&oacute; được trải nghiệm trọn vẹn nhất khi xem phim, YouTube, lướt mạng x&atilde; hội hay họp h&agrave;nh, học tập online.</p>\r\n<p><img id=\"Li&ecirc;n tục cập nhật để tăng t&iacute;nh bảo mật 11\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/BaoPK/lenovo-thinkpad-e460/nokia-g10-4g-64gb-4.JPG\" alt=\"Li&ecirc;n tục cập nhật để tăng t&iacute;nh bảo mật 1\" /></p>\r\n<h3><strong>Thời lượng pin tối đa ba ng&agrave;y</strong></h3>\r\n<p>Để gi&uacute;p người d&ugrave;ng kh&ocirc;ng gặp phải phiền to&aacute;i khi smartphone hết pin giữa chừng, Nokia đ&atilde; ph&aacute;t huy ưu thế vốn c&oacute; của h&atilde;ng v&agrave; trang bị cho chiếc điện thoại G10 vi&ecirc;n pin dung lượng lớn 5050 mAh. Sự kết hợp giữa chip xử l&yacute; tiết kiệm năng lượng c&ugrave;ng phần mềm được tối ưu để sử dụng pin hợp l&yacute;, từ đ&oacute; k&eacute;o d&agrave;i thời lượng trải nghiệm giữa mỗi lần sạc l&ecirc;n đến ba ng&agrave;y.</p>\r\n<p>Bạn c&oacute; thể an t&acirc;m rằng Nokia G10 lu&ocirc;n sẵn s&agrave;ng phục vụ bạn ngay cả trong những chuyến đi du lịch hoặc c&ocirc;ng t&aacute;c xa &ndash; khi ch&uacute;ng ta kh&ocirc;ng c&oacute; điều kiện để sạc pin li&ecirc;n tục cho smartphone.</p>\r\n<p><img id=\"Thời lượng pin tối đa ba ng&agrave;y 11\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/BaoPK/lenovo-thinkpad-e460/nokia-g10-4g-64gb-7.jpg\" alt=\"Thời lượng pin tối đa ba ng&agrave;y 1\" /></p>\r\n<h3><strong>Li&ecirc;n tục cập nhật để tăng t&iacute;nh bảo mật</strong></h3>\r\n<p>Nokia cam kết rằng h&atilde;ng sẽ li&ecirc;n tục cập nhật bảo mật thường xuy&ecirc;n cho G10. Việc n&acirc;ng cấp sẽ diễn ra h&agrave;ng th&aacute;ng trong ba năm li&ecirc;n tục kể từ thời điểm ra mắt. Do đ&oacute;, chiếc smartphone sẽ được bảo vệ an to&agrave;n trước c&aacute;c m&atilde; độc v&agrave; biện ph&aacute;p th&acirc;m nhập ng&agrave;y c&agrave;ng tinh vi của giới hacker nhằm đ&aacute;nh cắp th&ocirc;ng tin c&aacute; nh&acirc;n của ch&uacute;ng ta. Nokia G10 sẽ giữ cho h&igrave;nh ảnh, video v&agrave; dữ liệu thanh to&aacute;n lu&ocirc;n nằm trong phạm vi an to&agrave;n trong m&ocirc;i trường mạng phức tạp như hiện nay.</p>\r\n<p><img id=\"Trải nghiệm những g&igrave; mới nhất tr&ecirc;n Android 11 11\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/BaoPK/lenovo-thinkpad-e460/nokia-g10-4g-64gb-3.JPG\" alt=\"Trải nghiệm những g&igrave; mới nhất tr&ecirc;n Android 11 1\" /></p>\r\n<h3><strong>Trải nghiệm những g&igrave; mới nhất tr&ecirc;n Android 11</strong></h3>\r\n<p>Nokia G10 được trang bị sẵn&nbsp;<a href=\"https://fptshop.com.vn/tin-tuc/danh-gia/6-tinh-nang-moi-tot-nhat-co-tren-android-11-ma-ban-nen-biet-125534\">hệ điều h&agrave;nh Android 11</a>&nbsp;mới nhất của Google. Lợi thế về phần mềm cho ph&eacute;p bạn thoải m&aacute;i trải nghiệm những t&iacute;nh năng mới nhất, hiện đại nhất như kiểm so&aacute;t cuộc hội thoại tr&ecirc;n nhiều ứng dụng chat ở c&ugrave;ng một vị tr&iacute;, k&iacute;ch hoạt nhanh trợ l&yacute; ảo Google Assistant v&agrave; giao diện người d&ugrave;ng t&ugrave;y biến đẹp mắt.</p>\r\n<p>Nh&agrave; sản xuất sẽ ưu ti&ecirc;n cập nhật nền tảng Android mới cho Nokia G10 trong hai năm, đảm bảo bạn sẽ được trải nghiệm phần mềm v&agrave; t&iacute;nh năng mới nhất như ng&agrave;y đầu sử dụng.</p>\r\n<p><img id=\"Ngắm nh&igrave;n cuộc sống qua m&agrave;n h&igrave;nh 6.5 inch 11\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/BaoPK/lenovo-thinkpad-e460/nokia-g10-4g-64gb-2.JPG\" alt=\"Ngắm nh&igrave;n cuộc sống qua m&agrave;n h&igrave;nh 6.5 inch 1\" /></p>\r\n<h3><strong>Sở hữu bộ ba camera xuất sắc</strong></h3>\r\n<p>Tr&ecirc;n mặt lưng, Nokia G10 sở hữu bộ ba camera xuất sắc 13MP + 2MP + 2MP. Trải nghiệm thực tế cho thấy những khu&ocirc;n h&igrave;nh chụp được từ hệ thống camera n&agrave;y c&oacute; độ chi tiết tốt v&agrave; m&agrave;u sắc ch&acirc;n thực, gi&uacute;p bạn dễ d&agrave;ng bắt được những khoảnh khắc kỉ niệm đặc biệt với gia đ&igrave;nh, bạn b&egrave; một c&aacute;ch dễ d&agrave;ng.</p>\r\n<p><img id=\"Sở hữu bộ ba camera xuất sắc 11\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/BaoPK/lenovo-thinkpad-e460/nokia-g10-4g-64gb-8.jpg\" alt=\"Sở hữu bộ ba camera xuất sắc 1\" /></p>\r\n<h3><strong>Tối giản, thanh lịch v&agrave; đầy t&iacute;nh thẩm mỹ</strong></h3>\r\n<p>L&agrave; một sản phẩm được chế t&aacute;c bởi nh&agrave; sản xuất Phần Lan, kiểu d&aacute;ng của Nokia G10 mang đậm dấu ấn Bắc &Acirc;u với phong c&aacute;ch tối giản đầy thanh lịch. Cầm chiếc&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai\">điện thoại</a>&nbsp;tr&ecirc;n tay, bạn sẽ cảm nhận được n&eacute;t đẹp hiện đại ẩn b&ecirc;n trong bộ khung vỏ bền nhẹ. C&aacute;c đường viền m&agrave;n h&igrave;nh đều rất mỏng, camera selfie được đưa v&agrave;o kh&ocirc;ng gian hiển thị một c&aacute;ch tinh tế, c&aacute;ch bố tr&iacute; logo Nokia c&ugrave;ng những trung t&iacute;nh gi&uacute;p t&ocirc;n l&ecirc;n phong c&aacute;ch đặc biệt trang nh&atilde; của sản phẩm.</p>\r\n<p><img id=\"Tối giản, thanh lịch v&agrave; đầy t&iacute;nh thẩm mỹ 11\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/BaoPK/lenovo-thinkpad-e460/nokia-g10-4g-64gb-5.JPG\" alt=\"Tối giản, thanh lịch v&agrave; đầy t&iacute;nh thẩm mỹ 1\" /></p>\r\n<h3><strong>Bảo mật v&acirc;n tay, nhận diện khu&ocirc;n mặt</strong></h3>\r\n<p>Nokia G10 được trang bị cảm biến nhận diện v&acirc;n tay ở cạnh b&ecirc;n v&agrave; sử dụng cơ chế qu&eacute;t th&ocirc;ng minh để nhận diện khu&ocirc;n mặt người d&ugrave;ng cho thao t&aacute;c mở kh&oacute;a. Do đ&oacute;, bạn kh&ocirc;ng cần phải lo lắng khi c&oacute; ai đ&oacute; sử dụng thiết bị di động m&agrave; kh&ocirc;ng c&oacute; sự cho ph&eacute;p của bạn.</p>\r\n<p><img id=\"Bảo mật v&acirc;n tay, nhận diện khu&ocirc;n mặt 11\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/BaoPK/lenovo-thinkpad-e460/nokia-g10-4g-64gb-6.jpg\" alt=\"Bảo mật v&acirc;n tay, nhận diện khu&ocirc;n mặt 1\" /></p>', 0, '3190000', 'c0dea80a7e.jpg', 1),
(5, 'Nokia G21', 1, 7, '<p><strong>Đến từ thương hiệu nổi danh bậc nhất thế giới&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai\">điện thoại</a>,&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai/nokia-g21\">Nokia G21</a>&nbsp;đem đến cho bạn trải nghiệm vượt trội với thời lượng pin l&ecirc;n đến 3 ng&agrave;y, camera 50MP kết hợp c&ugrave;ng c&ocirc;ng nghệ AI xuất sắc. Ngo&agrave;i ra, sản phẩm c&ograve;n được trang bị bộ khung vỏ polycarbonate b&oacute;ng bẩy, si&ecirc;u bền c&ugrave;ng hệ điều h&agrave;nh cập nhật li&ecirc;n tục suốt 2 năm.</strong></p>\r\n<p><img id=\"Nokia G21 6GB-128GB 11\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/BaoPK/TamNeniPhone7/nokia-g21-6gb-128gb-1.JPG\" alt=\"Nokia G21 6GB-128GB 1\" /></p>\r\n<h3><strong>Trải nghiệm pin ba ng&agrave;y d&agrave;i l&acirc;u</strong></h3>\r\n<p>Thời lượng pin d&agrave;i từ l&acirc;u đ&atilde; trở th&agrave;nh bản sắc đặc trưng của c&aacute;c sản phẩm&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai/nokia\">Nokia</a>. Nh&agrave; sản xuất Phần Lan lu&ocirc;n biết c&aacute;ch khiến người d&ugrave;ng h&agrave;i l&ograve;ng về trải nghiệm pin tr&ecirc;n c&aacute;c sản phẩm của m&igrave;nh. Nokia G21 tiếp tục ph&aacute;t huy truyền thống n&agrave;y khi ghi nhận thời lượng hoạt động k&eacute;o d&agrave;i ba ng&agrave;y, kết hợp c&ugrave;ng c&ocirc;ng nghệ sạc nhanh 18W để gi&uacute;p bạn nhanh ch&oacute;ng nạp đầy pin khi cần thiết.</p>\r\n<p><img id=\"Trải nghiệm pin ba ng&agrave;y d&agrave;i l&acirc;u 11\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/BaoPK/TamNeniPhone7/nokia-g21-6gb-128gb-2.JPG\" alt=\"Trải nghiệm pin ba ng&agrave;y d&agrave;i l&acirc;u 1\" /></p>\r\n<h3><strong>Ưu ti&ecirc;n cập nhật phần mềm trong 2 năm</strong></h3>\r\n<p>Nokia cam đoan cập nhật hệ điều h&agrave;nh cho G21 trong năm kể từ ng&agrave;y ra mắt. Nhờ đ&oacute;, chủ nh&acirc;n chiếc điện thoại sẽ lu&ocirc;n cảm thấy an t&acirc;m khi trải nghiệm thiết bị. Việc n&acirc;ng cấp phần mềm thường xuy&ecirc;n c&ograve;n đảm bảo t&iacute;nh bảo mật gấp hai lần so với c&aacute;c d&ograve;ng sản phẩm đối thủ, bảo vệ bạn khỏi sự tấn c&ocirc;ng từ c&aacute;c nguy cơ tr&ecirc;n internet trong qu&aacute; tr&igrave;nh sử dụng Nokia G21.</p>\r\n<p><img id=\"Ưu ti&ecirc;n cập nhật phần mềm trong 2 năm 11\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/BaoPK/TamNeniPhone7/nokia-g21-6gb-128gb-3.JPG\" alt=\"Ưu ti&ecirc;n cập nhật phần mềm trong 2 năm 1\" /></p>\r\n<h3><strong>Bộ ba camera 50MP với t&iacute;nh năng chụp AI</strong></h3>\r\n<p>Nokia G21 được trang bị bộ ba camera hết sức xuất sắc, trong đ&oacute; c&oacute; sự g&oacute;p mặt của camera ch&iacute;nh 50MP, camera macro 2MP v&agrave; camera đo chiều s&acirc;u trường ảnh 2MP. Việc chụp được những khu&ocirc;n h&igrave;nh chất lượng l&agrave; điều chẳng c&ograve;n kh&oacute; khăn với sự kết hợp của bộ ba camera n&agrave;y. Sản phẩm cũng được t&iacute;ch hợp k&egrave;m nhiều t&iacute;nh năng để hỗ trợ chụp trong m&ocirc;i trường thiếu s&aacute;ng, gi&uacute;p bạn biến những &yacute; tưởng tuyệt vời nhất th&agrave;nh hiện thực.</p>\r\n<p><img id=\"Bộ ba camera 50MP với t&iacute;nh năng chụp AI 11\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/BaoPK/TamNeniPhone7/nokia-g21-6gb-128gb-4.JPG\" alt=\"Bộ ba camera 50MP với t&iacute;nh năng chụp AI 1\" /></p>\r\n<h3><strong>Tần số qu&eacute;t mượt m&agrave; của m&agrave;n h&igrave;nh lớn</strong></h3>\r\n<p>Sở hữu kh&ocirc;ng gian hiển thị h&igrave;nh ảnh 6.5 inch, Nokia G21 sẽ gi&uacute;p bạn tận hưởng mọi nội dung giải tr&iacute; đa phương tiện theo c&aacute;ch ấn tượng nhất. Độ ph&acirc;n giải HD+ (720 x 1.600 pixels) đảm bảo mọi khu&ocirc;n h&igrave;nh đều được t&aacute;i hiện một c&aacute;ch sắc n&eacute;t. Kh&ocirc;ng chỉ vậy, mỗi chuyển động đều được G21 diễn tả một c&aacute;ch mượt m&agrave; qua mức&nbsp;<a href=\"https://fptshop.com.vn/tin-tuc/danh-gia/toc-do-lam-tuoi-man-hinh-60hz-90hz-hoac-120hz-co-nghia-la-gi-113378\">tần số qu&eacute;t 90Hz</a>. Từng c&uacute; vuốt chạm, mỗi t&aacute;c vụ chuyển cảnh đều hết sức nịnh mắt.</p>\r\n<p><img id=\"Tần số qu&eacute;t mượt m&agrave; của m&agrave;n h&igrave;nh lớn 11\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/BaoPK/TamNeniPhone7/nokia-g21-6gb-128gb-5.JPG\" alt=\"Tần số qu&eacute;t mượt m&agrave; của m&agrave;n h&igrave;nh lớn 1\" /></p>\r\n<h3><strong>Kiểu d&aacute;ng tinh tế v&agrave; thiết kế th&acirc;n thiện</strong></h3>\r\n<p>Nokia G21 kho&aacute;c l&ecirc;n m&igrave;nh diện mạo hết sức tinh tế với m&agrave;n h&igrave;nh chiếm trọn diện t&iacute;ch mặt trước, camera selfie được đưa v&agrave;o trong kh&ocirc;ng gian hiển thị một c&aacute;ch nhẹ nh&agrave;ng theo dạng &ldquo;giọt nước&rdquo;. Ở mặt sau, nh&agrave; sản xuất lựa chọn c&aacute;ch ho&agrave;n thiện mặt lưng theo dạng v&acirc;n nh&aacute;m nổi ph&iacute;a sau, đem lại cảm nhận cầm nắm ch&acirc;n thật, đồng thời hạn chế hiện tượng lưu mồ h&ocirc;i, dấu v&acirc;n tay khi d&ugrave;ng. Phi&ecirc;n bản bạn đang theo d&otilde;i c&oacute; hai t&ugrave;y chọn m&agrave;u l&agrave; Xanh v&agrave; T&iacute;m, rất thanh lịch v&agrave; ph&ugrave; hợp với nhiều gu thẩm mỹ.</p>\r\n<p><img id=\"Kiểu d&aacute;ng tinh tế v&agrave; thiết kế th&acirc;n thiện 11\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/BaoPK/TamNeniPhone7/nokia-g21-6gb-128gb-7.JPG\" alt=\"Kiểu d&aacute;ng tinh tế v&agrave; thiết kế th&acirc;n thiện 1\" /></p>\r\n<h3><strong>Đ&aacute;p ứng tốt mọi t&aacute;c vụ bạn cần</strong></h3>\r\n<p>Về yếu tố sức mạnh, Nokia G21 được trang bị chip xử l&yacute; UNISOC T606, đ&acirc;y l&agrave; con chip t&aacute;m nh&acirc;n mạnh mẽ gồm hai nh&acirc;n Cortex-A75 v&agrave; s&aacute;u nh&acirc;n Cortex-A55, xung nhịp tối đa 1.8GHz đảm bảo đủ hiệu năng để đ&aacute;p ứng tốt mọi t&aacute;c vụ bạn cần trong ng&agrave;y. Dung lượng RAM 6GB v&agrave; bộ nhớ trong 128GB đảm bảo năng lực đa nhiệm, lưu trữ dữ liệu tốt.</p>\r\n<p><img id=\"Đ&aacute;p ứng tốt mọi t&aacute;c vụ bạn cần 11\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/BaoPK/TamNeniPhone7/nokia-g21-6gb-128gb-8.JPG\" alt=\"Đ&aacute;p ứng tốt mọi t&aacute;c vụ bạn cần 1\" /></p>', 1, '4090000', 'd1824a9dfa.jpg', 1),
(6, 'Vivo Y33s', 1, 8, '<p><strong>Kh&ocirc;ng chỉ ghi điểm với kiểu d&aacute;ng thời trang, th&acirc;n m&aacute;y si&ecirc;u mỏng v&agrave; m&agrave;u sắc nịnh mắt,&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai/vivo-y33s\">vivo Y33s</a>&nbsp;c&ograve;n cho thấy sức hấp dẫn kh&oacute; cưỡng trong ph&acirc;n kh&uacute;c tầm trung nhờ vi&ecirc;n pin dung lượng 5.000 mAh, m&agrave;n h&igrave;nh 6.58 inch tươi s&aacute;ng, camera 50MP chụp đ&ecirc;m ấn tượng v&agrave; hiệu năng vượt trội c&aacute;c đối thủ c&ugrave;ng tầm gi&aacute;.</strong></p>\r\n<p><strong><img id=\"Vivo Y33s 8GB-128GB 11\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/AnhNQ/3110/vivo-y33s-8gb-128gb-1.JPG\" alt=\"Vivo Y33s 8GB-128GB 1\" /></strong></p>\r\n<h3><strong>Mỏng nhẹ, thời trang v&agrave; hơn thế nữa</strong></h3>\r\n<p>Kho&aacute;c l&ecirc;n m&igrave;nh th&acirc;n m&aacute;y mỏng chỉ 8mm v&agrave; trọng lượng vỏn vẹn 182 gram, vivo Y33s đem nhận đặc biệt trang nh&atilde; về thẩm mỹ. Sản phẩm đề cao t&iacute;nh thời trang khi chọn c&aacute;ch sắp xếp camera kh&eacute;o l&eacute;o trong module nhằm tạo cảm gi&aacute;c sang trọng, nhẹ nh&agrave;ng. To&agrave;n bộ khung viền chạy quanh đều được v&aacute;t phẳng tinh tế, bắt kịp xu hướng thiết kế chung của thị trường hiện nay.</p>\r\n<p><img id=\"Mỏng nhẹ, thời trang v&agrave; hơn thế nữa 11\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/AnhNQ/3110/vivo-y33s-8gb-128gb-2.JPG\" alt=\"Mỏng nhẹ, thời trang v&agrave; hơn thế nữa 1\" /></p>\r\n<p>Phi&ecirc;n bản m&agrave;u đen gi&uacute;p ph&ocirc; diễn n&eacute;t đẹp quyền lực, lớp phủ b&oacute;ng bẩy tạo n&ecirc;n hiệu ứng chuyển đổi li&ecirc;n tục giữa sắc tối v&agrave; &aacute;nh s&aacute;ng, đem lại hiệu ứng thị gi&aacute;c đặc biệt cuốn h&uacute;t.</p>\r\n<p><img id=\"Mỏng nhẹ, thời trang v&agrave; hơn thế nữa 22\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/AnhNQ/3110/vivo-y33s-8gb-128gb-3.JPG\" alt=\"Mỏng nhẹ, thời trang v&agrave; hơn thế nữa 2\" /></p>\r\n<p>Trong khi đ&oacute;, bản gradient phong c&aacute;ch cực quang lại chọn c&aacute;ch biến đổi theo g&oacute;c nh&igrave;n v&agrave; h&ograve;a trộn nhiều tone m&agrave;u nhằm mang lại cảm nhận trẻ trung. Lớp phủ mịn b&ecirc;n ngo&agrave;i gi&uacute;p chống b&aacute;m mồ h&ocirc;i, gi&uacute;p&nbsp;trải nghiệm cầm nắm Y33s tr&ecirc;n tay đặc biệt th&uacute; vị.</p>\r\n<p><img id=\"Mỏng nhẹ, thời trang v&agrave; hơn thế nữa 33\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/AnhNQ/3110/vivo-y33s-8gb-128gb-4.JPG\" alt=\"Mỏng nhẹ, thời trang v&agrave; hơn thế nữa 3\" /></p>\r\n<h3><strong>M&agrave;n h&igrave;nh 6.58 inch chiếm tới 90,6% mặt trước</strong></h3>\r\n<p>vivo Y33s l&agrave; chiếc&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai/tren-60-inch\">điện thoại m&agrave;n h&igrave;nh lớn</a>&nbsp;sử dụng m&agrave;n h&igrave;nh rộng 6.58 inch độ ph&acirc;n giải Full HD+, thể hiện c&aacute;c sắc độ m&agrave;u tươi s&aacute;ng, khắc họa từng chi tiết sống động. C&aacute;ch thiết kế kh&eacute;o l&eacute;o gi&uacute;p kh&ocirc;ng gian hiển thị tr&agrave;n ra v&agrave; chiếm tới 90,6% th&acirc;n m&aacute;y, cho cảm gi&aacute;c đầy thuyết phục khi nh&igrave;n ngắm. C&ocirc;ng nghệ lọc &aacute;nh s&aacute;ng xanh m&agrave; nh&agrave; sản xuất t&iacute;ch hợp sẽ gi&uacute;p bạn giữ g&igrave;n sức khỏe mắt v&agrave; hạn chế t&igrave;nh trạng mệt mỏi khi quan s&aacute;t li&ecirc;n tục trong nhiều giờ.</p>\r\n<p><img id=\"M&agrave;n h&igrave;nh 6.58 inch chiếm tới 90,6% mặt trước 11\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/AnhNQ/3110/vivo-y33s-8gb-128gb-f.jpg\" alt=\"M&agrave;n h&igrave;nh 6.58 inch chiếm tới 90,6% mặt trước 1\" /></p>\r\n<h3><strong>Nhiều c&ocirc;ng nghệ bảo mật ấn tượng</strong></h3>\r\n<p>Tr&ecirc;n vivo Y33s, bạn sẽ t&igrave;m thấy nhiều c&ocirc;ng nghệ bảo mật ấn tượng như cảm biến v&acirc;n tay si&ecirc;u nhạy ở cạnh b&ecirc;n v&agrave; Face Wake gi&uacute;p nhận diện khu&ocirc;n mặt người d&ugrave;ng nhanh ch&oacute;ng để mở kh&oacute;a. Trải nghiệm thực tế cho thấy tốc độ qu&eacute;t khu&ocirc;n mặt của Y33s cực kỳ nhanh ch&oacute;ng, chỉ trong nh&aacute;y mắt l&agrave; bạn đ&atilde; ho&agrave;n tất việc mở kh&oacute;a m&agrave;n h&igrave;nh v&agrave; gần như kh&ocirc;ng cảm nhận được sự kh&aacute;c biệt.</p>\r\n<p><img id=\"Nhiều c&ocirc;ng nghệ bảo mật ấn tượng 11\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/AnhNQ/3110/vivo-y33s-8gb-128gb-6.JPG\" alt=\"Nhiều c&ocirc;ng nghệ bảo mật ấn tượng 1\" /></p>\r\n<h3><strong>Pin lớn, sạc nhanh v&agrave; hết sức an to&agrave;n</strong></h3>\r\n<p>Để người d&ugrave;ng kh&ocirc;ng cần bận t&acirc;m qu&aacute; nhiều về pin khi sử dụng Y33s, vivo đem đến ba giải ph&aacute;p hữu hiệu. Đầu ti&ecirc;n l&agrave; vi&ecirc;n pin dung lượng lớn 5.000 mAh, tiếp đến l&agrave;&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai/sac-nhanh\">điện thoại hỗ trợ c&ocirc;ng nghệ sạc nhanh</a>&nbsp;18W Fast Charge v&agrave; cuối c&ugrave;ng l&agrave; c&ocirc;ng nghệ Energy Guardian độc quyền. Việc r&uacute;t ngắn thời gian chờ đợi khi sạc m&agrave; vẫn an to&agrave;n l&agrave; những yếu tố ảnh hưởng rất nhiều đến trải nghiệm pin.</p>\r\n<p><img id=\"Pin lớn, sạc nhanh v&agrave; hết sức an to&agrave;n 11\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/AnhNQ/3110/vivo-y33s-8gb-128gb-7.JPG\" alt=\"Pin lớn, sạc nhanh v&agrave; hết sức an to&agrave;n 1\" /></p>\r\n<h3><strong>Bộ ba camera 50MP xuất sắc trong mọi ho&agrave;n cảnh</strong></h3>\r\n<p>Camera ch&iacute;nh 50MP của vivo Y33s sở hữu nhiều t&iacute;nh năng hỗ trợ chụp ảnh tiện lợi, c&oacute; thể bắt được những khoảnh khắc ấn tượng trong cuộc sống với độ n&eacute;t cao.&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai/vivo\">vivo</a>&nbsp;c&ograve;n trang bị th&ecirc;m một camera Bokeh chuy&ecirc;n hỗ trợ t&iacute;nh năng chụp &ldquo;x&oacute;a ph&ocirc;ng&rdquo; v&agrave; gi&uacute;p cho ảnh chụp c&oacute; chiều s&acirc;u hơn. Camera Macro cho ph&eacute;p người d&ugrave;ng chụp cận cảnh đặc biệt r&otilde; n&eacute;t, từ những giọt sương sớm cho đến mỗi chi tiết của c&aacute;nh hoa đều hiện l&ecirc;n một c&aacute;ch ch&acirc;n thực nhất.</p>\r\n<p><img id=\"Bộ ba camera 50MP xuất sắc trong mọi ho&agrave;n cảnh 11\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/AnhNQ/3110/vivo-y33s-8gb-128gb-8.JPG\" alt=\"Bộ ba camera 50MP xuất sắc trong mọi ho&agrave;n cảnh 1\" /></p>\r\n<h3><strong>Camera selfie 16MP đặc biệt ch&acirc;n thực</strong></h3>\r\n<p>Đến từ thương hiệu nổi tiếng với khả năng tối ưu chất lượng ảnh chụp selfie cho&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai\">điện thoại</a>, vivo Y33s sở hữu camera trước 16MP v&agrave; được hỗ trợ h&agrave;ng loạt thuật to&aacute;n hữu hiệu nhằm gi&uacute;p chủ nh&acirc;n chiếc điện thoại c&oacute; được những khu&ocirc;n h&igrave;nh sắc sảo v&agrave; rạng rỡ. Bạn kh&ocirc;ng cần phải thực hiện th&ecirc;m bất cứ kh&acirc;u kh&acirc;u chỉnh sửa n&agrave;o trước khi đăng tải những khu&ocirc;n h&igrave;nh selfie l&ecirc;n mạng x&atilde; hội.</p>\r\n<p><img id=\"Camera selfie 16MP đặc biệt ch&acirc;n thực 11\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/AnhNQ/3110/vivo-y33s-8gb-128gb-9.JPG\" alt=\"Camera selfie 16MP đặc biệt ch&acirc;n thực 1\" /></p>\r\n<h3><strong>Chế độ chụp ban đ&ecirc;m đặc biệt xuất sắc</strong></h3>\r\n<p>Thuật to&aacute;n Super Night m&agrave; vivo trang bị tr&ecirc;n hệ thống camera sau của Y33s sẽ gi&uacute;p người d&ugrave;ng nắm bắt trọn c&aacute;c sắc th&aacute;i h&igrave;nh ảnh trong đ&ecirc;m. Việc vận dụng c&ocirc;ng nghệ khử nhiễu ảnh RAW mới nhất kết hợp nhiều khung h&igrave;nh th&agrave;nh một sẽ gi&uacute;p cho những bức ảnh th&agrave;nh phẩm trở n&ecirc;n đặc biệt r&otilde; r&agrave;ng d&ugrave; được thực hiện trong m&ocirc;i trường thiếu s&aacute;ng.</p>\r\n<p><img id=\" Chế độ chụp ban đ&ecirc;m đặc biệt xuất sắc 11\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/AnhNQ/3110/vivo-y33s-8gb-128gb-a.JPG\" alt=\" Chế độ chụp ban đ&ecirc;m đặc biệt xuất sắc 1\" /></p>\r\n<h3><strong>Thoải m&aacute;i đa nhiệm v&agrave; tận hưởng cuộc sống</strong></h3>\r\n<p>Với vivo Y33s, trải nghiệm đa nhiệm nhiều t&aacute;c vụ trong c&ugrave;ng thời điểm sẽ chẳng bao giờ l&agrave; th&aacute;ch thức lớn. C&ocirc;ng nghệ đặc biệt gi&uacute;p bộ RAM 8GB của thiết bị được bổ sung th&ecirc;m 4GB chuyển đổi từ bộ nhớ trong. Nhờ vậy, những hiện tượng như giảm tỷ lệ khung h&igrave;nh hay ph&acirc;n mảnh bộ nhớ đều kh&oacute; l&ograve;ng xảy ra.</p>\r\n<p><img id=\"Thoải m&aacute;i đa nhiệm v&agrave; tận hưởng cuộc sống 12\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/AnhNQ/3110/vivo-y33s-8gb-128gb-b.JPG\" alt=\"Thoải m&aacute;i đa nhiệm v&agrave; tận hưởng cuộc sống 1\" /></p>\r\n<h3><strong>Đảm bảo sức mạnh bởi chip xử l&yacute; t&aacute;m nh&acirc;n</strong></h3>\r\n<p>Tr&aacute;i tim sức mạnh của vivo Y33s nằm ở chip xử l&yacute; t&aacute;m nh&acirc;n&nbsp;<a href=\"https://fptshop.com.vn/tin-tuc/tin-moi/mediatek-ra-mat-helio-g70-va-g80-tap-trung-vao-kha-nang-choi-game-109612\">Helio G80</a>&nbsp;ghi nhận mức xung nhịp tối đa l&agrave; 2.0 GHz.&nbsp;<a href=\"https://fptshop.com.vn/tin-tuc/danh-gia/tat-tan-tatve-chip-xu-ly-do-hoa-gpu-tren-dien-thoai-129564\">GPU</a>&nbsp;t&iacute;ch hợp mạnh mẽ của chip sẽ l&agrave; t&aacute;c nh&acirc;n chủ đạo gi&uacute;p cho chất lượng h&igrave;nh ảnh đặc biệt tốt, ghi nhận hiệu suất mạnh mẽ hơn nhiều so với phi&ecirc;n bản tiền nhiệm. Kh&ocirc;ng chỉ vậy, c&ocirc;ng nghệ HyperEngine đặc biệt c&ograve;n đ&oacute;ng vai tr&ograve; quan trọng trong việc n&acirc;ng tầm trải nghiệm gaming tr&ecirc;n Y33s để bạn thỏa sức đắm m&igrave;nh v&agrave;o những tựa game y&ecirc;u th&iacute;ch.</p>\r\n<p><img id=\"Đảm bảo sức mạnh bởi chip xử l&yacute; t&aacute;m nh&acirc;n 11\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/AnhNQ/3110/vivo-y33s-8gb-128gb-d.JPG\" alt=\"Đảm bảo sức mạnh bởi chip xử l&yacute; t&aacute;m nh&acirc;n 1\" /></p>\r\n<h3><strong>Hệ điều h&agrave;nh Funtouch đơn giản v&agrave; trực quan</strong></h3>\r\n<p>Kh&ocirc;ng ch&uacute; trọng v&agrave;o những chi tiết cầu kỳ, nền tảng Funtouch OS 11 đem lại một luồng gi&oacute; mới cho giao diện người d&ugrave;ng của vivo Y33s. Việc tối giản từng chi tiết b&ecirc;n trong tạo cảm gi&aacute;c mới mẻ, đơn giản nhưng thiết thực.</p>\r\n<p><img id=\"Hệ điều h&agrave;nh Funtouch đơn giản v&agrave; trực quan 11\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/AnhNQ/3110/vivo-y33s-8gb-128gb-e.JPG\" alt=\"Hệ điều h&agrave;nh Funtouch đơn giản v&agrave; trực quan 1\" /></p>', 0, '6390000', '87c4641c59.jpg', 1);
INSERT INTO `tbl_product` (`productId`, `productName`, `catId`, `brandId`, `product_description`, `type`, `price`, `image`, `status`) VALUES
(7, 'Vivo V23e', 1, 8, '<p><strong>Với&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai/vivo-v23e\">vivo V23e</a>, mọi khu&ocirc;n h&igrave;nh selfie của bạn sẽ đẹp lung linh nhờ sự trợ gi&uacute;p của camera selfie 50MP kết hợp c&ugrave;ng c&ocirc;ng nghệ Eye Autofocus. Ngo&agrave;i ra,&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai/vivo\">vivo</a>&nbsp;đ&atilde; th&agrave;nh c&ocirc;ng trong việc đưa h&agrave;ng loạt t&iacute;nh năng đỉnh cao của h&atilde;ng v&agrave;o bộ khung vỏ mỏng nhẹ, thời trang, đầy phong c&aacute;ch.</strong></p>\r\n<p><img id=\"M&ocirc; tả sản phẩm Vivo V23e 11\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/AnhNQ/Ph%E1%BB%A5%20ki%E1%BB%87n/mo-ta-san-pham-vivo-v23e-1.JPG\" alt=\"M&ocirc; tả sản phẩm Vivo V23e 1\" /></p>\r\n<h3><strong>Mỏng nhẹ, tinh tế, đơn giản v&agrave; thoải m&aacute;i</strong></h3>\r\n<p>vivo V23e kho&aacute;c l&ecirc;n m&igrave;nh th&acirc;n m&aacute;y si&ecirc;u mỏng với độ d&agrave;y chỉ 7.36mm. Sản phẩm được thiết kế theo phong c&aacute;ch tối giản nhưng kh&ocirc;ng k&eacute;m phần tinh tế. Nh&agrave; sản xuất cho thấy sự thấu hiểu người d&ugrave;ng khi tối ưu k&iacute;ch cỡ th&acirc;n m&aacute;y chỉ c&ograve;n 172 gram, đồng thời phủ một lớp chất liệu si&ecirc;u mịn nhằm đem đến bề mặt nh&aacute;m cho c&aacute;c cạnh bao quanh, kh&ocirc;ng chỉ gi&uacute;p cho trải nghiệm cầm nắm thoải m&aacute;i hơn, m&agrave; c&ograve;n tối ưu t&iacute;nh thẩm mỹ v&agrave; gia tăng độ ho&agrave;n thiện tổng thể.</p>\r\n<p><img id=\"Mỏng nhẹ, tinh tế, đơn giản v&agrave; thoải m&aacute;i 11\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/AnhNQ/Ph%E1%BB%A5%20ki%E1%BB%87n/mo-ta-san-pham-vivo-v23e-6.JPG\" alt=\"Mỏng nhẹ, tinh tế, đơn giản v&agrave; thoải m&aacute;i 1\" /></p>\r\n<h3><strong>Mặt lưng chống b&aacute;m v&acirc;n tay, thu h&uacute;t mọi &aacute;nh nh&igrave;n</strong></h3>\r\n<p>Với sự lựa chọn c&aacute;c gam m&agrave;u th&ocirc;ng minh v&agrave; kh&eacute;o l&eacute;o ứng dụng hiệu ứng chuyển sắc theo g&oacute;c nh&igrave;n, vivo V23e thể hiện vẻ đẹp m&ecirc; hoặc v&agrave; đầy cuốn h&uacute;t. Mặt lưng sản phẩm được chế t&aacute;c từ chất liệu rất mịn, chống b&aacute;m v&acirc;n tay để người d&ugrave;ng thỏa sức sử dụng trong nhiều giờ li&ecirc;n tục m&agrave; kh&ocirc;ng g&acirc;y bất cứ cảm gi&aacute;c kh&oacute; chịu n&agrave;o. Hai phi&ecirc;n bản Giai Điệu B&igrave;nh Minh v&agrave; Vũ Điệu &Aacute;nh Trăng của V23e chắc chắn sẽ thu h&uacute;t bạn ngay từ c&aacute;i nh&igrave;n đầu ti&ecirc;n.</p>\r\n<p><img id=\"Mặt lưng chống b&aacute;m v&acirc;n tay, thu h&uacute;t mọi &aacute;nh nh&igrave;n 11\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/AnhNQ/Ph%E1%BB%A5%20ki%E1%BB%87n/mo-ta-san-pham-vivo-v23e-7.JPG\" alt=\"Mặt lưng chống b&aacute;m v&acirc;n tay, thu h&uacute;t mọi &aacute;nh nh&igrave;n 1\" /></p>\r\n<h3><strong>H&igrave;nh ảnh m&atilde;n nh&atilde;n m&agrave; m&agrave;n h&igrave;nh 20:9 đem lại</strong></h3>\r\n<p>Kh&ocirc;ng gian hiển thị h&igrave;nh ảnh rộng 6.44 inch của Vivo V23e được x&acirc;y dựng theo tỉ lệ khung h&igrave;nh 20:9 v&agrave; đạt độ ph&acirc;n giải 2.400 x 1.080. Tấm nền AMOLED với thế mạnh trong việc thể hiện sắc đen s&acirc;u, tiết kiệm điện sẽ đem đến cho bạn những trải nghiệm trọn vẹn v&agrave; m&atilde;n nh&atilde;n nhất. Mọi thước phim, h&igrave;nh ảnh, v&agrave; th&ocirc;ng tin hiển thị tr&ecirc;n m&agrave;n h&igrave;nh vivo V23e đều c&oacute; độ chi tiết cao, cho thấy sự sắc sảo trong c&aacute;c gam m&agrave;u.</p>\r\n<p><img id=\"H&igrave;nh ảnh m&atilde;n nh&atilde;n m&agrave; m&agrave;n h&igrave;nh 20:9 đem lại 11\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/AnhNQ/Ph%E1%BB%A5%20ki%E1%BB%87n/mo-ta-san-pham-vivo-v23e-a.JPG\" alt=\"H&igrave;nh ảnh m&atilde;n nh&atilde;n m&agrave; m&agrave;n h&igrave;nh 20:9 đem lại 1\" /></p>\r\n<h3><strong>Bắt trọn vẻ đẹp ri&ecirc;ng của bạn</strong></h3>\r\n<p>C&ocirc;ng nghệ lấy n&eacute;t tự động m&agrave; Vivo đưa l&ecirc;n V23e gi&uacute;p ph&aacute;t huy trọn vẹn thế mạnh của camera trước 50MP, d&ugrave; bạn đưa m&aacute;y ra xa hay selfie cận mặt, sản phẩm cũng c&oacute; thể nhận diện ch&iacute;nh x&aacute;c để lấy n&eacute;t khu&ocirc;n mặt bạn. T&iacute;nh năng Eye AutoFocus với khả năng theo d&otilde;i &aacute;nh mắt cũng gi&uacute;p tiết kiệm nhiều c&ocirc;ng sức bắt n&eacute;t v&agrave; đảm bảo bạn lu&ocirc;n l&agrave; t&acirc;m điểm trong khung h&igrave;nh.</p>\r\n<p>Vivo đ&atilde; khẳng định c&ocirc;ng nghệ h&agrave;ng đầu trong lĩnh vực tối ưu ảnh chụp selfie bằng c&aacute;ch đưa l&ecirc;n V23e c&ocirc;ng nghệ Ch&acirc;n Dung Đa Phong C&aacute;ch, đem đến nhiều lớp filter v&agrave; gi&uacute;p chủ nh&acirc;n thiết bị c&oacute; thể biến h&oacute;a bức ảnh ch&acirc;n dung của m&igrave;nh theo nhiều phong c&aacute;ch ri&ecirc;ng biệt sau khi chụp.</p>\r\n<p><img id=\"Bắt trọn vẻ đẹp ri&ecirc;ng của bạn 11\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/AnhNQ/Ph%E1%BB%A5%20ki%E1%BB%87n/mo-ta-san-pham-vivo-v23e-3.JPG\" alt=\"Bắt trọn vẻ đẹp ri&ecirc;ng của bạn 1\" /></p>\r\n<h3><strong>Sạc nhanh ấn tượng v&agrave; an to&agrave;n</strong></h3>\r\n<p>L&agrave; chiếc&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai/sac-nhanh\">điện thoại sạc nhanh</a>, vivo V23e ghi nhận tốc độ sạc nhanh ấn tượng khi c&oacute; thể t&aacute;i tạo tới 69% pin chỉ trong v&ograve;ng nửa tiếng đồng hồ ngắn ngủi. Vi&ecirc;n pin dung lượng 4.050mAh sẽ cung cấp đủ điện năng một ng&agrave;y d&agrave;i sử dụng với nhiều t&aacute;c vụ đa dạng kh&aacute;c nhau. Bạn c&oacute; thể an t&acirc;m về trải nghiệm pin đầy hứa hẹn tr&ecirc;n vivo V23e.</p>\r\n<p><img id=\"Sạc nhanh ấn tượng v&agrave; an to&agrave;n 11\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/AnhNQ/Ph%E1%BB%A5%20ki%E1%BB%87n/mo-ta-san-pham-vivo-v23e-8.JPG\" alt=\"Sạc nhanh ấn tượng v&agrave; an to&agrave;n 1\" /></p>\r\n<h3><strong>Tối ưu hiệu suất nhờ c&ocirc;ng nghệ hiện đại</strong></h3>\r\n<p>Nhằm tối ưu h&oacute;a hiệu suất vận h&agrave;nh cho sản phẩm, vivo đ&atilde; t&iacute;ch hợp l&ecirc;n chiếc&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai\">điện thoại</a>&nbsp;c&ocirc;ng nghệ RAM mở rộng 2.0. C&ocirc;ng nghệ n&agrave;y hỗ trợ ph&acirc;n bổ một phần bộ nhớ trong sang bộ nhớ RAM, nhờ đ&oacute; gi&uacute;p vivo V23e c&oacute; được trải nghiệm xuy&ecirc;n suốt, thể hiện hiệu năng đa nhiệm xuất sắc. Đ&acirc;y l&agrave; điểm nhấn ấn tượng m&agrave; chỉ khi cầm nắm v&agrave; sử dụng thiết bị, bạn mới c&oacute; thể cảm nhận trọn vẹn lợi thế vượt trội n&agrave;y.</p>\r\n<p><img id=\"Tối ưu hiệu suất nhờ c&ocirc;ng nghệ hiện đại 11\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/AnhNQ/Ph%E1%BB%A5%20ki%E1%BB%87n/mo-ta-san-pham-vivo-v23e-9.JPG\" alt=\"Tối ưu hiệu suất nhờ c&ocirc;ng nghệ hiện đại 1\" /></p>\r\n<h3><strong>Hi-Res v&agrave; Multi-Turbo giải tr&iacute; trọn vẹn</strong></h3>\r\n<p>Từng thanh &acirc;m, từng tiết tấu m&agrave; vivo V23e thể hiện đều đảm bảo t&iacute;nh trong trẻo v&agrave; độ trầm ấm nhờ c&ocirc;ng nghệ Hi-Res. Chủ nh&acirc;n chiếc điện thoại sẽ chạm đến thế giới giải tr&iacute; sống động đầy phong c&aacute;ch, đồng thời thỏa th&iacute;ch tận hưởng những nội dung media một c&aacute;ch trọn vẹn nhất.</p>\r\n<p>Chế độ Multi-Turbo m&agrave; vivo V23e sở hữu cũng hỗ trợ bạn rất nhiều khi vừa l&agrave;m việc, vừa giải tr&iacute;, trở th&agrave;nh ch&igrave;a kh&oacute;a đảm bảo cho hiệu suất hoạt động mượt m&agrave;, đồng thời hạn chế tối đa t&igrave;nh trạng giật lag c&oacute; thể xảy ra.</p>\r\n<p><img id=\"Hi-Res v&agrave; Multi-Turbo giải tr&iacute; trọn vẹn 11\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/AnhNQ/Ph%E1%BB%A5%20ki%E1%BB%87n/mo-ta-san-pham-vivo-v23e-b.JPG\" alt=\"Hi-Res v&agrave; Multi-Turbo giải tr&iacute; trọn vẹn 1\" /></p>\r\n<h3><strong>Bộ ba camera sau chuy&ecirc;n nghiệp</strong></h3>\r\n<p>Sự kết hợp của camera ch&iacute;nh 64MP, camera g&oacute;c si&ecirc;u rộng 8MP v&agrave; camera si&ecirc;u cận 2MP tr&ecirc;n mặt lưng vivo V23e đem tới khả năng chụp ảnh chuy&ecirc;n nghiệp. H&atilde;y thỏa sức s&aacute;ng tạo những bức ảnh v&agrave; thước phim sống động ưng &yacute;. vivo đ&atilde; ứng dụng thuật to&aacute;n AI nhằm tối ưu khả năng chụp đ&ecirc;m của chiếc điện thoại th&ocirc;ng qua chế độ si&ecirc;u chụp đ&ecirc;m th&ocirc;ng minh, gi&uacute;p ảnh chụp s&aacute;ng n&eacute;t hơn, thể hiện độ tương phản cao v&agrave; sắc sảo.</p>\r\n<p>C&aacute;c t&iacute;nh năng chụp ảnh chuy&ecirc;n dụng kh&aacute;c như Bokeh Flare v&agrave; Ch&acirc;n Dung Đa Phong C&aacute;ch sẽ tăng th&ecirc;m phần chuy&ecirc;n nghiệp cho c&aacute;c khu&ocirc;n h&igrave;nh bạn chụp. Tin rằng sự hiệu quả của hệ thống camera vivo V23e sẽ l&agrave;m h&agrave;i l&ograve;ng c&aacute;c kh&aacute;ch h&agrave;ng kh&oacute; t&iacute;nh nhất.</p>\r\n<p><img id=\"Bộ ba camera sau chuy&ecirc;n nghiệp 11\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/AnhNQ/Ph%E1%BB%A5%20ki%E1%BB%87n/mo-ta-san-pham-vivo-v23e-d.JPG\" alt=\"Bộ ba camera sau chuy&ecirc;n nghiệp 1\" /></p>\r\n<h3><strong>Giao diện th&acirc;n thiện, Widget đa dạng</strong></h3>\r\n<p>vivo V23e được trang bị giao diện người d&ugrave;ng th&acirc;n thiện bậc nhất tr&ecirc;n thị trường di động hiện nay. Sản phẩm cho ph&eacute;p chủ nh&acirc;n c&oacute; thể dễ d&agrave;ng t&ugrave;y chọn những widget cần d&ugrave;ng trong bộ sưu tập widget đa dạng, phong ph&uacute;. Từ đ&oacute;, bạn sẽ thoải m&aacute;i đưa bản sắc c&aacute; t&iacute;nh của ri&ecirc;ng m&igrave;nh v&agrave;o giao diện tr&ecirc;n m&agrave;n h&igrave;nh.</p>\r\n<p><img id=\"Giao diện th&acirc;n thiện, Widget đa dạng 11\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/AnhNQ/Ph%E1%BB%A5%20ki%E1%BB%87n/mo-ta-san-pham-vivo-v23e-c.JPG\" alt=\"Giao diện th&acirc;n thiện, Widget đa dạng 1\" /></p>', 0, '7490000', '9e7d961bd7.jpg', 1),
(8, 'Vivo V23 5G', 1, 8, '<h3><span>Kh&aacute;m ph&aacute; ch&iacute;nh bạn với những điểm tự nhi&ecirc;n nhất</span></h3>\r\n<p>vivo V23 5G sở hữu camera selfie k&eacute;p 50MP, được tinh chỉnh độc quyền của&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai/vivo\">vivo</a>, kết hợp ISOCELL 3.0 mang đến cho bạn độ chi tiết v&agrave; n&eacute;t đẹp tự nhi&ecirc;n của ch&iacute;nh m&igrave;nh. C&ugrave;ng với thuật to&aacute;n AI, n&oacute; sẽ nhận diện c&aacute;c chi tiết t&acirc;m điểm tr&ecirc;n khu&ocirc;n mặt bạn v&agrave; từ đ&oacute; tự động điều chỉnh để c&aacute;c chi tiết đ&oacute; r&otilde; n&eacute;t v&agrave; ch&acirc;n thực hơn.&nbsp;</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/vivo-v23-5g-2.png\" alt=\"vivo V23 5G (1)\" /></p>\r\n<h3><span>Mở rộng khung h&igrave;nh, l&agrave;m đẹp tự nhi&ecirc;n</span></h3>\r\n<p>Với camera selfie g&oacute;c rộng 8MP, vivo V23 5G sẽ khiến nỗi lo chụp ảnh nh&oacute;m của bạn biến mất. Cảm biến n&agrave;y sẽ chụp những bức ảnh selfie nh&oacute;m v&agrave; c&oacute; thể thu được nhiều phong cảnh hơn. Với ch&acirc;n dung selfie, chế độ l&agrave;m đẹp t&ugrave;y chọn được t&iacute;ch hợp sẽ gi&uacute;p cho chất lượng bức ảnh ch&acirc;n dung được tốt hơn, nhưng vẫn đảm bảo được độ tự nhi&ecirc;n v&agrave; ch&acirc;n thực. Chế độ Ch&acirc;n dung đa phong c&aacute;ch t&iacute;ch hợp th&ecirc;m 3 kiểu phong c&aacute;ch mới, đồng thời cung cấp nhiều c&aacute;ch để tạo hiệu ứng bokeh đầy nghệ thuật.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/vivo-v23-5g-3.png\" alt=\"vivo V23 5G (2)\" /></p>\r\n<h3><span>Kh&ocirc;ng gian ho&agrave;n hảo cho bạn</span></h3>\r\n<p>Hiệu ứng &aacute;nh trăng k&eacute;p của vivo đ&atilde; được n&acirc;ng cấp cho vivo V23 5G so với thế hệ trước, trang bị đến bốn đ&egrave;n flash ở hai b&ecirc;n cụm camera selfie để bạn dễ d&agrave;ng c&aacute; nh&acirc;n h&oacute;a t&acirc;m trạng v&agrave; &aacute;nh s&aacute;ng chỉ với một c&uacute; chạm. Với chế độ n&agrave;y, n&oacute; sẽ cung cấp đầy đủ &aacute;nh s&aacute;ng cho bạn ở bất kỳ điều kiện n&agrave;o để c&oacute; thể chụp selfie v&agrave; quay video selfie r&otilde; n&eacute;t v&agrave; tự nhi&ecirc;n.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/vivo-v23-5g-4.png\" alt=\"vivo V23 5G (3)\" /></p>\r\n<h3><span>Dễ d&agrave;ng s&aacute;ng tạo c&aacute;c thước phim, đa dạng phong c&aacute;ch cho bạn&nbsp;</span></h3>\r\n<p>Với h&agrave;ng loạt chế độ như Video Selfie 4K, Video Ch&acirc;n dung đa phong c&aacute;ch v&agrave; Video Hiển thị k&eacute;p, bạn sẽ dễ d&agrave;ng tạo ra những thước phim c&oacute; độ ph&acirc;n giải cao, kh&ocirc;ng out n&eacute;t v&agrave; trực quan hơn. N&oacute; c&ograve;n gi&uacute;p bạn dễ d&agrave;ng bung hết sức s&aacute;ng tạo của m&igrave;nh, tạo ra những video selfie th&uacute; vị hơn nữa.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/vivo-v23-5g-5.png\" alt=\"vivo V23 5G (4)\" /></p>\r\n<h3><span>K&iacute;nh quang sắc cực đẹp, mặt lưng cao cấp</span></h3>\r\n<p>Từ trước đến nay, vivo lu&ocirc;n mang đến thiết kế tổng thể cao cấp v&agrave; ấn tượng cho c&aacute;c sản phẩm của họ. vivo V23 5G to&aacute;t l&ecirc;n vẻ cao cấp nhờ hai phi&ecirc;n bản m&agrave;u độc đ&aacute;o (Xanh Vũ Trụ v&agrave; V&agrave;ng &Aacute;nh Dương) được phủ bởi lớp pha l&ecirc; dọc theo c&ocirc;ng nghệ K&iacute;nh &Aacute;nh Kim AG,&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai\">điện thoại</a>&nbsp;lu&ocirc;n tỏa s&aacute;ng với độ ho&agrave;n thiện cao. Với m&agrave;u V&agrave;ng &Aacute;nh Dương, c&ocirc;ng nghệ K&iacute;nh quang sắc sẽ gi&uacute;p mặt lưng c&oacute; thể thay đổi m&agrave;u sắc ở c&aacute;c g&oacute;c độ kh&aacute;c nhau dưới &aacute;nh s&aacute;ng mặt trời, c&aacute;c nguồn tia UV&hellip; mang đến một mặt lưng bắt mắt v&agrave; cao cấp.&nbsp;</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/vivo-v23-5g-6.png\" alt=\"vivo V23 5G (5)\" /></p>\r\n<h3><span>Mỏng nhẹ, thanh lịch</span></h3>\r\n<p>vivo V23 5G sử dụng Khung viền phẳng được ho&agrave;n thiện bằng chất liệu hợp kim nh&ocirc;m đặc biệt, sẽ mang&nbsp;đến thiết kế đẹp mắt v&agrave; thoải m&aacute;i khi cầm nắm. Điện thoại c&oacute; độ mỏng nhẹ thuộc top đầu hiện nay với độ mỏng chỉ 7.39mm v&agrave; chỉ nặng 157g, cho bạn những trải nghiệm thoải m&aacute;i d&ugrave; sử dụng li&ecirc;n tục.&nbsp;</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/vivo-v23-5g-7.png\" alt=\"vivo V23 5G (6)\" /></p>\r\n<h3><span>Sức mạnh chụp ảnh được nh&acirc;n l&ecirc;n</span></h3>\r\n<p>Kh&ocirc;ng chỉ nổi bật ở thiết kế cụm camera sau, chất lượng chụp ảnh của n&oacute; cũng được tăng l&ecirc;n nhiều lần. vivo V23 5G c&oacute; 3 camera AI ở mặt sau, mang đến cho bạn những bức ảnh nghệ thuật ở một đẳng cấp ho&agrave;n to&agrave;n mới. Những bức ảnh thường ng&agrave;y đều được xử l&yacute; một c&aacute;ch dễ d&agrave;ng, trở th&agrave;nh một t&aacute;c phẩm nghệ thuật.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/vivo-v23-5g-8.png\" alt=\"vivo V23 5G (7)\" /></p>\r\n<h3><span>Chụp đ&ecirc;m ấn tượng, ch&acirc;n dung đầy th&uacute; vị&nbsp;</span></h3>\r\n<p>Trong điều kiện &aacute;nh s&aacute;ng yếu, cụm camera sau của vivo V23 5G c&oacute; độ nhạy s&aacute;ng cao hơn với cảm biến GW1, l&agrave;m đa dạng hơn c&aacute;c lựa chọn trong việc chụp ảnh. B&ecirc;n cạnh đ&oacute;, những t&iacute;nh năng bổ trợ cho chụp ch&acirc;n dung như Ch&acirc;n dung đa phong c&aacute;ch, Bokeh Flare được n&acirc;ng cấp sẽ mang đến những bức ảnh ch&acirc;n dung th&uacute; vị v&agrave; r&otilde; n&eacute;t hơn.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/vivo-v23-5g-9.png\" alt=\"vivo V23 5G (8)\" /></p>\r\n<h3><span>Hiệu suất đỉnh cao với 5G, RAM Mở rộng 2.0</span></h3>\r\n<p>Mang trong m&igrave;nh bộ vi xử l&yacute; Dimensity 920 6nm của MediaTek, vivo V23 5G được cung cấp một hiệu năng cao c&ugrave;ng khả năng tiết kiệm điện năng đ&aacute;ng nể. Chipset n&agrave;y sẽ mang đến chế độ 5G k&eacute;p, đạt hiệu suất cao cho những thứ bạn cần.&nbsp;</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/vivo-v23-5g-10.png\" alt=\"vivo V23 5G (9)\" /></p>\r\n<p>C&ocirc;ng nghệ RAM Mở rộng 2.0 được t&iacute;ch hợp sẽ cho ph&eacute;p ph&acirc;n bổ 4GB ROM sang RAM bằng thuật to&aacute;n khi cần thiết, nhờ đ&oacute; bạn c&oacute; thể sử dụng điện thoại m&agrave; kh&ocirc;ng gặp bất cứ giật lag n&agrave;o, c&oacute; thể sử dụng đa nhiệm c&ugrave;ng l&uacute;c 25 ứng dụng một c&aacute;ch mượt m&agrave;.&nbsp;</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/vivo-v23-5g-11.png\" alt=\"vivo V23 5G (10)\" /></p>\r\n<h3><span>Sạc nhanh hơn, trải nghiệm với kh&ocirc;ng gian mượt m&agrave; hơn</span></h3>\r\n<p>D&ugrave; c&oacute; thiết kế mỏng nhẹ nhưng vivo V23 5G cũng được t&iacute;ch hợp vi&ecirc;n pin l&ecirc;n đến 4200mAh, cho bạn thời lượng sử dụng hơn một ng&agrave;y m&agrave; kh&ocirc;ng lo pin cạn. M&aacute;y sẽ được nạp đầy nhanh ch&oacute;ng trong chớp mắt với sạc si&ecirc;u tốc 44W được t&iacute;ch hợp. Ngo&agrave;i ra, trải nghiệm giải tr&iacute; của bạn sẽ trở n&ecirc;n ấn tượng hơn với tần số qu&eacute;t m&agrave;n h&igrave;nh 90Hz, mang đến một kh&ocirc;ng gian hiển thị sắc n&eacute;t c&ugrave;ng c&aacute;c thao t&aacute;c sử dụng mượt m&agrave;.&nbsp;</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/vivo-v23-5g-12.png\" alt=\"vivo V23 5G (11)\" /></p>\r\n<h3><span>Giao diện mới, t&ugrave;y biến dựa tr&ecirc;n Android 12</span></h3>\r\n<p>vivo V23 5G l&agrave; sản phẩm vivo đầu ti&ecirc;n được t&ugrave;y biến dựa tr&ecirc;n Android 12 ngay khi ra mắt. Với giao diện Funtouch OS 12, n&oacute; sẽ cung cấp nhiều hơn những t&iacute;nh năng so với trước cũng như th&ecirc;m c&aacute;c tiện &iacute;ch, widget dễ d&agrave;ng t&ugrave;y chỉnh theo sở th&iacute;ch v&agrave; nhu cầu của m&igrave;nh.&nbsp;</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/vivo-v23-5g-13.png\" alt=\"vivo V23 5G (12)\" /></p>', 1, '12490000', 'cc8119cc43.jpg', 1),
(9, 'Tai nghe choàng đầu Beats Studio3 Wireless', 3, 9, '<h2 class=\"card-title\"><strong style=\"font-size: 10px;\">Với h&agrave;ng loạt c&aacute;c t&iacute;nh năng như khử ồn chủ động Pure ANC, hiệu chỉnh &acirc;m thanh thời gian thực v&agrave; thời lượng pin l&ecirc;n đến 22 giờ, Beats Studio3 chắc chắn sẽ l&agrave; một chiếc tai nghe kh&ocirc;ng d&acirc;y ho&agrave;n hảo cho những người đang t&igrave;m kiếm trải nghiệm nghe cao cấp.</strong></h2>\r\n<div class=\"card-body\">\r\n<div class=\"st-pd-content\">\r\n<h3><strong>&Acirc;m thanh cao cấp</strong></h3>\r\n<p><strong><img src=\"https://fptshop.com.vn/Uploads/images/2015/VuTT29/04-Beats-Studio3-Wireless-Blue-01.jpg\" alt=\"PKNK Tai nghe cho&agrave;ng đầu Beats Studio3 Wireless Blue MX402PA/A\" /></strong></p>\r\n<p>Beats Studio3 sẽ mang đến trải nghiệm nghe cao cấp cho mọi người d&ugrave;ng trong bất cứ ho&agrave;n cảnh n&agrave;o. Với t&iacute;nh năng chống ồn chủ động Pure ANC, tai nghe kh&ocirc;ng d&acirc;y n&agrave;y c&oacute; thể dễ d&agrave;ng ngăn chặn tiếng ồn b&ecirc;n ngo&agrave;i v&agrave; hiệu chỉnh &acirc;m thanh thời gian thực để duy tr&igrave; độ chi tiết của &acirc;m thanh đến tai người nghe. Với việc t&iacute;ch hợp c&aacute;c micro ti&ecirc;n tiến v&agrave; chip xử l&yacute; Apple W1, tai nghe c&oacute; thể li&ecirc;n tục x&aacute;c định ch&iacute;nh x&aacute;c c&aacute;c &acirc;m thanh xung quanh cần chặn v&agrave; tối ưu đầu ra &acirc;m thanh để đảm bảo trải nghiệm nghe cao cấp đ&uacute;ng nghĩa.</p>\r\n<h3><strong>Được trang bị chip Apple W1</strong></h3>\r\n<p><strong><img src=\"https://fptshop.com.vn/Uploads/images/2015/VuTT29/04-Beats-Studio3-Wireless-Blue-02.jpg\" alt=\"PKNK Tai nghe cho&agrave;ng đầu Beats Studio3 Wireless Blue MX402PA/A\" /></strong></p>\r\n<p>Với việc được trang bị chip Apple W1 cao cấp, Beats Studio3 kh&ocirc;ng chỉ mang đến &acirc;m thanh cao cấp m&agrave; c&ograve;n cung cấp khả năng thiết lập, kết nối v&agrave; chuyển đổi liền mạch tr&ecirc;n mọi thiết bị của Apple. Bạn chỉ cần nhấn n&uacute;t nguồn v&agrave; đặt tai nghe b&ecirc;n cạnh iPhone, cả hai đ&atilde; c&oacute; thể nhanh ch&oacute;ng nhận diện v&agrave; gh&eacute;p nối với nhau trong nh&aacute;y mắt. Từ đ&oacute;, cải thiện đ&aacute;ng kể trải nghiệm sử dụng của người d&ugrave;ng.</p>\r\n<h3><strong>Giữ kết nối ổn định</strong></h3>\r\n<p><strong><img src=\"https://fptshop.com.vn/Uploads/images/2015/VuTT29/04-Beats-Studio3-Wireless-Blue-03.jpg\" alt=\"PKNK Tai nghe cho&agrave;ng đầu Beats Studio3 Wireless Blue MX402PA/A\" /></strong></p>\r\n<p>Kh&ocirc;ng chỉ mang lại trải nghiệm gh&eacute;p nối thuận tiện cho người d&ugrave;ng, con chip Apple W1 c&ograve;n cung cấp c&ocirc;ng nghệ kh&ocirc;ng d&acirc;y Bluetooth Class 1 h&agrave;ng đầu trong ng&agrave;nh để gi&uacute;p đảm bảo phạm vi v&agrave; cường độ t&iacute;n hiệu lu&ocirc;n l&agrave; tốt nhất. V&igrave; vậy, bạn c&oacute; thể y&ecirc;n t&acirc;m nghe nhạc v&agrave; tr&ograve; chuyện tr&ecirc;n Beats Studio3 trong khi di chuyển với chất lượng &acirc;m thanh to v&agrave; liền mạch.</p>\r\n<h3><strong>Nghe nhạc cả ng&agrave;y với một lần sạc</strong></h3>\r\n<p><strong><img src=\"https://fptshop.com.vn/Uploads/images/2015/VuTT29/04-Beats-Studio3-Wireless-Blue-04.jpg\" alt=\"PKNK Tai nghe cho&agrave;ng đầu Beats Studio3 Wireless Blue MX402PA/A\" /></strong></p>\r\n<p>Cho d&ugrave; bạn k&iacute;ch hoạt t&iacute;nh năng Pure ANC li&ecirc;n tục, tai nghe kh&ocirc;ng d&acirc;y của Beats vẫn cho ph&eacute;p bạn nghe nhạc cả ng&agrave;y d&agrave;i m&agrave; kh&ocirc;ng cần đến nguồn cấp điện. Với chỉ một lần sạc duy nhất, bạn c&oacute; thể sử dụng Beats Studio3 trong v&ograve;ng 22 giờ. Thời gian n&agrave;y thậm ch&iacute; c&ograve;n tăng l&ecirc;n 40 giờ nếu như bạn tắt t&iacute;nh năng chống ồn chủ động của tai nghe. B&ecirc;n cạnh đ&oacute;, sản phẩm c&oacute; hỗ trợ sạc si&ecirc;u nhanh Fast Fuel, cho ph&eacute;p bạn c&oacute; ngay 3 giờ nghe nhạc với chỉ 10 ph&uacute;t sạc.</p>\r\n<h3><strong>Được thiết kế để ph&ugrave; hợp với bạn</strong></h3>\r\n<p><strong><img src=\"https://fptshop.com.vn/Uploads/images/2015/VuTT29/04-Beats-Studio3-Wireless-Blue-05.jpg\" alt=\"PKNK Tai nghe cho&agrave;ng đầu Beats Studio3 Wireless Blue MX402PA/A\" /></strong></p>\r\n<p>Tai nghe kh&ocirc;ng d&acirc;y của Beats được thiết kế để mang lại sự thoải m&aacute;i trong thời gian d&agrave;i, ph&ugrave; hợp với thời lượng pin ấn tượng l&ecirc;n đến 22 giờ để chơi cả ng&agrave;y. Thiết kế chụp tai với tấm đệm mềm mại kết hợp c&aacute;c lỗ th&ocirc;ng hơi ti&ecirc;n tiến v&agrave; trục xoay tiện lợi sẽ đảm bảo người d&ugrave;ng c&oacute; thể linh hoạt tuỳ chỉnh tai nghe vừa vặn với bất cứ k&iacute;ch thước đầu n&agrave;o. C&ugrave;ng với trọng lượng nhẹ, Beats Studio3 sẽ mang đến cho bạn những ph&uacute;t gi&acirc;y nghe nhạc thư gi&atilde;n v&agrave; thoải m&aacute;i nhất trong cả ng&agrave;y d&agrave;i.</p>\r\n</div>\r\n</div>', 0, '6990000', '3f11583275.jpg', 1),
(10, 'Tai nghe Samsung Galaxy Buds Pro', 3, 2, '<h2 class=\"card-title\"><strong style=\"font-size: 10px;\">Kế thừa v&agrave; ph&aacute;t huy trọn vẹn những ưu điểm từ c&aacute;c thế hệ trước, tai nghe kh&ocirc;ng d&acirc;y&nbsp;<a href=\"https://fptshop.com.vn/phu-kien/tai-nghe-bluetooth-nhet-tai-samsung-galaxy-buds-pro\">Galaxy Buds Pro</a>&nbsp;c&oacute; thiết kế th&ocirc;ng minh với kiểu d&aacute;ng in-ear c&aacute;ch &acirc;m tốt. Sản phẩm đi k&egrave;m hộp sạc vu&ocirc;ng nhỏ gọn bo tr&ograve;n c&aacute;c g&oacute;c v&agrave; được t&iacute;ch hợp c&ocirc;ng nghệ chống ồn chủ động ANC linh hoạt với chất &acirc;m h&agrave;ng đầu thị trường.</strong></h2>\r\n<div class=\"card-body\">\r\n<div class=\"st-pd-content\">\r\n<p><img id=\"\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/07/15/Tai-nghe-samsung-galaxy-buds-pro-1.JPG\" alt=\"Tai nghe Samsung Galaxy Buds Pro\" /></p>\r\n<h3><strong>Khử ồn hiệu quả với cơ chế ANC th&ocirc;ng minh</strong></h3>\r\n<p>C&ocirc;ng nghệ chống ồn chủ động ANC tr&ecirc;n Galaxy Buds Pro c&oacute; thể loại bỏ tới 99% tạp &acirc;m từ m&ocirc;i trường. Để l&agrave;m được điều n&agrave;y,&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai/samsung\">Samsung</a>&nbsp;đ&atilde; t&iacute;ch hợp cho sản phẩm tới ba micro bố tr&iacute; tại những vị tr&iacute; b&ecirc;n trong v&agrave; b&ecirc;n ngo&agrave;i tai nghe để đạt hiệu quả theo d&otilde;i tiếng ồn nhạy b&eacute;n nhất. Sản phẩm đem tới ba mức khử ồn kh&aacute;c nhau gồm Cao &ndash; Trung B&igrave;nh &ndash; Thấp để bạn t&ugrave;y chỉnh linh hoạt.</p>\r\n<p><img id=\"\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/07/15/Tai-nghe-samsung-galaxy-buds-pro-5.JPG\" alt=\"Khử ồn hiệu quả Galaxy Buds Pro\" /></p>\r\n<h3><strong>Chất &acirc;m ấn tượng chuẩn ph&ograve;ng thu</strong></h3>\r\n<p>Chất lượng &acirc;m thanh m&agrave; Galaxy Buds Pro thể hiện đ&atilde; trải qua tinh chỉnh từ c&aacute;c chuy&ecirc;n gia tới từ thương hiệu AKG. Nhờ đ&oacute;, mọi &acirc;m sắc của chiếc&nbsp;<a href=\"https://fptshop.com.vn/phu-kien/tai-nghe\">tai nghe</a>&nbsp;đều đem tới trải nghiệm trọn vẹn v&agrave; s&acirc;u sắc. Đeo Galaxy Buds Pro l&ecirc;n, bạn sẽ ấn tượng với &acirc;m treble r&otilde; r&agrave;ng, &acirc;m bass chắc khỏe v&agrave; dải mid r&otilde; r&agrave;ng của sản phẩm. D&ugrave; bạn nghe nhạc, giải tr&iacute; hay h&ograve;a m&igrave;nh v&agrave; những podcast &yacute; nghĩa, chiếc tai nghe n&agrave;y đều sẽ khiến bạn ưng &yacute;.</p>\r\n<p><img id=\"\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/07/15/Tai-nghe-samsung-galaxy-buds-pro-3.JPG\" alt=\"&acirc;m thanh Galaxy Buds Pro\" /></p>\r\n<h3><strong>&Acirc;m thanh v&ograve;m Dolby Atmos sống động</strong></h3>\r\n<p>C&ocirc;ng nghệ Dolby Head Tracking sẽ theo d&otilde;i cử động của bạn để tạo n&ecirc;n hiệu ứng &acirc;m thanh v&ograve;m. T&iacute;nh năng n&agrave;y tạo n&ecirc;n trải nghiệm cực kỳ kh&aacute;c biệt khi bạn xem phim v&agrave; chơi game, đưa bạn v&agrave;o thế giới giải tr&iacute; cực kỳ ch&acirc;n thực. Galaxy Buds Pro sẽ x&aacute;c định ch&iacute;nh x&aacute;c hướng của &acirc;m thanh khi bạn cử động cổ để tạo n&ecirc;n hiệu ứng &acirc;m thanh 3D sinh động nhất.</p>\r\n<p><img id=\"\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/07/15/Tai-nghe-samsung-galaxy-buds-pro-4.JPG\" alt=\"&Acirc;m thanh v&ograve;m Galaxy Buds Pro\" /></p>\r\n<h3><strong>Tương t&aacute;c th&ocirc;ng minh với những thiết bị kh&aacute;c</strong></h3>\r\n<p>Một yếu tố mới tr&ecirc;n Galaxy Buds Pro l&agrave; t&iacute;nh năng tự động chuyển đổi nguồn ph&aacute;t. C&ocirc;ng nghệ n&agrave;y cho ph&eacute;p sản phẩm tự nhận diện c&aacute;c t&aacute;c vụ kh&aacute;c nhau để t&igrave;m kiếm thiết bị ph&aacute;t th&iacute;ch hợp. D&ugrave; bạn đang kết nối tai nghe với laptop hay m&aacute;y t&iacute;nh bảng, khi c&oacute; cuộc gọi tới tr&ecirc;n smartphone, Galaxy Buds Pro sẽ tự chuyển kết nối sang điện thoại để bạn nhận cuộc gọi m&agrave; kh&ocirc;ng cần thao t&aacute;c trực tiếp tr&ecirc;n smartphone.</p>\r\n<p><img id=\"\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/07/15/Tai-nghe-samsung-galaxy-buds-pro-a.JPG\" alt=\"kết nối Galaxy Buds Pro\" /></p>\r\n<h3><strong>Thiết kế th&ocirc;ng minh v&agrave; tiện dụng bậc nhất</strong></h3>\r\n<p>Galaxy Buds Pro quy tụ mọi ưu điểm thiết kế từ c&aacute;c thế hệ tiền nhiệm nhằm t&igrave;m ra kiểu d&aacute;ng ho&agrave;n thiện nhất. Lớp phủ b&oacute;ng bẩy đậm chất thời thượng kết hợp với loạt m&agrave;u sắc c&aacute; t&iacute;nh trẻ trung của chiếc tai nghe sẽ đem tới cảm quan ấn tượng ngay từ lần đầu ti&ecirc;n bạn nh&igrave;n thấy. Hộp sạc Galaxy Buds Pro c&oacute; k&iacute;ch cỡ rất gọn, nằm lọt trong l&ograve;ng b&agrave;n tay, rất tiện để bạn mang theo đi khắp mọi nơi.</p>\r\n<p><img id=\"\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/07/15/Tai-nghe-samsung-galaxy-buds-pro-2.JPG\" alt=\"thiết kế Galaxy Buds Pro\" /></p>\r\n<h3><strong>Chiếc tai nghe in-ear dễ chịu nhất bạn từng đeo</strong></h3>\r\n<p>Samsung đ&atilde; đầu tư nghi&ecirc;n cứu nhằm tạo n&ecirc;n một thiết kế in-ear nh&eacute;t trong dễ chịu nhất khi đeo. N&uacute;t silicon của Galaxy Buds Pro được tạo h&igrave;nh theo dạng oval ph&ugrave; hợp với ống tai, kết hợp với hệ thống lỗ tho&aacute;t kh&iacute; gi&uacute;p c&acirc;n bằng &aacute;p suất trong tai sẽ đảm bảo người d&ugrave;ng thoải m&aacute;i trải nghiệm suốt nhiều giờ m&agrave; kh&ocirc;ng thấy bất cứ hiện tượng kh&oacute; chịu n&agrave;o. Ngo&agrave;i ra, b&ecirc;n trong hộp đựng ban đầu của Buds Pro c&ograve;n c&oacute; ba k&iacute;ch cỡ n&uacute;t silicon kh&aacute;c nhau để bạn lựa chọn.</p>\r\n<p><img id=\"\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/07/15/Tai-nghe-samsung-galaxy-buds-pro-7.JPG\" alt=\"trải nghiệm Galaxy Buds Pro\" /></p>\r\n<h3><strong>Trải nghiệm pin l&ecirc;n đến 28 tiếng</strong></h3>\r\n<p>Pin l&agrave; một điểm cộng lớn của Galaxy Buds Pro. Nếu k&iacute;ch hoạt chống ồn chủ động, chiếc tai nghe c&oacute; thể vận h&agrave;nh trong 5 tiếng, cộng th&ecirc;m 13 tiếng nếu t&iacute;nh cả pin b&ecirc;n trong hộp sạc. Trong trường hợp bạn ngưng chống ồn chủ động, Galaxy Buds Pro sẽ duy tr&igrave; pin 8 tiếng li&ecirc;n tục v&agrave; c&oacute; th&ecirc;m 20 tiếng nếu sạc nhanh trong hộp sạc, như vậy thời lượng sử dụng tối đa của sản phẩm l&ecirc;n tới 28 tiếng.</p>\r\n<p><img id=\"\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/07/15/Tai-nghe-samsung-galaxy-buds-pro-8.JPG\" alt=\"thời lượng pin Galaxy Buds Pro\" /></p>\r\n<h3><strong>Đạt chuẩn chống nước cao nhất từ trước tới nay</strong></h3>\r\n<p>Galaxy Buds Pro l&agrave; mẫu tai nghe đạt chuẩn kh&aacute;ng nước IPX7 &ndash; cao nhất trong tất cả c&aacute;c d&ograve;ng Galaxy Buds từ trước tới nay. Nhờ lợi thế n&agrave;y, người d&ugrave;ng Buds Pro sẽ thoải m&aacute;i chạy bộ, tập thể dục v&agrave; hoạt động thể thao khi đeo tai nghe m&agrave; kh&ocirc;ng lo mồ h&ocirc;i sẽ l&agrave;m ảnh hưởng tới độ bền thiết bị. Những cơn mưa bất chợt hoặc tai nghe về nước cũng chẳng l&agrave;m ảnh hưởng tới độ bền bỉ của Galaxy Buds Pro, gi&uacute;p bạn an t&acirc;m hơn trong suốt qu&aacute; tr&igrave;nh sử dụng sản phẩm.</p>\r\n<p><img id=\"\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/07/15/Tai-nghe-samsung-galaxy-buds-pro-9.jpg\" alt=\"chống nước Galaxy Buds Pro\" /></p>\r\n</div>\r\n</div>', 0, '3990000', '1c9dcfe262.jpg', 1),
(11, 'Tai nghe AirPods Max', 3, 1, '<h2 class=\"card-title\"><strong style=\"font-size: 10px;\">L&agrave; chiếc&nbsp;<a title=\"Tham khảo tai nghe Airpods ch&iacute;nh h&atilde;ng Apple tại FPTShop\" type=\"Tham khảo tai nghe Airpods ch&iacute;nh h&atilde;ng Apple tại FPTShop\" href=\"https://fptshop.com.vn/phu-kien/tai-nghe?khac=apple-airpods\">AirPods</a>&nbsp;đầu ti&ecirc;n trong lịch sử Apple đi theo phong c&aacute;ch thiết kế over-ear chuy&ecirc;n dụng,&nbsp;<a title=\"Tai nghe AirPods Max ch&iacute;nh h&atilde;ng Apple tại FPTShop\" type=\"Tai nghe AirPods Max ch&iacute;nh h&atilde;ng Apple tại FPTShop\" href=\"https://fptshop.com.vn/phu-kien/tai-nghe-airpods-max\">AirPods Max</a>&nbsp;đem tới sự c&acirc;n bằng ho&agrave;n hảo giữa trải nghiệm &acirc;m thanh trung thực v&agrave; t&iacute;nh tiện dụng đặc trưng của d&ograve;ng tai nghe đến từ T&aacute;o khuyết.</strong></h2>\r\n<div class=\"card-body\">\r\n<div class=\"st-pd-content\">\r\n<p><img id=\"\" class=\"Tai nghe Apple AirPods Max\" title=\"Tai nghe Apple AirPods Max\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/An/Tai-nghe-apple-airpods-max-2.jpg\" alt=\"Tai nghe Apple AirPods Max\" /></p>\r\n<h3><strong>Chiếc tai nghe over-ear tinh tế nhất</strong></h3>\r\n<p>Với AirPods Max, Apple đ&atilde; định nghĩa lại phong c&aacute;ch over-ear khi đem tới thiết kế tối giản từ&nbsp;<a title=\"Tham khảo tai nghe chụp tai gi&aacute; tốt tại FPTShop\" type=\"Tham khảo tai nghe chụp tai gi&aacute; tốt tại FPTShop\" href=\"https://fptshop.com.vn/phu-kien/tai-nghe?loai=tai-nghe-chup-tai\">chụp tai</a>&nbsp;tới quai đeo headband. Mọi chi tiết tr&ecirc;n AirPods Max đều c&oacute; k&iacute;ch cỡ nhỏ gọn vừa đủ, hướng tới cảm gi&aacute;c thoải m&aacute;i nhất cho mọi đối tượng kh&aacute;ch h&agrave;ng d&ugrave; đeo li&ecirc;n tục h&agrave;ng giờ.</p>\r\n<p><img id=\"\" class=\"AirPods Max - Chiếc tai nghe over-ear tinh tế nhất\" title=\"AirPods Max - Chiếc tai nghe over-ear tinh tế nhất\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/An/Tai-nghe-apple-airpods-max-g.JPG\" alt=\"AirPods Max - Chiếc tai nghe over-ear tinh tế nhất\" /></p>\r\n<p>Trong đ&oacute;, phần headband được t&iacute;ch hợp th&ecirc;m mặt lưới nhẹ nh&agrave;ng, vừa đem tới cảm gi&aacute;c th&ocirc;ng tho&aacute;ng, vừa giảm thiểu lực t&aacute;c động từ headband l&ecirc;n người d&ugrave;ng, tr&aacute;nh g&acirc;y kh&oacute; chịu bức b&iacute; như c&aacute;c d&ograve;ng&nbsp;<a title=\"Tham khảo th&ecirc;m tai nghe ch&iacute;nh h&atilde;ng gi&aacute; tốt nhất tại FPTShop\" type=\"Tham khảo th&ecirc;m tai nghe ch&iacute;nh h&atilde;ng gi&aacute; tốt nhất tại FPTShop\" href=\"https://fptshop.com.vn/phu-kien/tai-nghe\">tai nghe</a>&nbsp;over-ear kh&aacute;c.</p>\r\n<p>Apple đ&atilde; bọc một lớp chất liệu mềm b&ecirc;n ngo&agrave;i khung th&eacute;p kh&ocirc;ng gỉ để tạo cảm gi&aacute;c linh hoạt, mềm mại m&agrave; vẫn đảm bảo t&iacute;nh bền bỉ cứng c&aacute;p trong thiết kế tổng thể chung. Ngo&agrave;i ra, chủ nh&acirc;n của AirPods Max c&oacute; thể điều chỉnh độ d&agrave;i hai b&ecirc;n headband cho tới khi ưng &yacute; v&agrave; ph&ugrave; hợp với k&iacute;ch cỡ ri&ecirc;ng của bản th&acirc;n.</p>\r\n<p><img id=\"\" class=\"AirPods Max - chất liệu mềm b&ecirc;n ngo&agrave;i khung th&eacute;p kh&ocirc;ng gỉ\" title=\"AirPods Max - chất liệu mềm b&ecirc;n ngo&agrave;i khung th&eacute;p kh&ocirc;ng gỉ\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/An/Tai-nghe-apple-airpods-max-9.jpg\" alt=\"AirPods Max - chất liệu mềm b&ecirc;n ngo&agrave;i khung th&eacute;p kh&ocirc;ng gỉ\" /></p>\r\n<p>Việc ứng dụng chất liệu nh&ocirc;m Anodized khi chế tạo phần chụp tai hai b&ecirc;n gi&uacute;p c&acirc;n bằng &aacute;p suất b&ecirc;n trong hiệu quả m&agrave; vẫn tối ưu h&oacute;a t&iacute;nh thẩm mỹ cho AirPods Max.</p>\r\n<p><img id=\"\" class=\"AirPods Max - ứng dụng chất liệu nh&ocirc;m Anodized\" title=\"AirPods Max - ứng dụng chất liệu nh&ocirc;m Anodized\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/An/Tai-nghe-apple-airpods-max-4.jpg\" alt=\"AirPods Max - ứng dụng chất liệu nh&ocirc;m Anodized\" /></p>\r\n<h3><strong>Đệm tai &ecirc;m &aacute;i v&agrave; tho&aacute;ng kh&iacute; tối đa</strong></h3>\r\n<p>Như mọi sản phẩm&nbsp;<a title=\"Tham khảo tai nghe ch&iacute;nh h&atilde;ng Apple tại FPT Shop\" type=\"Tham khảo tai nghe ch&iacute;nh h&atilde;ng Apple tại FPT Shop\" href=\"https://fptshop.com.vn/phu-kien/tai-nghe?thuong-hieu=apple\">tai nghe Apple</a>&nbsp;kh&aacute;c, AirPods Max được ho&agrave;n thiện tỉ mỉ đến từng chi tiết nhỏ nhất. Nếu những model AirPods tiền nhiệm ghi điểm ở phần driver b&aacute;m tai m&agrave; vẫn thoải m&aacute;i th&igrave; AirPods Max chọn hướng sử dụng đệm tai &ecirc;m &aacute;i nhất.</p>\r\n<p>Apple đ&atilde; ứng dụng chất liệu bọt hoạt t&iacute;nh chuy&ecirc;n dụng cho c&aacute;c thiết bị &acirc;m thanh l&agrave;m th&agrave;nh phần ch&iacute;nh để sản xuất đệm tai. Bọc b&ecirc;n ngo&agrave;i l&agrave; một lớp vải lưới đặc biệt, được thiết kế ri&ecirc;ng nhằm tạo cảm gi&aacute;c mềm mại m&agrave; vẫn tho&aacute;ng kh&iacute;.</p>\r\n<p><img id=\"\" class=\"AirPods Max - Đệm tai &ecirc;m &aacute;i v&agrave; tho&aacute;ng kh&iacute; tối đa\" title=\"AirPods Max - Đệm tai &ecirc;m &aacute;i v&agrave; tho&aacute;ng kh&iacute; tối đa\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/An/Tai-nghe-apple-airpods-max-a.JPG\" alt=\"AirPods Max - Đệm tai &ecirc;m &aacute;i v&agrave; tho&aacute;ng kh&iacute; tối đa\" /></p>\r\n<h3><strong>Điều khiển dễ d&agrave;ng với n&uacute;t xoay Digital Crown</strong></h3>\r\n<p>Nhỏ gọn v&agrave; tinh tế, n&uacute;t xoay Digital Crown chỉ chiếm diện t&iacute;ch khi&ecirc;m tốn ở một b&ecirc;n chụp tai nhưng lại hỗ trợ người d&ugrave;ng thực hiện được rất nhiều t&aacute;c vụ điều khiển AirPods Max. Từ việc tinh chỉnh độ to/nhỏ của &acirc;m lượng cho tới c&aacute;c chức năng như chuyển b&agrave;i, nhận cuộc gọi đến v&agrave; k&iacute;ch hoạt trợ l&yacute; ảo Siri đều rất dễ d&agrave;ng v&agrave; tiện dụng.</p>\r\n<p><img id=\"\" class=\"AirPods Max - Nhỏ gọn v&agrave; tinh tế, n&uacute;t xoay Digital Crown\" title=\"AirPods Max - Nhỏ gọn v&agrave; tinh tế, n&uacute;t xoay Digital Crown\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/An/Tai-nghe-apple-airpods-max-8(1).JPG\" alt=\"AirPods Max - Nhỏ gọn v&agrave; tinh tế, n&uacute;t xoay Digital Crown\" /></p>\r\n<h3><strong>&Acirc;m thanh cao cấp chưa từng c&oacute; tr&ecirc;n AirPods</strong></h3>\r\n<p>Hướng tới việc tạo n&ecirc;n một chiếc tai nghe cho chất &acirc;m cao cấp vượt qua cả&nbsp;<a title=\"Tai nghe AirPods Pro ch&iacute;nh h&atilde;ng gi&aacute; rẻ tại FPT Shop\" type=\"Tai nghe AirPods Pro ch&iacute;nh h&atilde;ng gi&aacute; rẻ tại FPT Shop\" href=\"https://fptshop.com.vn/phu-kien/apple-tai-nghe-airpods-pro\">AirPods Pro</a>, Apple kh&ocirc;ng chỉ tối ưu thiết kế AirPods Max m&agrave; c&ograve;n t&iacute;ch hợp c&ocirc;ng nghệ chống ồn chủ động ANC v&agrave;o sản phẩm n&agrave;y. Mỗi th&agrave;nh phần cấu tạo n&ecirc;n tr&igrave;nh điều khiển &acirc;m thanh đều được tinh chỉnh nhằm đem tới chất lượng &acirc;m thanh đầu ra c&oacute; độ trung thực cao nhất.</p>\r\n<p>Bạn sẽ h&agrave;i l&ograve;ng với &acirc;m bass trầm ấm, dải mid r&otilde; r&agrave;ng v&agrave; &acirc;m treble sắc sảo m&agrave; sản phẩm đem lại. D&ugrave; gu của bạn l&agrave; nhạc trẻ s&ocirc;i động hay nhạc kh&ocirc;ng lời nhẹ nh&agrave;ng, AirPods Max cũng sẽ đ&aacute;p ứng tốt nhu cầu của bạn.</p>\r\n<p><img id=\"\" class=\"AirPods Max - &Acirc;m thanh cao cấp chưa từng c&oacute; tr&ecirc;n AirPods\" title=\"AirPods Max - &Acirc;m thanh cao cấp chưa từng c&oacute; tr&ecirc;n AirPods\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/An/Tai-nghe-apple-airpods-max-5.jpg\" alt=\"AirPods Max - &Acirc;m thanh cao cấp chưa từng c&oacute; tr&ecirc;n AirPods\" /></p>\r\n<h3><strong>Chip H1 hiện đại &ndash; tr&aacute;i tim của c&ocirc;ng nghệ Apple</strong></h3>\r\n<p>AirPods Max sở hữu kh&ocirc;ng chỉ một m&agrave; tới hai bộ vi xử l&yacute; H1 - ph&acirc;n bố ở hai b&ecirc;n tai nghe. Sự g&oacute;p mặt của chip H1 gi&uacute;p xử l&yacute; t&iacute;n hiệu &acirc;m thanh truyền tới v&agrave; tạo n&ecirc;n chất &acirc;m đầu ra tối ưu nhất. Phần mềm m&agrave; c&aacute;c kỹ sư T&aacute;o khuyết đưa v&agrave;o tận dụng hiệu quả cả 10 l&otilde;i xử l&yacute; &acirc;m thanh trong mỗi chip H1, từ đ&oacute; gia tăng khả năng chống ồn chủ động tối đa. Kh&ocirc;ng chỉ vậy, H1 c&ograve;n l&agrave; tr&aacute;i tim c&ocirc;ng nghệ Apple, vừa đảm bảo khả năng tương t&aacute;c Siri nhanh nhạy, vừa gi&uacute;p tiết kiệm năng lượng tốt hơn.</p>\r\n<p><img id=\"\" class=\"AirPods Max - Chip H1 hiện đại &ndash; tr&aacute;i tim của c&ocirc;ng nghệ Apple\" title=\"AirPods Max - Chip H1 hiện đại &ndash; tr&aacute;i tim của c&ocirc;ng nghệ Apple\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/An/Tai-nghe-apple-airpods-max-7.jpg\" alt=\"AirPods Max - Chip H1 hiện đại &ndash; tr&aacute;i tim của c&ocirc;ng nghệ Apple\" /></p>\r\n<h3><strong>Tương th&iacute;ch tối ưu với c&aacute;c sản phẩm Apple</strong></h3>\r\n<p>AirPods Max thừa hưởng mọi c&ocirc;ng nghệ hiện đại trước đ&oacute; của d&ograve;ng sản phẩm AirPods, từ việc tương t&aacute;c v&agrave; ra lệnh cho Siri tới khả năng kết nối linh hoạt với c&aacute;c sản phẩm mang logo T&aacute;o khuyết như&nbsp;<a title=\"Tham khảo điện thoại iPhone ch&iacute;nh h&atilde;ng tại FPT Shop\" type=\"Tham khảo điện thoại iPhone ch&iacute;nh h&atilde;ng tại FPT Shop\" href=\"https://fptshop.com.vn/dien-thoai/apple-iphone\">iPhone</a>,&nbsp;<a title=\"Tham khảo m&aacute;y t&iacute;nh bảng iPad ch&iacute;nh h&atilde;ng tại FPT Shop\" type=\"Tham khảo m&aacute;y t&iacute;nh bảng iPad ch&iacute;nh h&atilde;ng tại FPT Shop\" href=\"https://fptshop.com.vn/may-tinh-bang/apple-ipad\">iPad</a>&nbsp;v&agrave;&nbsp;<a title=\"Tham khảo Macbook ch&iacute;nh h&atilde;ng gi&aacute; tốt nhất tại FPT Shop\" type=\"Tham khảo Macbook ch&iacute;nh h&atilde;ng gi&aacute; tốt nhất tại FPT Shop\" href=\"https://fptshop.com.vn/may-tinh-xach-tay/apple-macbook\">MacBook</a>. Trải nghiệm kết nối v&agrave; tương t&aacute;c giữa c&aacute;c thiết bị diễn ra xuy&ecirc;n suốt m&agrave; ho&agrave;n to&agrave;n kh&ocirc;ng cần tới sự hỗ trợ của bất cứ c&aacute;p kết nối vướng v&iacute;u n&agrave;o.</p>\r\n<p>Cụ thể, AirPods Max sẽ tự động qu&eacute;t để gh&eacute;p đ&ocirc;i với iPhone/iPad khi hai thiết bị ở gần nhau. Một th&ocirc;ng b&aacute;o sẽ hiện l&ecirc;n để x&aacute;c nhận qu&aacute; tr&igrave;nh kết nối tr&ecirc;n thiết bị ph&aacute;t t&iacute;n hiệu. Tất cả những g&igrave; bạn cần l&agrave;m l&agrave; đồng &yacute; v&agrave; thưởng thức trải nghiệm &acirc;m thanh.</p>\r\n<p><img id=\"\" class=\"AirPods Max - Tương th&iacute;ch tối ưu với c&aacute;c sản phẩm Apple\" title=\"AirPods Max - Tương th&iacute;ch tối ưu với c&aacute;c sản phẩm Apple\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/An/Tai-nghe-apple-airpods-max-c.JPG\" alt=\"AirPods Max - Tương th&iacute;ch tối ưu với c&aacute;c sản phẩm Apple\" /></p>\r\n<p>Ngo&agrave;i ra, cơ chế chuyển thiết bị ph&aacute;t t&iacute;n hiệu từ iPhone sang iPad hoặc m&aacute;y Mac sẽ diễn ra rất linh hoạt. Nếu bạn đang ph&aacute;t nhạc bằng m&aacute;y Mac v&agrave; bỗng nhi&ecirc;n c&oacute; cuộc gọi tới tr&ecirc;n iPhone, tai nghe AirPods Max sẽ tự gh&eacute;p nối với iPhone để bạn nghe cuộc gọi đ&oacute;.</p>\r\n<p><img id=\"\" class=\"AirPods Max - chuyển thiết bị ph&aacute;t t&iacute;n hiệu từ iPhone sang iPad\" title=\"AirPods Max - chuyển thiết bị ph&aacute;t t&iacute;n hiệu từ iPhone sang iPad\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/An/Tai-nghe-apple-airpods-max-d.JPG\" alt=\"AirPods Max - chuyển thiết bị ph&aacute;t t&iacute;n hiệu từ iPhone sang iPad\" /></p>\r\n<p>Chia sẻ &acirc;m thanh dễ d&agrave;ng với một chiếc tai nghe AirPods kh&aacute;c. Trong trường hợp bạn đang nghe nhạc bằng iPhone v&agrave; muốn chia sẻ bản nhạc th&uacute; vị n&agrave;y với một ai kh&aacute;c, hệ thống sẽ cho ph&eacute;p bạn sử dụng một thiết bị duy nhất (iPhone/iPad hoặc Mac) để ph&aacute;t nhạc tới nhiều chiếc AirPods ở gần đ&oacute;.</p>\r\n<p><img id=\"\" class=\"AirPods Max - Chia sẻ &acirc;m thanh dễ d&agrave;ng với một chiếc tai nghe AirPod kh&aacute;c\" title=\"AirPods Max - Chia sẻ &acirc;m thanh dễ d&agrave;ng với một chiếc tai nghe AirPod kh&aacute;c\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/An/Tai-nghe-apple-airpods-max-e.JPG\" alt=\"AirPods Max - Chia sẻ &acirc;m thanh dễ d&agrave;ng với một chiếc tai nghe AirPod kh&aacute;c\" /></p>\r\n<h3><strong>Linh hoạt hơn với t&iacute;nh&nbsp;năng ph&aacute;t hiện vị tr&iacute;</strong></h3>\r\n<p>AirPods Max sẽ tạm dừng ph&aacute;t nhạc khi bạn hạ tai nghe xuống cổ để n&oacute;i chuyện với ai đ&oacute; nhằm tiết kiệm pin, v&agrave; sẽ tiếp tục qu&aacute; tr&igrave;nh ph&aacute;t nhạc nếu bạn đeo lại vị tr&iacute; cũ. Sự linh hoạt n&agrave;y gi&uacute;p bạn kh&ocirc;ng bỏ lỡ bất cứ tiết tấu n&agrave;o m&agrave; vẫn tr&ograve; chuyện thoải m&aacute;i với người cạnh b&ecirc;n.</p>\r\n<p>Ngo&agrave;i ra, việc sử dụng linh hoạt Siri gi&uacute;p bạn c&oacute; thể ra lệnh cho trợ l&yacute; ảo đọc tin nhắn tới th&agrave;nh tiếng v&agrave; phản hồi m&agrave; kh&ocirc;ng cần phải chạm tay v&agrave;o iPhone. Khẩu lệnh quen thuộc &ldquo;Hey, Siri&rdquo; sẽ mở ra một trải nghiệm tiện dụng khi tận dụng Siri để thực hiện h&agrave;ng loạt t&aacute;c vụ kh&aacute;c như kiểm tra thời tiết, l&ecirc;n lịch họp v&agrave; nhiều hơn thế nữa.</p>\r\n<p><img id=\"\" class=\"AirPods Max - Linh hoạt hơn với khả năng ph&aacute;t hiện vị tr&iacute;\" title=\"AirPods Max - Linh hoạt hơn với khả năng ph&aacute;t hiện vị tr&iacute;\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/An/Tai-nghe-apple-airpods-max-f.jpg\" alt=\"AirPods Max - Linh hoạt hơn với khả năng ph&aacute;t hiện vị tr&iacute;\" /></p>\r\n<h3><strong>Khả năng tiết kiệm pin cực kỳ hiệu quả</strong></h3>\r\n<p>Khi được cất trong Smart Case chuy&ecirc;n dụng, tai nghe AirPods Max sẽ tự động chuyển sang trạng th&aacute;i ti&ecirc;u thụ năng lượng thấp để tiết kiệm pin tối ưu v&agrave; k&eacute;o d&agrave;i thời gian sử dụng. C&ocirc;ng nghệ sạc nhanh đem tới khả năng sạc 5 ph&uacute;t v&agrave; đem tới 1,5 giờ nghe nhạc li&ecirc;n tục sau đ&oacute;. Khi ở trạng th&aacute;i đầy năng lượng, AirPods Max cho ph&eacute;p bạn đ&agrave;m thoại, nghe nhạc, xem phim suốt nhiều giờ d&ugrave; c&oacute; k&iacute;ch hoạt chống ồn chủ động ANC.</p>\r\n<p><img id=\"\" class=\"AirPods Max - Khả năng tiết kiệm pin cực kỳ hiệu quả\" title=\"AirPods Max - Khả năng tiết kiệm pin cực kỳ hiệu quả\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/An/Tai-nghe-apple-airpods-max-6(1).jpg\" alt=\"AirPods Max - Khả năng tiết kiệm pin cực kỳ hiệu quả\" /></p>\r\n</div>\r\n</div>', 1, '13990000', 'b05c5a2c3e.jpg', 1);
INSERT INTO `tbl_product` (`productId`, `productName`, `catId`, `brandId`, `product_description`, `type`, `price`, `image`, `status`) VALUES
(12, 'Tai nghe AirPods 2 hộp sạc dây', 3, 1, '<h2 class=\"card-title\"><strong style=\"font-size: 10px;\">Kế thừa to&agrave;n bộ tinh hoa của chiếc tai nghe AirPods đời đầu,&nbsp;<a href=\"https://fptshop.com.vn/phu-kien/apple-tai-nghe-airpods-2-hop-sac-day-mv7n2vna\">tai nghe AirPods 2</a>&nbsp;được Apple bổ sung nhiều n&acirc;ng cấp hiệu quả để gia tăng thời lượng pin cũng như tối ưu h&oacute;a &acirc;m thanh để trở th&agrave;nh một trong những mẫu tai nghe True Wireless c&oacute; c&ocirc;ng nghệ tốt nhất.</strong></h2>\r\n<div class=\"card-body\">\r\n<div class=\"st-pd-content\">\r\n<p><img id=\"M&ocirc; tả sản phẩm AirPods 2 hộp sạc d&acirc;y\" class=\"Tai nghe AirPods 2 hộp sạc d&acirc;y\" title=\"Tai nghe AirPods 2 hộp sạc d&acirc;y\" src=\"http://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/AnhNQ/1310/Mo-ta-san-pham-airpods-2-hop-sac-day-1.jpg\" alt=\"Tai nghe AirPods 2 hộp sạc d&acirc;y\" /></p>\r\n<h2><strong>Đơn giản, hiệu quả v&agrave; tinh tế của tai nghe AirPod 2</strong></h2>\r\n<p>Kể từ khi bắt đầu th&agrave;nh lập, Apple đ&atilde; nổi tiếng với việc theo đuổi triết l&yacute;&nbsp;<strong>thiết kế đơn giản đẹp mắt</strong>&nbsp;m&agrave; vẫn đảm bảo hiệu quả tối đa cho người d&ugrave;ng, điều n&agrave;y được thể hiện r&otilde; n&eacute;t qua diện mạo của AirPods 2.</p>\r\n<p>C&aacute;ch thiết kế tinh tế với hai b&ecirc;n tai nghe kh&ocirc;ng d&acirc;y đi c&ugrave;ng chiếc hộp đựng ki&ecirc;m sạc pin l&agrave; điểm đặc trưng của d&ograve;ng tai nghe AirPods. Giống như thế hệ đầu, AirPods 2 c&oacute; phần th&acirc;n tai nghe chạy dọc xuống ph&iacute;a dưới khi đeo, khiến ch&uacute;ng ta kh&oacute; l&ograve;ng nhầm lẫn với d&ograve;ng tai nghe kh&ocirc;ng d&acirc;y n&agrave;o kh&aacute;c.</p>\r\n<p><img id=\"\" class=\"Đơn giản, hiệu quả v&agrave; tinh tế của tai nghe AirPod 2\" title=\"Đơn giản, hiệu quả v&agrave; tinh tế của tai nghe AirPod 2\" src=\"http://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/AnhNQ/1310/Mo-ta-san-pham-airpods-2-hop-sac-day-5.jpg\" alt=\"Đơn giản, hiệu quả v&agrave; tinh tế của tai nghe AirPod 2\" /></p>\r\n<h2><strong>Đem lại cảm gi&aacute;c thoải m&aacute;i khi đeo</strong></h2>\r\n<p>Những trải nghiệm thực tế cho thấy AirPods 2 rất kh&oacute; rơi tr&ecirc;n tai người d&ugrave;ng, nhưng lại kh&ocirc;ng hề chật chội g&acirc;y kh&oacute; chịu cho khoang tai khi sử dụng trong thời gian d&agrave;i.</p>\r\n<p>Để l&agrave;m được điều n&agrave;y, Apple đ&atilde; phải thử nghiệm rất nhiều nhằm t&igrave;m ra c&aacute;ch thiết kế phần earbuds bo tr&ograve;n hiệu quả v&agrave; vừa vặn nhất. Cảm gi&aacute;c thoải m&aacute;i khi đeo gi&uacute;p AirPods 2 phục vụ tốt cho những kh&aacute;ch h&agrave;ng c&oacute; nhu cầu trải nghiệm &acirc;m thanh trong l&uacute;c tập thể dục v&agrave; di chuyển tr&ecirc;n đường.</p>\r\n<p><img id=\"\" src=\"http://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/AnhNQ/1310/Mo-ta-san-pham-airpods-2-hop-sac-day-3.jpg\" alt=\"AirPods 2 đem lại cảm gi&aacute;c thoải m&aacute;i khi đeo\" /></p>\r\n<h2><strong>Hộp sạc</strong><strong>&nbsp;tiện dụng đặc trưng của d&ograve;ng AirPods</strong></h2>\r\n<p>Với AirPods 2, hộp đựng kh&ocirc;ng chỉ l&agrave; nơi cất giữ những chiếc tai nghe khi kh&ocirc;ng d&ugrave;ng tới m&agrave; c&ograve;n l&agrave;&nbsp;<a href=\"https://fptshop.com.vn/phu-kien/apple-hop-sac-khong-day-cho-airpods-mr8u2vna\">hộp sạc cho tai nghe Airpods</a>.</p>\r\n<p>Đội ngũ kĩ sư của Apple tiếp tục thể hiện sự tinh tế khi thiết kế chiếc hộp đựng AirPods gọn g&agrave;ng với c&aacute;c g&oacute;c bo tr&ograve;n b&oacute;ng bẩy, k&iacute;ch cỡ nhỏ nhẹ cho ph&eacute;p người d&ugrave;ng mang theo b&ecirc;n m&igrave;nh trong mọi chuyến đi. Phi&ecirc;n bản bạn đang theo d&otilde;i l&agrave; AirPods 2 hộp sạc d&acirc;y, khi muốn nạp năng lượng cho thiết bị, bạn chỉ cần kết nối chiếc hộp với bộ sạc th&ocirc;ng qua cổng lightning quen thuộc của Apple.</p>\r\n<p><img id=\"Hộp sạc tiện dụng đặc trưng của d&ograve;ng AirPods\" src=\"http://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/AnhNQ/1310/Mo-ta-san-pham-airpods-2-hop-sac-day-9.jpg\" alt=\"Hộp sạc tiện dụng đặc trưng của d&ograve;ng AirPods\" /></p>\r\n<h2><strong>Chất lượng &acirc;m thanh tốt, ph&ugrave; hợp cho c&ocirc;ng việc v&agrave; giải tr&iacute;</strong></h2>\r\n<p>Tai nghe AirPods 2 đem tới trải nghiệm &acirc;m thanh dễ nghe với &acirc;m trung vững v&agrave; &acirc;m cao độ tốt. Chất &acirc;m m&agrave; sản phẩm đem lại kh&ocirc;ng hề ch&oacute;i gắt m&agrave; thi&ecirc;n về t&ocirc;ng ấm, mịn ở dải trầm.</p>\r\n<p>Nh&igrave;n chung, AirPods 2 được tạo ra để đ&aacute;p ứng mọi nhu cầu của người d&ugrave;ng d&ugrave; l&agrave; nghe nhạc, xem phim cho tới những t&aacute;c vụ học tập hay c&ocirc;ng việc.</p>\r\n<p><img id=\"\" src=\"http://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/AnhNQ/1310/Mo-ta-san-pham-airpods-2-hop-sac-day-11.JPG\" alt=\"Chất lượng &acirc;m thanh AirPods 2 tốt\" /></p>\r\n<h2><strong>Khiến bạn h&agrave;i l&ograve;ng về trải nghiệm pin</strong></h2>\r\n<p>Một trong những điểm nổi bật của AirPods 2 so với AirPods đời đầu nằm ở thời lượng pin xuất sắc, Apple đ&atilde; mang tới những cải tiến hiệu quả để gi&uacute;p AirPods 2 c&oacute;&nbsp;<strong>thời lượng pin l&ecirc;n tới 4 tiếng rưỡi.</strong></p>\r\n<p>Tuy nhi&ecirc;n, bạn c&oacute; thể dễ d&agrave;ng nạp th&ecirc;m năng lượng cho tai nghe bất cứ l&uacute;c n&agrave;o th&ocirc;ng qua chiếc hộp đựng lưu trữ pin đủ cho 24 giờ trải nghiệm. Ước t&iacute;nh, 15 ph&uacute;t sạc trong hộp đựng l&agrave; AirPods 2 c&oacute; thể đem lại khoảng 3 giờ trải nghiệm nữa.</p>\r\n<p><img id=\"\" src=\"http://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/AnhNQ/1310/Mo-ta-san-pham-airpods-2-hop-sac-day-4.jpg\" alt=\"Khiến bạn h&agrave;i l&ograve;ng về trải nghiệm pin tr&ecirc;n AirPods 2\" /></p>\r\n<h2><strong>Sự kết hợp ho&agrave;n hảo&nbsp;</strong><strong>giữa iPhone</strong><strong>&nbsp;v&agrave; AirPods 2</strong></h2>\r\n<p>Apple đ&atilde; tối ưu h&oacute;a để gi&uacute;p AirPods 2 trở th&agrave;nh chiếc tai nghe cực k&igrave; tiện dụng nếu gh&eacute;p đ&ocirc;i với d&ograve;ng&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai/apple-iphone\">điện thoại iPhone</a>. Cụ thể, khi bạn th&aacute;o một b&ecirc;n tai ra khi đang nghe, ứng dụng nhạc sẽ tạm dừng v&agrave; tiếp tục hoạt động nếu bạn đeo hai b&ecirc;n tai trở lại.</p>\r\n<p>Kh&ocirc;ng chỉ vậy, tới thế hệ AirPods 2, thao t&aacute;c k&iacute;ch hoạt trợ l&yacute; ảo Siri đ&atilde; được chuyển th&agrave;nh khẩu lệnh &ldquo;Hey Siri&rdquo; tiện dụng thay v&igrave; phải g&otilde; hai lần l&ecirc;n&nbsp;<a href=\"http://fptshop.com.vn/phu-kien/tai-nghe\">tai nghe</a>&nbsp;như thế hệ đầu.</p>\r\n<p><img id=\"Sự kết hợp ho&agrave;n hảo giữa iPhone v&agrave; AirPods 2\" src=\"http://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/AnhNQ/1310/Mo-ta-san-pham-airpods-2-hop-sac-day-6.JPG\" alt=\"Sự kết hợp ho&agrave;n hảo giữa iPhone v&agrave; AirPods 2\" /></p>\r\n</div>\r\n</div>', 1, '3299000', '0ac7cba755.jpg', 1),
(13, 'Pin sạc dự phòng Veger 10000mAh L10 PD&QC3.0 Đen', 3, 10, '<h2 class=\"card-title\"><strong style=\"font-size: 10px;\">Bộ&nbsp;<a href=\"https://fptshop.com.vn/phu-kien/pin-sac-du-phong-li-polymer-10000mah-veger-l10-pd-qc3-0-den\">pin sạc dự ph&ograve;ng Li-Polymer VEGER L10 PD&amp;QC3.0</a>&nbsp;hội tụ những c&ocirc;ng nghệ hiện đại nhất trong lĩnh vực bao gồm kiểm so&aacute;t nguồn điện Power Delivery v&agrave;&nbsp;<a href=\"https://fptshop.com.vn/phu-kien/sac-cap-nhanh\">sạc nhanh</a>&nbsp;Quick Charge 3.0. Nhờ vậy, sản phẩm đảm bảo hai yếu tố then chốt l&agrave; độ an to&agrave;n v&agrave; t&iacute;nh tiện dụng. Ngo&agrave;i ra, thiết kế hiện đại sang trọng cũng l&agrave; điểm cộng lớn m&agrave; VEGER đem tới cho thiết bị.</strong></h2>\r\n<div class=\"card-body\">\r\n<div class=\"st-pd-content\">\r\n<p><strong><img id=\"sạc dự ph&ograve;ng Li-Polymer 10000mAh VEGER L10 PD 11\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/BaoPK/PhuKien/Pin-sac-du-phong-li-polymer-10000mah-veger-l10-pd-1.jpg\" alt=\"sạc dự ph&ograve;ng Li-Polymer 10000mAh VEGER L10 PD 1\" /></strong></p>\r\n<h3><strong>Thiết kế sang trọng v&agrave; lịch l&atilde;m</strong></h3>\r\n<p>So với những bộ&nbsp;<a href=\"https://fptshop.com.vn/phu-kien/sac-du-phong\">pin sạc dự ph&ograve;ng</a>&nbsp;tr&ecirc;n thị trường, VEGER L10 PD&amp;QC3.0 c&oacute; diện mạo sang trọng v&agrave; lịch l&atilde;m hơn nhiều. Bộ khung vỏ chế t&aacute;c từ nhựa ABS bền bỉ được phủ nh&aacute;m cả hai mặt trước/sau gi&uacute;p tăng khả năng chống xước, đồng thời tối ưu h&oacute;a trải nghiệm cầm nắm tr&ecirc;n tay. Phần viền bao quanh ho&agrave;n thiện với m&agrave;u v&agrave;ng gold tạo điểm nhấn thu h&uacute;t về t&iacute;nh thẩm mỹ.</p>\r\n<p><img id=\"sạc dự ph&ograve;ng Li-Polymer 10000mAh VEGER L10 PD 33\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/BaoPK/PhuKien/Pin-sac-du-phong-li-polymer-10000mah-veger-l10-pd-4.jpg\" alt=\"sạc dự ph&ograve;ng Li-Polymer 10000mAh VEGER L10 PD 3\" /></p>\r\n<h3><strong>M&agrave;n h&igrave;nh hiển thị phần trăm pin</strong></h3>\r\n<p>Thay v&igrave; hiển thị pin theo dạng vạch th&ocirc;ng b&aacute;o để người d&ugrave;ng tự quan s&aacute;t v&agrave; ước lượng, VEGER L10 PD&amp;QC3.0 sở hữu m&agrave;n h&igrave;nh LCD với khả năng hiển thị phần trăm pin c&ograve;n lại rất r&otilde; r&agrave;ng. Ngo&agrave;i ra, m&agrave;n h&igrave;nh n&agrave;y c&ograve;n cho ph&eacute;p chủ nh&acirc;n&nbsp;<a href=\"https://fptshop.com.vn/phu-kien/sac-cap\">bộ sạc</a>&nbsp;thấy được d&ograve;ng sạc v&agrave;o. Nếu th&ocirc;ng số tr&ecirc;n m&agrave;n h&igrave;nh l&agrave; 5V th&igrave; điện thoại của bạn đang sạc&nbsp;với tốc độ thường, c&ograve;n nếu hiển thị từ 9V tới 12V th&igrave; cơ chế sạc nhanh đ&atilde; được k&iacute;ch hoạt (t&ugrave;y theo từng d&ograve;ng&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai\">smartphone</a>).</p>\r\n<p><img id=\"sạc dự ph&ograve;ng Li-Polymer 10000mAh VEGER L10 PD 122\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/BaoPK/PhuKien/Pin-sac-du-phong-li-polymer-10000mah-veger-l10-pd-3%20(1).jpg\" alt=\"sạc dự ph&ograve;ng Li-Polymer 10000mAh VEGER L10 PD 12\" /></p>\r\n<h3><strong>Những c&ocirc;ng nghệ sạc tối t&acirc;n h&agrave;ng đầu</strong></h3>\r\n<p>Sự ưu việt của VEGER L10 PD&amp;QC3.0 thể hiện ở loạt c&ocirc;ng nghệ sạc nhanh m&agrave; sản phẩm quy tụ. Trong đ&oacute;, Power Delivery sẽ k&iacute;ch hoạt tự động nếu bạn sạc pin cho&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai/apple-iphone\">iPhone</a>, c&ograve;n Quick Charge 3.0 sẽ tự động triển khai khi bạn kết nối bộ sạc với smartphone Android như&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai/samsung\">Samsung</a>&nbsp;hay&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai/xiaomi\">Xiaomi</a>. Sở hữu cả hai c&ocirc;ng nghệ sạc nhanh t&acirc;n tiến nhất, VEGER L10 PD&amp;QC3.0 đạt độ tương th&iacute;ch tối ưu với mọi d&ograve;ng điện thoại để bạn an t&acirc;m sử dụng.</p>\r\n<p><img id=\"sạc dự ph&ograve;ng Li-Polymer 10000mAh VEGER L10 PD 55\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/BaoPK/PhuKien/Pin-sac-du-phong-li-polymer-10000mah-veger-l10-pd-7.JPG\" alt=\"sạc dự ph&ograve;ng Li-Polymer 10000mAh VEGER L10 PD 5\" /></p>\r\n<h3><strong>R&uacute;t ngắn thời gian chờ đợi cho bạn</strong></h3>\r\n<p>Hai c&ocirc;ng nghệ Power Delivery v&agrave; Quick Charge 3.0 tr&ecirc;n VEGER L10 PD&amp;QC3.0 sẽ chia qu&aacute; tr&igrave;nh sạc cho điện thoại ra l&agrave;m ba giai đoạn: Từ 5% đến 50%, từ 51% đến 80% v&agrave; từ 81% đến khi đầy pin. Trong đ&oacute;, giai đoạn đầu đạt tốc độ nạp năng lượng nhanh nhất nhằm phục vụ cho những trường hợp cần thiết. Từ giai đoạn hai trở đi, tốc độ sẽ giảm dần để bảo đảm an to&agrave;n cho qu&aacute; tr&igrave;nh sạc cũng như cho sức khỏe vi&ecirc;n pin. Ước t&iacute;nh, bạn chỉ mất xấp xỉ 1 giờ 30 ph&uacute;t để sạc đầy pin cho&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai/iphone-12\">iPhone 12</a>.</p>\r\n<p><img id=\"sạc dự ph&ograve;ng Li-Polymer 10000mAh VEGER L10 PD 67\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/BaoPK/PhuKien/Pin-sac-du-phong-li-polymer-10000mah-veger-l10-pd-6.JPG\" alt=\"sạc dự ph&ograve;ng Li-Polymer 10000mAh VEGER L10 PD 6\" /></p>\r\n<h3><strong>Dung lượng 10.000 mAh với l&otilde;i pin cao cấp</strong></h3>\r\n<p><a href=\"https://fptshop.com.vn/phu-kien/sac-du-phong-veger\">Sạc dự ph&ograve;ng VEGER</a>&nbsp;L10 PD&amp;QC3.0 c&oacute; l&otilde;i pin&nbsp;cao cấp với&nbsp;<a href=\"https://fptshop.com.vn/phu-kien/sac-du-phong-10000-mah\">dung lượng 10.000 mAh</a>. Sau khi sạc đầy, sản phẩm sẽ chứa đủ năng lượng để sử dụng trong từ 2 đến 2.5 lần với những sản phẩm như iPhone 12 hay&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai/iphone-12-pro?dung-luong=128gb\">iPhone 12 Pro</a>. Bạn chỉ cần sạc đủ pin cho VEGER L10 PD&amp;QC3.0 l&agrave; sẽ c&oacute; thể sử dụng thoải m&aacute;i cho một chuyến h&agrave;nh tr&igrave;nh ngắn ng&agrave;y m&agrave; chẳng cần phải đem theo bộ sạc k&egrave;m m&aacute;y.</p>\r\n<p><img id=\"sạc dự ph&ograve;ng Li-Polymer 10000mAh VEGER L10 PD 99\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/BaoPK/PhuKien/Pin-sac-du-phong-li-polymer-10000mah-veger-l10-pd-5.jpg\" alt=\"sạc dự ph&ograve;ng Li-Polymer 10000mAh VEGER L10 PD 9\" /></p>\r\n<h3><strong>Nhiều cổng sạc, tương th&iacute;ch với mọi smartphone</strong></h3>\r\n<p>Sau khi sử dụng xong, thao t&aacute;c t&aacute;i tạo năng lượng VEGER L10 PD&amp;QC3.0 cũng rất dễ d&agrave;ng. Bộ sạc sử dụng nguồn v&agrave;o 5V/2A phổ biến v&agrave; c&oacute; hai cổng nguồn ra gồm USB Type-C c&ugrave;ng USB 3.0. Việc hỗ trợ nhiều nguồn ra gi&uacute;p bạn sạc nhiều điện thoại song song trong c&ugrave;ng thời điểm, đồng thời cho ph&eacute;p bạn dễ d&agrave;ng t&igrave;m được c&aacute;p kết nối từ bộ sạc tới smartphone tương th&iacute;ch.</p>\r\n<p><img id=\"sạc dự ph&ograve;ng Li-Polymer 10000mAh VEGER L10 PD 99\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/BaoPK/PhuKien/Pin-sac-du-phong-li-polymer-10000mah-veger-l10-pd-3%20(2).jpg\" alt=\"sạc dự ph&ograve;ng Li-Polymer 10000mAh VEGER L10 PD 9\" /></p>\r\n<h3><strong>Vận h&agrave;nh an to&agrave;n, chống ch&aacute;y nổ</strong></h3>\r\n<p>Với nguy&ecirc;n tắc đặt trải nghiệm v&agrave; sự an to&agrave;n của kh&aacute;ch h&agrave;ng l&ecirc;n h&agrave;ng đầu, nh&agrave; sản xuất trang bị cho bộ sạc dự ph&ograve;ng VEGER L10 PD&amp;QC3.0 hệ thống mạch IC gi&uacute;p điều tiết d&ograve;ng điện hiệu quả cho từng giai đoạn sạc, nhận biết nguồn v&agrave;o/nguồn ra để chống qu&aacute; tải v&agrave; chống ch&aacute;y nổ. Những hiện tượng dễ g&acirc;y nguy hiểm như đoản mạch hay qu&aacute; d&ograve;ng đều được ph&ograve;ng tr&aacute;nh khi bạn sử dụng thiết bị.</p>\r\n</div>\r\n</div>', 0, '549000', 'fefc8bd422.jpg', 1),
(14, 'Pin sạc dự phòng Xiaomi Mi 3 Ultra Compact 10000mAh', 3, 4, '<h2 class=\"card-title\"><strong style=\"font-size: 10px;\">Bạn sẽ kh&oacute; l&ograve;ng t&igrave;m thấy bộ sạc dự ph&ograve;ng 10.000 mAh n&agrave;o nhỏ gọn hơn&nbsp;<a href=\"https://fptshop.com.vn/phu-kien/pin-sac-du-phong-10000mah-mi-ultra-compact\">Mi Ultra Compact</a>&nbsp;tr&ecirc;n thị trường hiện nay. Với k&iacute;ch cỡ chỉ xấp xỉ một chiếc thẻ ATM, bộ sạc đắc lực của Xiaomi sẽ nằm gọn trong t&uacute;i quần của bạn. Sản phẩm được t&iacute;ch hợp cơ chế sạc nhanh, đảm bảo độ bền bỉ v&agrave; kết nối hai chiều USB-C.</strong></h2>\r\n<div class=\"card-body\">\r\n<div class=\"st-pd-content\">\r\n<p><strong><img id=\"\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/04/Pin-sac-du-phong-10000-mah-mi-ultra-compact-1.jpg\" alt=\"Pin sạc dự ph&ograve;ng 10000 mAh Mi Ultra Compact\" /></strong></p>\r\n<h3><strong>Bộ sạc 10.000 mAh gọn g&agrave;ng bậc nhất</strong></h3>\r\n<p>K&iacute;ch cỡ tổng thể của&nbsp;<a href=\"https://fptshop.com.vn/phu-kien/sac-du-phong\">sạc dự ph&ograve;ng</a>&nbsp;Mi Ultra Compact l&agrave; 9 x 6.4 x 2.4 cm v&agrave; đạt trọng lượng xấp xỉ 200 gram. Đ&acirc;y l&agrave; th&ocirc;ng số rất nhỏ so với một bộ sạc dự ph&ograve;ng hội tụ đủ c&aacute;c c&ocirc;ng nghệ an to&agrave;n đạt chuẩn v&agrave; c&oacute; dung lượng 10.000 mAh. Sự gọn nhẹ n&agrave;y tạo điều kiện thuận lợi để bạn mang theo bộ sạc trong t&uacute;i x&aacute;ch, t&uacute;i quần,&nbsp;<a href=\"https://fptshop.com.vn/phu-kien/balo-tui-xach\">balo</a>&nbsp;d&ugrave;ng trong mọi thời điểm.</p>\r\n<p><img id=\"\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/04/Pin-sac-du-phong-10000-mah-mi-ultra-compact-6.jpg\" alt=\"Sạc dự ph&ograve;ng 10000mAh Mi Ultra Compact 2\" /></p>\r\n<h3><strong>Dung lượng pin đủ sạc smartphone nhiều lần</strong></h3>\r\n<p>Mi Ultra Compact sử dụng c&ocirc;ng nghệ pin lithium ion polymer chất lượng cao. Theo số liệu từ&nbsp;<a href=\"https://fptshop.com.vn/xiaomi\">Xiaomi</a>, trung b&igrave;nh bộ sạc c&oacute; thể nạp năng lượng tới hơn ba lần cho một chiếc&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai\">điện thoại</a>&nbsp;như iPhone SE 2. Bạn sẽ an t&acirc;m rằng thiết bị di động của m&igrave;nh lu&ocirc;n c&oacute; nguồn năng lượng dự trữ ở cạnh b&ecirc;n.</p>\r\n<p><img id=\"\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/04/Pin-sac-du-phong-10000-mah-mi-ultra-compact-8.JPG\" alt=\"Sạc dự ph&ograve;ng 10000mAh Mi Ultra Compact 3\" /></p>\r\n<h3><strong>Sạc đồng thời ba thiết bị tương th&iacute;ch</strong></h3>\r\n<p>Ngo&agrave;i hai cổng USB-A, sạc dự ph&ograve;ng Mi Ultra Compact c&ograve;n được t&iacute;ch hợp th&ecirc;m một cổng USB-C tiện dụng. Ba cổng kết nối n&agrave;y c&oacute; thể sử dụng đồng thời trong c&ugrave;ng một thời điểm. Cho ph&eacute;p bạn sạc mọi thiết bị di động quanh m&igrave;nh như smartphone,&nbsp;<a href=\"https://fptshop.com.vn/may-tinh-bang\">tablet</a>, hay&nbsp;<a href=\"https://fptshop.com.vn/smartwatch\">smartwatch</a>, thực sự linh hoạt v&agrave; hết sức đa năng.</p>\r\n<p><img id=\"\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/04/Pin-sac-du-phong-10000-mah-mi-ultra-compact-7.JPG\" alt=\"Sạc dự ph&ograve;ng 10000mAh Mi Ultra Compact 4\" /></p>\r\n<h3><strong>Sạc nhanh hai chiều tr&ecirc;n cổng USB-C</strong></h3>\r\n<p>Cổng kết nối USB-C tr&ecirc;n Mi Ultra Compact đem đến khả năng sạc nhanh hai chiều cho thiết bị. Trong đ&oacute;, ri&ecirc;ng việc nạp năng lượng v&agrave;o trong bộ sạc c&oacute; thể đạt c&ocirc;ng suất 22.5W nếu bạn kết nối với củ sạc nhanh th&iacute;ch hợp. Do đ&oacute;, bạn chỉ cần phải chờ khoảng 3.5 giờ để Mi Ultra Compact t&aacute;i tạo lại năng lượng để sẵn s&agrave;ng đồng h&agrave;nh c&ugrave;ng bạn.</p>\r\n<p><img id=\"\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/04/Pin-sac-du-phong-10000-mah-mi-ultra-compact-5.jpg\" alt=\"Sạc dự ph&ograve;ng 10000mAh Mi Ultra Compact 5\" /></p>\r\n<h3><strong>Chip IC đảm bảo an to&agrave;n tối ưu</strong></h3>\r\n<p>Tr&ecirc;n Mi Ultra Compact, nh&agrave; sản xuất đ&atilde; ứng dụng c&ocirc;ng nghệ chip IC chất lượng cao kết hợp với cảm biến điện dung v&agrave; điện cảm c&oacute; độ ch&iacute;nh x&aacute;c cao nhằm đảm bảo t&iacute;nh an to&agrave;n trong qu&aacute; tr&igrave;nh sử dụng. Xuy&ecirc;n suốt v&ograve;ng đời sử dụng của bộ sạc, thiết bị sẽ duy tr&igrave; những phương ph&aacute;p bảo vệ như: Bảo vệ nhiệt độ, bảo vệ đoản mạch, bảo vệ qu&aacute; &aacute;p, bảo vệ qu&aacute; d&ograve;ng đầu v&agrave;o, bảo vệ qu&aacute; d&ograve;ng đầu ra, bảo vệ qu&aacute; &aacute;p đầu ra&hellip;</p>\r\n<p><img id=\"\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/04/Pin-sac-du-phong-10000-mah-mi-ultra-compact-2.JPG\" alt=\"Sạc dự ph&ograve;ng 10000mAh Mi Ultra Compact 6\" /></p>\r\n<h3><strong>Chất liệu cao cấp, chịu được sự thay đổi nhiệt đột ngột</strong></h3>\r\n<p>Bộ sạc dự ph&ograve;ng Mi Ultra Compact được ho&agrave;n thiện từ những chất liệu cao cấp v&agrave; th&acirc;n thiện với m&ocirc;i trường. Trong đ&oacute;, bộ vỏ kết hợp từ PC v&agrave; ABS cho thấy độ bền cao, được l&agrave;m nh&aacute;m nhẹ để tr&aacute;nh lưu dấu mồ h&ocirc;i đồng thời tr&aacute;nh xước x&aacute;t khi bạn cầm theo b&ecirc;n người. To&agrave;n bộ cấu tr&uacute;c của Mi Ultra Compact được l&agrave;m theo phong c&aacute;ch nguy&ecirc;n khối bền bỉ, liền mạch.</p>\r\n<p><img id=\"\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/04/Pin-sac-du-phong-10000-mah-mi-ultra-compact-3.JPG\" alt=\"Sạc dự ph&ograve;ng 10000mAh Mi Ultra Compact 7\" /></p>\r\n<h3><strong>Kiểm tra dung lượng pin c&ograve;n lại qua đ&egrave;n b&aacute;o</strong></h3>\r\n<p>Th&ocirc;ng qua c&aacute;c đ&egrave;n b&aacute;o đặt s&aacute;t c&aacute;c cổng kết nối của bộ sạc, Mi Ultra Compact sẽ th&ocirc;ng b&aacute;o cho bạn biết dung lượng pin c&ograve;n lại b&ecirc;n trong. Bạn chỉ cần căn cứ v&agrave;o đ&oacute; để l&ecirc;n kế hoạch sử dụng cho ph&ugrave; hợp, sạc bổ sung pin trước khi cầm theo hoặc ước lượng bộ sạc c&ograve;n đủ pin sạc điện thoại trong mấy lần.</p>\r\n<p><img id=\"\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/04/Pin-sac-du-phong-10000-mah-mi-ultra-compact-4.JPG\" alt=\"Sạc dự ph&ograve;ng 10000mAh Mi Ultra Compact 8\" /></p>\r\n</div>\r\n</div>', 0, '569000', '22452c7f70.png', 1),
(15, 'Tai nghe AirPods 3 2021', 3, 1, '<h2 class=\"card-title\"><strong style=\"font-size: 10px;\">Với&nbsp;<a href=\"https://fptshop.com.vn/phu-kien/tai-nghe-airpods-3-2021\">tai nghe AirPods 3</a>, Apple đem đến những cải tiến hiệu quả ở cả thiết kế, thời lượng pin, chất &acirc;m v&agrave; cảm ứng lực nhằm đem lại trải nghiệm sử dụng trọn vẹn tr&ecirc;n tất cả mọi kh&iacute;a cạnh. H&atilde;y thoải m&aacute;i tận hưởng những c&ocirc;ng nghệ ưu việt nhất tr&ecirc;n AirPods 3.</strong></h2>\r\n<div class=\"card-body\">\r\n<div class=\"st-pd-content\">\r\n<p><img id=\"\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/BaoPK/HDCameraZenfoneLaser/mo-ta-san-pham-airpods-3-1.JPG\" alt=\"tai nghe AirPods 3\" /></p>\r\n<h3><strong>Bước c&aacute;ch t&acirc;n ấn tượng về thiết kế</strong></h3>\r\n<p>So với&nbsp;<a href=\"https://fptshop.com.vn/phu-kien/apple-tai-nghe-airpods-2-hop-sac-day-mv7n2vna\">AirPods 2</a>, AirPods 3 đ&atilde; kho&aacute;c l&ecirc;n m&igrave;nh diện mạo ho&agrave;n to&agrave;n mới khi sử dụng hộp sạc thu&ocirc;n gọn theo chiều ngang v&agrave; th&acirc;n tai nghe được l&agrave;m ngắn hơn trước. Bạn sẽ c&oacute; cảm nhận gọn g&agrave;ng hơn khi trải nghiệm thế hệ&nbsp;<a href=\"https://fptshop.com.vn/apple/tai-nghe\">tai nghe Apple</a>&nbsp;mới.</p>\r\n<p>Với mỗi b&ecirc;n tai nghe nặng vỏn vẹn 4,28 gram v&agrave; hộp sạc nhẹ nh&agrave;ng 37,91 gram, AirPods 3 đ&atilde; chạm tới đỉnh cao mới của thiết kế tối giản nhỏ nhẹ - gi&uacute;p chủ nh&acirc;n thiết bị dễ d&agrave;ng mang theo để trải nghiệm kh&ocirc;ng gian &acirc;m nhạc ri&ecirc;ng tư ở bất cứ nơi n&agrave;o.</p>\r\n<p><img id=\"\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/BaoPK/HDCameraZenfoneLaser/mo-ta-san-pham-airpods-3-b.JPG\" alt=\"thiết kế tai nghe AirPods 3\" /></p>\r\n<h3><strong>Chống nước tốt, đạt chuẩn IPX4</strong></h3>\r\n<p>Kh&ocirc;ng chỉ sở hữu thiết kế nhỏ nhắn v&agrave; h&agrave;i h&ograve;a hơn, AirPods 3 c&ograve;n g&acirc;y ấn tượng nhờ khả năng chống nước đạt chuẩn IPX4 với cả hộp sạc v&agrave; hai b&ecirc;n tai nghe.</p>\r\n<p>Người d&ugrave;ng sẽ kh&ocirc;ng phải qu&aacute; lo lắng khi đang đeo AirPods 3 m&agrave; gặp phải cơn mưa r&agrave;o bất chợt, hoặc nghi ngại rằng mồ h&ocirc;i trong l&uacute;c tập luyện sẽ l&agrave;m ảnh hưởng đến độ bền thiết bị. H&atilde;y thỏa sức khơi dậy tinh thần thể thao h&agrave;o hứng qua kh&ocirc;ng gian &acirc;m nhạc s&ocirc;i động m&agrave; chiếc&nbsp;<a title=\"Tai nghe ch&iacute;nh h&atilde;ng\" type=\"Tai nghe ch&iacute;nh h&atilde;ng\" href=\"https://fptshop.com.vn/phu-kien/tai-nghe\">tai nghe</a>&nbsp;đem lại.</p>\r\n<p><img id=\"\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/BaoPK/HDCameraZenfoneLaser/mo-ta-san-pham-airpods-3-4.JPG\" alt=\"Chống nước tai nghe AirPods 3\" /></p>\r\n<h3><strong>H&ograve;a m&igrave;nh v&agrave;o &acirc;m thanh v&ograve;m sống động</strong></h3>\r\n<p>AirPods 3 được trang bị c&ocirc;ng nghệ &acirc;m thanh v&ograve;m Spatial Audio &ndash; cho ph&eacute;p thiết bị theo d&otilde;i hướng cử động cổ để giả lập chiều ph&aacute;t &acirc;m thanh, từ đ&oacute; mang lại cảm nhận sống động như ch&uacute;ng ta đang thực sự hiện diện tại kh&ocirc;ng gian ph&ograve;ng thu đ&iacute;ch thực. Nhờ Spatial Audio, mọi t&aacute;c vụ giải tr&iacute; m&agrave; bạn trải nghiệm tr&ecirc;n AirPods như nghe nhạc, xem phim hay chơi game đều ch&acirc;n thật v&agrave; thực sự ấn tượng.</p>\r\n<p><img id=\"\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/BaoPK/HDCameraZenfoneLaser/mo-ta-san-pham-airpods-3-6.JPG\" alt=\"&acirc;m thanh tai nghe AirPods 3\" /></p>\r\n<h3><strong>Adapter EQ cho chất &acirc;m thực sự đ&atilde; tai</strong></h3>\r\n<p>Mỗi tiết tấu thanh &acirc;m m&agrave; AirPods 3 truyền tải đều trở n&ecirc;n trau chuốt hơn nhờ Adapter EQ. Cốt l&otilde;i của c&ocirc;ng nghệ n&agrave;y nằm ở micro si&ecirc;u nhỏ được thiết kế hướng v&agrave;o khoang tai nhằm tiến h&agrave;nh đo đạc tần số &acirc;m thanh ch&iacute;nh x&aacute;c. Từ đ&oacute;, AirPods 3 sẽ t&iacute;nh to&aacute;n v&agrave; tự tiến h&agrave;nh tinh chỉnh chất &acirc;m sao cho ph&ugrave; hợp với ống tai v&agrave; thể loại nhạc m&agrave; bạn y&ecirc;u th&iacute;ch.</p>\r\n<p>Đ&acirc;y l&agrave; b&iacute; quyết gi&uacute;p sản phẩm thể hiện &acirc;m thanh sắc sảo đến từng chi tiết, gi&uacute;p bạn đắm ch&igrave;m v&agrave;o từng giai điệu với&nbsp;<a href=\"https://fptshop.com.vn/tin-tuc/thu-thuat/am-bass-tren-tai-nghe-co-y-nghia-gi-132301\">&acirc;m bass</a>&nbsp;chắc khỏe v&agrave; &acirc;m treble trong trẻo đ&atilde; tai.</p>\r\n<p><img id=\"\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/BaoPK/HDCameraZenfoneLaser/mo-ta-san-pham-airpods-3-7.jpg\" alt=\"chất &acirc;m tai nghe AirPods 3\" /></p>\r\n<h3><strong>Thiết kế giảm thiểu tiếng ồn của gi&oacute;</strong></h3>\r\n<p>AirPods 3 được t&iacute;ch hợp th&ecirc;m một lớp lưới lọc &acirc;m thanh đặc biệt tr&ecirc;n housing. Tại đ&oacute;, một micro được trang bị b&ecirc;n trong nhằm thu r&otilde; tiếng n&oacute;i của người d&ugrave;ng. Nhờ vậy, &acirc;m thanh khi bạn gọi điện sẽ được truyền tải r&otilde; r&agrave;ng tới đầu d&acirc;y b&ecirc;n kia, đảm bảo chất lượng đ&agrave;m thoại qua AirPods 3 lu&ocirc;n ổn định, tr&aacute;nh bị ảnh hưởng bởi những t&aacute;c nh&acirc;n ngo&agrave;i m&ocirc;i trường.</p>\r\n<p><img id=\"\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/BaoPK/HDCameraZenfoneLaser/mo-ta-san-pham-airpods-3-9.jpg\" alt=\"Thiết kế giảm thiểu tiếng ồn của AirPods 3\" /></p>\r\n<h3><strong>Linh hoạt hơn nhờ cảm ứng lực Force Touch</strong></h3>\r\n<p>Th&ocirc;ng qua cảm ứng lực Force Touch tr&ecirc;n th&acirc;n tai nghe, chủ nh&acirc;n của AirPods 3 dễ d&agrave;ng điều khiển thiết bị chỉ bằng thao t&aacute;c chạm nhẹ đơn giản. Cụ thể, bạn chỉ cần g&otilde; một lần để ph&aacute;t/dừng b&agrave;i h&aacute;t hoặc nhận cuộc gọi, g&otilde; hai lần nếu muốn chuyển b&agrave;i, g&otilde; ba lần để quay lại b&agrave;i h&aacute;t vừa ph&aacute;t, v&agrave; nhấn &ndash; giữ nếu muốn k&iacute;ch hoạt trợ l&yacute; ảo Siri.</p>\r\n<p><img id=\"\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/BaoPK/HDCameraZenfoneLaser/mo-ta-san-pham-airpods-3-a.JPG\" alt=\"cảm ứng tai nghe AirPods 3\" /></p>\r\n<h3><strong>Thời lượng pin xuất sắc, sạc kh&ocirc;ng d&acirc;y tiện dụng</strong></h3>\r\n<p>Hộp sạc đi k&egrave;m của AirPods 3 được thiết kế để hoạt động tương th&iacute;ch với c&aacute;c&nbsp;<a href=\"https://fptshop.com.vn/phu-kien/sac-cap-de-sac-khong-day\">đế sạc kh&ocirc;ng d&acirc;y</a>&nbsp;chuẩn Qi tr&ecirc;n thị trường, đồng thời vận h&agrave;nh tối ưu khi được đặt l&ecirc;n c&aacute;c bộ sạc MagSafe do Apple sản xuất.</p>\r\n<p>Sau khi được sạc đủ, hộp sạc AirPods 3 c&oacute; thể t&iacute;ch trữ năng lượng đủ cho AirPods 3 vận h&agrave;nh trong 30 giờ. Khi tai nghe cạn pin, bạn chỉ cần đặt thiết bị trong hộp 5 ph&uacute;t l&agrave; sẽ c&oacute; đủ pin trải nghiệm trong 1 giờ li&ecirc;n tục sau đ&oacute;. Ngo&agrave;i ra, mỗi b&ecirc;n tai nghe AirPods 3 cũng cho thời lượng sử dụng tối đa l&agrave; 6 tiếng.</p>\r\n<p><img id=\"\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/BaoPK/HDCameraZenfoneLaser/mo-ta-san-pham-airpods-3-5.JPG\" alt=\"Thời lượng pin tai nghe AirPods 3\" /></p>\r\n<h3><strong>Kết nối ho&agrave;n hảo với iPhone, iPad v&agrave; m&aacute;y Mac</strong></h3>\r\n<p>L&agrave; một&nbsp;<a title=\"Phụ kiện ch&iacute;nh h&atilde;ng\" type=\"Phụ kiện ch&iacute;nh h&atilde;ng\" href=\"https://fptshop.com.vn/phu-kien\">phụ kiện</a>&nbsp;nằm trong&nbsp;<a href=\"https://fptshop.com.vn/apple\">hệ sinh th&aacute;i Apple</a>, AirPods 3 đạt độ tương th&iacute;ch ho&agrave;n hảo với c&aacute;c thiết bị c&ocirc;ng nghệ do nh&agrave; T&aacute;o sản xuất. Bạn chỉ cần đặt AirPods gần&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai/apple-iphone\">iPhone</a>,&nbsp;<a href=\"https://fptshop.com.vn/may-tinh-bang/apple-ipad\">iPad</a>&nbsp;hoặc m&aacute;y&nbsp;<a href=\"https://fptshop.com.vn/may-tinh-xach-tay/apple-macbook\">Macbook</a>&nbsp;v&agrave; ấn x&aacute;c nhận gh&eacute;p đ&ocirc;i l&agrave; dễ d&agrave;ng kết nối tai nghe c&ugrave;ng mọi thiết bị gắn với t&agrave;i khoản iCloud của bạn.</p>\r\n<p>Trong trường hợp bạn đang kết nối AirPods 3 với m&aacute;y Mac nhưng lại c&oacute; cuộc gọi đến tr&ecirc;n iPhone, chiếc tai nghe sẽ tự chuyển kết nối sang điện thoại để bạn đ&agrave;m thoại m&agrave; kh&ocirc;ng cần phải thực hiện bất cứ thao t&aacute;c rườm r&agrave; n&agrave;o.</p>\r\n<p><img id=\"\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/BaoPK/HDCameraZenfoneLaser/mo-ta-san-pham-airpods-3-c.JPG\" alt=\"Kết nối tai nghe AirPods 3\" /></p>\r\n<h3><strong>Cảm biến dừng/ph&aacute;t nhạc th&ocirc;ng minh</strong></h3>\r\n<p>Với khả năng nhận biết bạn c&oacute; đang đeo hay kh&ocirc;ng đeo thiết bị, AirPods 3 sẽ tự động dừng/ph&aacute;t nhạc nhằm tiết kiệm pin v&agrave; tr&aacute;nh để &acirc;m thanh ph&aacute;t ra l&agrave;m ảnh hưởng đến những người xung quanh. Bạn sẽ thực sự h&agrave;i l&ograve;ng khi tận hưởng sự tiện lợi trong cơ chế vận h&agrave;nh của AirPods 3.</p>\r\n<p><img id=\"\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/BaoPK/HDCameraZenfoneLaser/mo-ta-san-pham-airpods-3-8.jpg\" alt=\"Cảm biến tai nghe AirPods 3\" /></p>\r\n<h3><strong>T&igrave;m kiếm dễ d&agrave;ng hơn với ứng dụng Find My</strong></h3>\r\n<p>Trong trường hợp bạn thất lạc chiếc AirPods 3 của m&igrave;nh v&agrave; kh&ocirc;ng nhớ đ&atilde; để qu&ecirc;n ở đ&acirc;u, bạn h&atilde;y bật ứng dụng Find My tr&ecirc;n điện thoại. Một giao diện định vị sẽ qu&eacute;t phạm vi l&acirc;n cận để x&aacute;c định chiếc tai nghe c&oacute; ở gần bạn hay kh&ocirc;ng.</p>\r\n<p>Bạn c&oacute; thể th&ocirc;ng qua Find My để ra lệnh cho AirPods 3 ph&aacute;t &acirc;m thanh cảnh b&aacute;o nếu vẫn kh&ocirc;ng t&igrave;m thấy thiết bị. Trường hợp AirPods 3 nằm ngo&agrave;i v&ugrave;ng phủ s&oacute;ng của Bluetooth, một cảnh b&aacute;o t&aacute;ch biệt sẽ được gửi đến tr&ecirc;n m&agrave;n h&igrave;nh iPhone của bạn.</p>\r\n<p><img id=\"\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/BaoPK/HDCameraZenfoneLaser/mo-ta-san-pham-airpods-3-d.JPG\" alt=\"T&igrave;m kiếm tai nghe AirPods 3\" /></p>\r\n</div>\r\n</div>', 1, '4469000', '16dffc4fb9.jpg', 1),
(16, 'Oppo Reno6 5G', 1, 3, '<p><strong>Sẵn s&agrave;ng bật tung cảm x&uacute;c c&ugrave;ng&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai/oppo-reno6\">OPPO Reno6 5G</a>, chiếc&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai\">điện thoại</a>&nbsp;sở hữu thiết kế đẳng cấp si&ecirc;u mỏng nhẹ, camera chụp ch&acirc;n dung Bokeh Flare độc đ&aacute;o v&agrave; tr&ecirc;n hết l&agrave; kết nối mạng 5G si&ecirc;u tốc, c&ugrave;ng bạn kết nối tương lai.</strong></p>\r\n<p><strong><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/oppo-reno-6-5g-1.JPG\" alt=\"OPPO Reno6 5G\" /></strong></p>\r\n<h3><strong>Quay video x&oacute;a ph&ocirc;ng cực đỉnh</strong></h3>\r\n<p>OPPO Reno6 5G n&acirc;ng tầm khả năng quay video l&ecirc;n một tầm cao mới bằng những t&iacute;nh năng đầy nghệ thuật. Bạn c&oacute; thể quay ch&acirc;n dung Bokeh Flare, l&agrave;m mờ &aacute;nh đ&egrave;n ph&ocirc;ng nền một c&aacute;ch lung linh huyền ảo, tạo hiệu ứng điện ảnh.&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai/ai-camera\">Tr&iacute; tuệ nh&acirc;n tạo AI tr&ecirc;n camera</a>&nbsp;sẽ kết hợp t&iacute;nh năng Live HDR v&agrave; Si&ecirc;u video quay đ&ecirc;m để bạn lưu lại những khoảnh khắc ấn tượng l&uacute;c b&igrave;nh minh hay ho&agrave;ng h&ocirc;n. Ngo&agrave;i ra bạn c&ograve;n c&oacute; thể quay video l&agrave;m đẹp ch&acirc;n dung t&ocirc;n vinh vẻ đẹp thần th&aacute;i; chế độ ch&acirc;n dung m&agrave;u AI nổi bật; quay video kh&oacute;a n&eacute;t chủ thể để h&igrave;nh ảnh lu&ocirc;n sắc n&eacute;t d&ugrave; c&oacute; chuyển động nhanh trong khung h&igrave;nh.</p>\r\n<p><iframe src=\"https://www.youtube.com/embed/xszLPD6fkEs\" frameborder=\"0\" width=\"640\" height=\"360\"></iframe></p>\r\n<h3><strong>Thế giới m&agrave;u sắc sống động</strong></h3>\r\n<p>Camera ch&iacute;nh 64MP của OPPO Reno6 5G được hỗ trợ bởi cảm biến nhiệt độ m&agrave;u, t&aacute;i tạo m&agrave;u sắc ho&agrave;n hảo. Cảm biến nhiệt độ m&agrave;u sẽ ghi lại c&acirc;n bằng trắng thời điểm bạn chụp h&igrave;nh, tăng tốc độ m&agrave;n trập, đồng thời điều chỉnh m&agrave;u để m&agrave;u sắc ch&iacute;nh x&aacute;c v&agrave; sống động như thật. Tốc độ chụp ảnh si&ecirc;u nhanh gi&uacute;p bạn kh&ocirc;ng bỏ lỡ bất cứ khoảnh khắc đ&aacute;ng nhớ n&agrave;o. Ngo&agrave;i ra OPPO Reno6 5G c&ograve;n c&oacute; chế độ ảnh si&ecirc;u n&eacute;t 108MP, ghi lại h&igrave;nh ảnh sắc n&eacute;t hơn bao giờ hết, để bạn d&ugrave; c&oacute; ph&oacute;ng to cũng kh&ocirc;ng bị vỡ h&igrave;nh, bức h&igrave;nh vẫn rất gi&agrave;u chi tiết.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/oppo-reno-6-5g-2.JPG\" alt=\"camera OPPO Reno6 5G\" /></p>\r\n<h3><strong>Thiết kế si&ecirc;u mỏng nhẹ, đơn giản nhưng đậm chất tinh tế</strong></h3>\r\n<p>OPPO Reno6 5G c&oacute; thiết kế si&ecirc;u mỏng nhẹ với độ mỏng chỉ 7,59mm. Điện thoại được ho&agrave;n thiện theo ng&ocirc;n ngữ thiết kế phẳng, vu&ocirc;ng vắn, đơn giản nhưng kh&ocirc;ng bao giờ lỗi thời, vừa mang đến d&aacute;ng vẻ sang trọng, lại vừa cho cảm gi&aacute;c cầm nắm thoải m&aacute;i. M&aacute;y được phủ lớp sơn OPPO Reno Glow độc đ&aacute;o, đẹp lấp l&aacute;nh nhưng kh&ocirc;ng hề qu&aacute; ph&ocirc; trương v&agrave; đặc biệt kh&ocirc;ng b&aacute;m bẩn hay dấu v&acirc;n tay. C&ocirc;ng nghệ n&agrave;y đ&atilde; được c&ocirc;ng nhận bởi hơn 20 bằng s&aacute;ng chế, khiến cho OPPO Reno6 5G đẹp như một t&aacute;c phẩm nghệ thuật.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/oppo-reno-6-5g-3.JPG\" alt=\"thiết kế OPPO Reno6 5G\" /></p>\r\n<h3><strong>Đến với kỷ nguy&ecirc;n của tốc độ</strong></h3>\r\n<p>Trong nh&aacute;y mắt, cả thế giới th&ocirc;ng tin đều nằm trong tay bạn với kết nối 5G đỉnh cao tr&ecirc;n OPPO Reno6 5G. Điện thoại trang bị bộ vi xử l&yacute;&nbsp;<a href=\"https://fptshop.com.vn/tin-tuc/danh-gia/cac-tinh-nang-dang-chu-y-nhat-tren-chip-dimensity-900-moi-135067\">Dimensity 900 5G</a>&nbsp;được sản xuất tr&ecirc;n quy tr&igrave;nh 6nm ti&ecirc;n tiến vượt bậc, kh&ocirc;ng chỉ cho kết nối di động nhanh chưa từng thấy m&agrave; c&ograve;n c&oacute; hiệu năng mạnh mẽ v&agrave; khả năng tiết kiệm năng lượng tối ưu.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/oppo-reno-6-5g-4.JPG\" alt=\"cấu h&igrave;nh OPPO Reno6 5G\" /></p>\r\n<h3><strong>Thoải m&aacute;i đa nhiệm v&agrave; lưu trữ</strong></h3>\r\n<p>OPPO Reno6 5G c&oacute; sẵn bộ nhớ cực khủng với 8GB RAM v&agrave; 128GB bộ nhớ trong, cho bạn thoải m&aacute;i mở c&ugrave;ng l&uacute;c nhiều ứng dụng, c&agrave;i đặt hay lưu trữ bất cứ thứ g&igrave; bạn th&iacute;ch. Hơn thế nữa, m&aacute;y c&ograve;n c&oacute; t&iacute;nh năng bộ nhớ mở rộng, chuyển đổi th&ecirc;m 5GB RAM từ ROM sang RAM để bạn c&oacute; được dung lượng RAM khổng lồ 13GB, mang tới trải nghiệm mượt m&agrave;.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/oppo-reno-6-5g-5.JPG\" alt=\"bộ nhớ OPPO Reno6 5G\" /></p>\r\n<h3><strong>C&ocirc;ng nghệ sạc si&ecirc;u nhanh 65W</strong></h3>\r\n<p>SuperVOOC 2.0 l&agrave; c&ocirc;ng nghệ sạc si&ecirc;u nhanh đ&atilde; th&agrave;nh thương hiệu của&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai/oppo\">OPPO</a>. Giờ đ&acirc;y&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai/sac-nhanh\">điện thoại sạc nhanh</a>&nbsp;l&ecirc;n đến&nbsp;65W tr&ecirc;n OPPO Reno6 5G, bạn chỉ mất vỏn vẹn 28 ph&uacute;t để sạc đầy ho&agrave;n to&agrave;n 100% pin, hoặc 5 ph&uacute;t để sạc được 25% pin. Bạn sẽ kh&ocirc;ng cần phải chờ đợi v&agrave; tự tin sử dụng điện thoại suốt cả ng&agrave;y, lu&ocirc;n ngập tr&agrave;n năng lượng.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/oppo-reno-6-5g-6.JPG\" alt=\"sạc nhanh OPPO Reno6 5G\" /></p>\r\n<h3><strong>Chơi game chuy&ecirc;n nghiệp</strong></h3>\r\n<p>OPPO Reno6 5G l&agrave; chiếc điện thoại l&yacute; tưởng cho chơi game khi b&ecirc;n cạnh hiệu năng h&agrave;ng đầu, m&aacute;y c&ograve;n sở hữu loạt c&ocirc;ng nghệ m&agrave; c&aacute;c game thủ sẽ rất hứng th&uacute;. Hệ thống l&agrave;m m&aacute;t đa năng với tấm tản nhiệt graphite 9200 diện t&iacute;ch rộng sẽ lu&ocirc;n giữ cho nhiệt độ CPU ổn định, đảm bảo hiệu năng cao d&ugrave; bạn chơi game trong thời gian d&agrave;i; c&ocirc;ng nghệ Z-axis Linear Motor mang tới những chuyển động rung ch&acirc;n thực; &acirc;m thanh Dolby Atmos đầy sống động;&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai/tren-60-inch\">điện thoại m&agrave;n h&igrave;nh lớn</a>&nbsp;với k&iacute;ch thước&nbsp;6,43 inch AMOLED tần số qu&eacute;t cao 90Hz cho những thao t&aacute;c vuốt chạm của bạn mượt m&agrave; đầy hứng khởi.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/oppo-reno-6-5g-7.JPG\" alt=\"chơi game OPPO Reno6 5G\" /></p>', 0, '10990000', '0acb780ec6.jpg', 1),
(17, 'OPPO Find X3 Pro 5G', 1, 3, '<p><strong>Sẵn s&agrave;ng c&ugrave;ng bạn hướng tới tương lai với&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai/oppo-find-x3-pro\">OPPO Find X3 Pro 5G</a>, chiếc&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai\">điện thoại</a>&nbsp;sở hữu camera m&agrave;u sắc trung thực, m&agrave;n h&igrave;nh 120Hz si&ecirc;u mượt, 1 tỷ m&agrave;u sống động v&agrave; hiệu năng đột ph&aacute; với sức mạnh của con chip Qualcomm Snapdragon 888 hỗ trợ 5G.</strong></p>\r\n<p><strong><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/oppo-find-x3-pro-12.jpg\" alt=\"OPPO Find X3 Pro 5G\" /></strong></p>\r\n<h3><strong>M&agrave;n h&igrave;nh 1 tỷ m&agrave;u, độ s&acirc;u m&agrave;u 10-bit</strong></h3>\r\n<p>Kh&aacute;m ph&aacute; m&agrave;n h&igrave;nh smartphone sở hữu m&agrave;u sắc phong ph&uacute; v&agrave; trung thực bậc nhất từ trước đến nay tr&ecirc;n OPPO Find X3 Pro 5G. Bạn sẽ được tận hưởng mọi h&igrave;nh ảnh ở 1 tỷ m&agrave;u sắc, độ s&acirc;u m&agrave;u 10-bit, độ bao phủ m&agrave;u 100% P3 cho m&agrave;u sắc ch&iacute;nh x&aacute;c đ&aacute;ng kinh ngạc. C&ocirc;ng nghệ độ s&acirc;u m&agrave;u 10-bit chỉ d&agrave;nh cho những m&agrave;n h&igrave;nh chuy&ecirc;n dụng v&agrave; cao cấp đảm bảo độ chuyển m&agrave;u ho&agrave;n hảo, từ t&ocirc;ng m&agrave;u da đến m&agrave;u bầu trời. Cả thế giới ngo&agrave;i đời thực được t&aacute;i hiện ngập tr&agrave;n m&agrave;u sắc ngay tr&ecirc;n tay bạn. Thậm ch&iacute; với chuẩn HDR 10+, những nội dung khi bạn xem ở c&aacute;c ứng dụng hỗ trợ như Youtube, Netflix sẽ c&ograve;n hấp dẫn v&agrave; sống động hơn.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/oppo-find-x3-pro-2.jpg\" alt=\"m&agrave;n h&igrave;nh OPPO Find X3 Pro 5G\" /></p>\r\n<h3><strong>Camera k&eacute;p 50MP, đ&aacute;nh thức sắc m&agrave;u</strong></h3>\r\n<p>Kh&ocirc;ng chỉ thưởng thức, bạn c&ograve;n c&oacute; thể tự ghi lại thế giới m&agrave;u sắc trung thực nhờ bộ đ&ocirc;i camera 50MP tr&ecirc;n OPPO Find X3 Pro 5G. Sử dụng cảm biến Sony IMX 766 cao cấp, hỗ trợ chống rung quang học OIS, camera g&oacute;c rộng v&agrave; g&oacute;c si&ecirc;u rộng của Find X3 Pro c&oacute; khả năng chụp ảnh 1 tỷ m&agrave;u, độ s&acirc;u m&agrave;u 10-bit chất lượng tuyệt hảo. B&ecirc;n cạnh đ&oacute;, t&iacute;nh năng nhận diện cảnh n&acirc;ng cao AI sẽ điều chỉnh t&ocirc;ng m&agrave;u ph&ugrave; hợp dựa theo ngoại cảnh, để bạn c&oacute; thể cảm thấy m&igrave;nh như đang ở một nơi n&agrave;o đ&oacute; mỗi khi xem lại những kỷ niệm tr&ecirc;n OPPO Find X3 Pro 5G.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/oppo-find-x3-pro-3.jpg\" alt=\"camera OPPO Find X3 Pro 5G\" /></p>\r\n<h3><strong>Sức mạnh của 4 camera</strong></h3>\r\n<p>4 camera với 4 vai tr&ograve; ri&ecirc;ng biệt của OPPO Find X3 Pro 5G mang đến cho bạn đầy đủ t&iacute;nh năng chụp h&igrave;nh thời thượng nhất hiện nay. Camera g&oacute;c si&ecirc;u rộng sẽ mở rộng khung h&igrave;nh tới 110.3 độ, cho bức ảnh phong cảnh trọn vẹn. Trong khi đ&oacute; camera micro mang tới g&oacute;c nh&igrave;n cận cảnh với khả năng lấy n&eacute;t gần hơn tới 60 lần, chụp ảnh c&aacute;c vật thể cực nhỏ m&agrave; mắt thường kh&oacute; l&ograve;ng nh&igrave;n r&otilde;. Cuối c&ugrave;ng l&agrave; camera Tele 13MP hỗ trợ zoom quang 5x v&agrave; zoom kỹ thuật số 20x, đưa mọi khung cảnh từ xa đến gần ngay trước mắt.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/oppo-find-x3-pro-4.jpg\" alt=\"4 camera OPPO Find X3 Pro 5G\" /></p>\r\n<h3><strong>L&agrave;m phim chuy&ecirc;n nghiệp</strong></h3>\r\n<p>Hệ thống camera đỉnh cao v&agrave; chế độ điện ảnh chuy&ecirc;n nghiệp gi&uacute;p OPPO Find X3 Pro 5G c&oacute; thể quay phim chất lượng xuất sắc với khả năng t&aacute;i tạo m&agrave;u tốt nhất từ trước đến nay. T&iacute;nh năng quay video AI l&agrave; tập hợp của c&aacute;c c&ocirc;ng nghệ ti&ecirc;n tiến bao gồm chống rung, chế độ ban đ&ecirc;m, ghi &acirc;m từ xa v&agrave; độ s&acirc;u m&agrave;u 8-bit. Kể cả những cảnh phức tạp như đốt ph&aacute;o hoa trong đ&ecirc;m tối cũng kh&ocirc;ng thể l&agrave;m kh&oacute; được camera Find X3 Pro.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/oppo-find-x3-pro-5.jpg\" alt=\"quay phim OPPO Find X3 Pro 5G\" /></p>\r\n<h3><strong>Lưu giữ kỷ niệm tr&ecirc;n camera selfie 32MP</strong></h3>\r\n<p>Camera selfie lu&ocirc;n l&agrave; thế mạnh của&nbsp;<a title=\"điện thoại oppo gi&aacute; rẻ\" type=\"điện thoại oppo gi&aacute; rẻ\" href=\"https://fptshop.com.vn/dien-thoai/oppo\">điện thoại OPPO</a>. Với Find X3 Pro, camera trước độ ph&acirc;n giải l&ecirc;n tới 32MP c&oacute; khả năng hoạt động tương đương camera ch&iacute;nh, hỗ trợ chế độ chụp đ&ecirc;m, để bạn c&oacute; thể dễ d&agrave;ng lưu giữ lại những kỷ niệm đ&aacute;ng nhớ bằng c&aacute;c bức ảnh &ldquo;tự sướng&rdquo; c&ugrave;ng bạn b&egrave;.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/oppo-find-x3-pro-6.jpg\" alt=\"selfie OPPO Find X3 Pro 5G\" /></p>\r\n<h3><strong>Thiết kế đến từ tương lai</strong></h3>\r\n<p>Lấy cảm hứng từ những con t&agrave;u vũ trụ trong kh&ocirc;ng gian v&agrave; được chế t&aacute;c từ những kỹ thuật ti&ecirc;n tiến, Find X3 Pro sở hữu một vẻ đẹp hiện đại v&agrave; tương lai. Module m&aacute;y ảnh được l&agrave;m thanh lịch với những đường cong mềm mại, ho&agrave;n thiện b&oacute;ng bẩy v&agrave; cao cấp, OPPO Find X3 Pro 5G ch&iacute;nh l&agrave; sự phong c&aacute;ch của ri&ecirc;ng bạn. Tất nhi&ecirc;n bền vững l&agrave; điều kh&ocirc;ng thể thiếu ở một chiếc điện thoại h&agrave;ng đầu,&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai/chong-nuoc-bui\">điện thoại chuẩn chống nước</a>&nbsp;IP68 sẽ gi&uacute;p Find X3 Pro lu&ocirc;n bền bỉ theo thời gian.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/oppo-find-x3-pro-7.jpg\" alt=\"thiết kế OPPO Find X3 Pro 5G\" /></p>\r\n<h3><strong>Cảm nhận sự mượt m&agrave; đến kh&oacute; tin</strong></h3>\r\n<p>OPPO Find X3 Pro 5G ch&iacute;nh l&agrave; chiếc điện thoại mượt m&agrave; bậc nhất m&agrave; bạn được cầm tr&ecirc;n tay. Tốc độ l&agrave;m mới m&agrave;n h&igrave;nh sẽ chuyển đổi liền mạch từ 5Hz-120Hz, vừa đảm bảo độ mượt đến kh&oacute; tin lại vừa tiết kiệm pin th&ocirc;ng minh. Từ những thao t&aacute;c vuốt chạm th&ocirc;ng thường cho đến c&aacute;c hoạt động phức tạp như chơi game nặng, Find X3 Pro đều phản hồi ngay lập tức.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/oppo-find-x3-pro-8.jpg\" alt=\"mượt m&agrave; OPPO Find X3 Pro 5G\" /></p>\r\n<h3><strong>Cuộc sống trở n&ecirc;n dễ d&agrave;ng hơn</strong></h3>\r\n<p>Sức mạnh đ&aacute;ng kinh ngạc của bộ vi xử l&yacute; Snapdragon 888 tr&ecirc;n OPPO Find X3 Pro 5G gi&uacute;p cuộc sống của bạn trở n&ecirc;n dễ d&agrave;ng hơn. Trải nghiệm nhanh ch&oacute;ng, kh&ocirc;ng phải mất thời gian chờ đợi sẽ khiến bạn lu&ocirc;n h&agrave;i l&ograve;ng. Đặc biệt, con chip SD 888 cũng hỗ trợ kết nối 5G, cho tốc độ mạng si&ecirc;u nhanh ở bất cứ đ&acirc;u, sẵn s&agrave;ng cho khả năng kết nối của bạn.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/oppo-find-x3-pro-9.jpg\" alt=\"cấu h&igrave;nh OPPO Find X3 Pro 5G\" /></p>\r\n<h3><strong>Sạc nhanh 65W SuperVOOC</strong></h3>\r\n<p>OPPO Find X3 Pro 5G l&agrave; chiếc điện thoại d&agrave;nh cho cuộc sống năng động, v&igrave; thế m&aacute;y lu&ocirc;n tr&agrave;n đầy năng lượng.&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai/sac-nhanh\">Điện thoại&nbsp;sạc nhanh</a>&nbsp;65W SuperVOOC c&oacute; thể sạc điện thoại tới 40% pin chỉ trong v&ograve;ng 10 ph&uacute;t. Đ&acirc;y cũng l&agrave; c&ocirc;ng nghệ hết sức an to&agrave;n khi đảm bảo tuổi thọ linh kiện v&agrave; kh&ocirc;ng khiến m&aacute;y bị qu&aacute; n&oacute;ng.</p>', 1, '18990000', 'aa0db7e46f.jpg', 1);
INSERT INTO `tbl_product` (`productId`, `productName`, `catId`, `brandId`, `product_description`, `type`, `price`, `image`, `status`) VALUES
(18, 'Realme 8 Pro', 1, 11, '<p><strong><a href=\"https://fptshop.com.vn/dien-thoai/realme-8-pro\">Realme 8 Pro</a>&nbsp;với camera độ ph&acirc;n giải l&ecirc;n tới 108MP, mang đến cho bạn những h&igrave;nh ảnh v&agrave; thước phim ch&acirc;n thực nhất. B&ecirc;n cạnh đ&oacute; cấu h&igrave;nh ấn tượng nhờ bộ vi xử l&yacute; Snapdragon 720G sẽ tạo n&ecirc;n dấu ấn sức mạnh đỉnh cao trong một thiết kế mỏng nhẹ.</strong></p>\r\n<p><strong><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/realme-8-pro-1.jpg\" alt=\"Realme 8 Pro\" /></strong></p>\r\n<h3><strong>Vẻ đẹp từ sự huyền b&iacute;</strong></h3>\r\n<p>Realme 8 Pro lấy cảm hứng từ vũ trụ huyền b&iacute;, phi&ecirc;n bản m&agrave;u đen m&ocirc; phỏng bầu trời đ&ecirc;m huyền ảo với những v&igrave; tinh t&uacute; lấp l&aacute;nh. Khẩu hiệu d&aacute;m bứt ph&aacute; &ldquo;Dare to leap&rdquo; được in lớn ph&aacute; c&aacute;ch tr&ecirc;n mặt lưng, thể hiện c&aacute; t&iacute;nh nổi bật. D&ugrave; c&oacute; vẻ ngo&agrave;i đơn giản nhưng tr&ecirc;n thực tế realme 8 Pro được ho&agrave;n thiện từ quy tr&igrave;nh chống l&oacute;a pha l&ecirc; ho&agrave;n to&agrave;n mới, tạo n&ecirc;n thiết kế cuốn h&uacute;t, đầy chiều s&acirc;u. Độ mỏng nhẹ ấn tượng v&agrave; cảm gi&aacute;c mềm mại khi chạm v&agrave;o gi&uacute;p&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai\">điện thoại</a>&nbsp;ho&agrave;n hảo trong l&ograve;ng b&agrave;n tay.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/realme-8-pro-2.jpg\" alt=\"thiết kế Realme 8 Pro\" /></p>\r\n<h3><strong>Vượt qua giới hạn với camera 108MP</strong></h3>\r\n<p>realme 8 Pro sở hữu 4 camera sau, bao gồm đầy đủ camera ch&iacute;nh, camera g&oacute;c si&ecirc;u rộng, camera g&oacute;c si&ecirc;u cận v&agrave; camera ch&acirc;n dung Trắng &ndash; Đen. Trong đ&oacute; nổi bật nhất chắc chắn l&agrave; camera ch&iacute;nh 108MP t&iacute;ch hợp cảm biến ISOCELL HM2 cao cấp từ Samsung. C&ocirc;ng nghệ gh&eacute;p 9 điểm ảnh l&agrave;m 1 gi&uacute;p điểm ảnh m&ocirc; phỏng lớn tới 2,1um, tăng cường độ s&aacute;ng tối đa. D&ugrave; trong bất kỳ điều kiện &aacute;nh s&aacute;ng n&agrave;o, bạn cũng c&oacute; thể dễ d&agrave;ng chụp được những bức ảnh r&otilde; n&eacute;t, m&agrave;u sắc sống động v&agrave; ch&acirc;n thực đến từng chi tiết.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/realme-8-pro-3.jpg\" alt=\"camera Realme 8 Pro\" /></p>\r\n<h3><strong>Zoom quang học 3X</strong></h3>\r\n<p>Đưa những cảnh vật từ xa lại gần ngay trước mắt m&agrave; kh&ocirc;ng cần phải di chuyển khi realme 8 Pro c&oacute; t&iacute;nh năng zoom quang học 3X. Chỉ cần một c&uacute; chạm, h&igrave;nh ảnh sẽ được ph&oacute;ng to gấp 3 lần nhưng chất lượng kh&ocirc;ng hề bị giảm m&agrave; vẫn v&ocirc; c&ugrave;ng sắc n&eacute;t.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/realme-8-pro-4.jpg\" alt=\"zoom 3x Realme 8 Pro\" /></p>\r\n<h3><strong>Loạt t&iacute;nh năng đầy s&aacute;ng tạo</strong></h3>\r\n<p>Ngo&agrave;i phần cứng camera xuất sắc, realme 8 Pro c&ograve;n c&ugrave;ng bạn kh&aacute;m ph&aacute; thế giới với loạt t&iacute;nh năng đầy s&aacute;ng tạo ở cả chụp ảnh lẫn quay video. Bạn sẽ c&oacute; chế độ si&ecirc;u chụp đ&ecirc;m mới; chế độ nghi&ecirc;ng &ndash; dịch chuyển; chế độ ch&acirc;n dung Neon; chế độ chụp v&agrave; quay video thi&ecirc;n văn; chế độ quay video dịch chuyển Time-lapse; chế độ quay video hiển thị k&eacute;p. Tự bản th&acirc;n bạn cũng c&oacute; thể tạo ra những h&igrave;nh ảnh v&agrave; đoạn phim chuy&ecirc;n nghiệp rất độc đ&aacute;o.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/realme-8-pro-5.jpg\" alt=\"camera Realme 8 Pro\" /></p>\r\n<h3><strong>Tốc độ sạc nhanh như chớp</strong></h3>\r\n<p>Kh&ocirc;ng để bạn phải chờ đợi,&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai/sac-nhanh\">điện thoại hỗ trợ c&ocirc;ng nghệ sạc si&ecirc;u nhanh</a>&nbsp;SuperDart 50W, c&oacute; thể sạc được 50% pin chỉ sau 17 ph&uacute;t. C&ocirc;ng nghệ sạc n&agrave;y cũng rất an to&agrave;n với 5 lớp bảo vệ v&agrave; chip th&ocirc;ng minh, đảm bảo độ bền của linh kiện. L&agrave; chiếc&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai/pin-khung\">điện thoại pin tr&acirc;u</a>&nbsp;với vi&ecirc;n pin dung lượng cao 4500mAh của realme 8 Pro cho ph&eacute;p bạn thoải m&aacute;i sử dụng cả ng&agrave;y. Nếu lỡ hết pin, sạc si&ecirc;u nhanh sẽ gi&uacute;p bạn kh&ocirc;ng cần phải lo lắng.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/realme-8-pro-6.jpg\" alt=\"sạc nhanh Realme 8 Pro\" /></p>\r\n<h3><strong>Sức mạnh hiệu năng vượt trội</strong></h3>\r\n<p>Cấu h&igrave;nh của realme 8 Pro rất ấn tượng với bộ n&atilde;o l&agrave; con chip Snapdragon 720G. Đ&acirc;y l&agrave; con chip sản xuất tr&ecirc;n tiến tr&igrave;nh 8nm ti&ecirc;n tiến, c&oacute; 8 nh&acirc;n v&agrave; xung nhịp tối đa l&ecirc;n tới 2.3GHz. Snapdragon 720G c&ograve;n t&iacute;ch hợp GPU đồ họa Adreno 618 v&agrave; động cơ AI thế hệ thứ 5. Ngo&agrave;i ra điện thoại c&ograve;n sở hữu tới 8GB RAM c&ugrave;ng 128GB bộ nhớ trong. Cấu h&igrave;nh cực mạnh gi&uacute;p realme 8 Pro kh&ocirc;ng chỉ lu&ocirc;n lu&ocirc;n chạy si&ecirc;u mượt m&agrave; c&ograve;n c&oacute; thể chơi tốt c&aacute;c tựa game đồ họa cao.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/realme-8-pro-7.jpg\" alt=\"cấu h&igrave;nh Realme 8 Pro\" /></p>\r\n<h3><strong>H&igrave;nh ảnh đẹp mỹ m&atilde;n</strong></h3>\r\n<p>Mọi thứ sẽ đều trở n&ecirc;n đẹp hơn tr&ecirc;n m&agrave;n h&igrave;nh của realme 8 Pro. Trước mắt bạn l&agrave; chiếc&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai/tren-60-inch\">điện thoại m&agrave;n h&igrave;nh lớn</a>&nbsp;với&nbsp;m&agrave;n h&igrave;nh lớn 6,4 inch, tấm nền Super AMOLED cao cấp, độ s&aacute;ng tối đa l&ecirc;n tới 1000 nits, hiển thị m&agrave;u sắc sống động, ch&acirc;n thực. M&agrave;n h&igrave;nh chiếm tới 90,8% diện t&iacute;ch hiển thị mặt trước c&ugrave;ng camera selfie t&iacute;ch hợp tinh tế trong tấm k&iacute;nh m&agrave;n h&igrave;nh, cho trải nghiệm xem thực sự ấn tượng. Chưa hết, tần số cảm ứng 180Hz tăng cường độ nhạy của m&agrave;n h&igrave;nh cảm ứng, gi&uacute;p phản hồi nhanh hơn ở c&aacute;c tựa game.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/realme-8-pro-8.jpg\" alt=\"m&agrave;n h&igrave;nh Realme 8 Pro\" /></p>\r\n<h3><strong>Cảm biến v&acirc;n tay trong m&agrave;n h&igrave;nh cực nhạy</strong></h3>\r\n<p>Một cảm biến v&acirc;n tay được t&iacute;ch hợp ngay trong m&agrave;n h&igrave;nh gi&uacute;p bạn mở kh&oacute;a realme 8 Pro chỉ sau một c&uacute; chạm. Kh&ocirc;ng chỉ mở kh&oacute;a, cảm biến v&acirc;n tay n&agrave;y c&ograve;n cho ph&eacute;p thay thế mật khẩu đăng nhập ở nhiều trang web, ứng dụng, kể cả c&aacute;c ứng dụng ng&acirc;n h&agrave;ng v&agrave; v&iacute; điện tử đ&ograve;i hỏi t&iacute;nh bảo mật cao.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/realme-8-pro-9.jpg\" alt=\"v&acirc;n tay Realme 8 Pro\" /></p>', 0, '6990000', 'ebb376f72f.jpg', 1),
(19, 'Realme 9 Pro 5G', 1, 11, '<p><span>Tận hưởng hiệu năng mạnh c&ugrave;ng m&agrave;n h&igrave;nh si&ecirc;u mượt 120Hz, kết nối 5G v&agrave; sạc nhanh 33W,&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai/realme-9-pro\">Realme 9 Pro</a>&nbsp;l&agrave; người bạn đồng h&agrave;nh l&yacute; tưởng cho cuộc sống năng động. Kh&ocirc;ng chỉ vậy, hệ thống camera 64MP v&agrave; thiết kế mới c&ograve;n gi&uacute;p bạn bắt trọn mọi xu hướng trong năm 2022 n&agrave;y.</span></p>\r\n<p><span><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/02/Realme-9-Pro.jpg\" alt=\"Thiết kế Realme 9 Pro\" /></span></p>\r\n<h3><span>Thiết kế phản&nbsp;quang, ngập tr&agrave;n m&agrave;u sắc</span></h3>\r\n<p>Realme 9 Pro sở hữu vẻ ngo&agrave;i lung linh với thiết kế phản quang ho&agrave;n to&agrave;n mới. Hai m&agrave;u c&oacute; tr&ecirc;n Realme 9 Pro l&agrave; Lam Hừng Đ&ocirc;ng v&agrave; Lục Cực Quang. Lam Hừng Đ&ocirc;ng khi gặp &aacute;nh s&aacute;ng mặt trời sẽ chuyển từ xanh lam sang đỏ, tạo n&ecirc;n hiệu ứng lấp l&aacute;nh tuyệt đẹp. Lục Cực Quang l&agrave; hiệu ứng&nbsp;&aacute;nh s&aacute;ng đầy sắc m&agrave;u tr&ecirc;n nền đ&ecirc;m huyền b&iacute;. C&aacute;c đường n&eacute;t bo cong của Realme 9 Pro kết hợp c&ugrave;ng m&agrave;u mới, tạo n&ecirc;n vẻ đẹp hiện đại nhưng kh&ocirc;ng k&eacute;m phần s&aacute;ng tạo.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/thiet-ke-Realme-9-Pro-1.jpg\" alt=\"Thiết kế Realme 9 Pro\" /></p>\r\n<p>Đồng thời, Realme 9 Pro cũng&nbsp;c&oacute; độ mỏng 8.55mm v&agrave; nặng 195g để bạn dễ d&agrave;ng cầm nắm, sử dụng trong thời gian d&agrave;i m&agrave; kh&ocirc;ng cảm gi&aacute;c&nbsp;kh&oacute; chịu.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/thiet-ke-Realme-9-Pro.jpg\" alt=\"Thiết kế Realme 9 Pro\" /></p>\r\n<h3><span>Kh&aacute;m ph&aacute; hiệu năng ấn tượng</span></h3>\r\n<p>Được cung cấp hiệu năng bởi Snadpragon 695 tr&ecirc;n tiến tr&igrave;nh 6nm ti&ecirc;n tiến, hiệu suất GPU tăng 30%, CPU tăng 15%, Realme 9 Pro đảm bảo xử l&yacute; ấn tượng mọi t&aacute;c vụ, từ cơ bản đến phức tạp. C&aacute;c ứng dụng đ&ograve;i hỏi nhiều t&agrave;i nguy&ecirc;n nhất cũng kh&ocirc;ng l&agrave;m kh&oacute; được Realme 9 Pro. Chưa dừng lại đ&oacute;,&nbsp;<a href=\"https://fptshop.com.vn/tin-tuc/danh-gia/mang-5g-la-gi-tong-quan-ve-mang-khong-day-nhanh-nhat-the-gioi-hien-nay-50246\">c&ocirc;ng nghệ 5G</a>&nbsp;t&iacute;ch hợp c&ograve;n mang đến kỷ nguy&ecirc;n kết nối si&ecirc;u tốc, để bạn lu&ocirc;n c&oacute; trải nghiệm trực tuyến si&ecirc;u nhanh, &ldquo;chiến&rdquo; game hay tải ứng dụng chỉ trong chớp mắt.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/hieu-nang-Realme-9-Pro-1.jpg\" alt=\"Hiệu năng Realme 9 Pro\" /></p>\r\n<h3><span>C&ocirc;ng nghệ mở rộng RAM tự động, đa nhiệm ho&agrave;n hảo</span></h3>\r\n<p>Kh&ocirc;ng chỉ mạnh mẽ, m&agrave; khả năng đa nhiệm tr&ecirc;n Realme 9 Pro cũng ấn tượng. Reame 9 Pro hỗ trợ c&ocirc;ng nghệ mở rộng RAM tự động,&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai\">điện thoại</a>&nbsp;c&oacute; thể tăng th&ecirc;m 5GB, tổng RAM l&ecirc;n đến 13GB. Giờ đ&acirc;y bạn ho&agrave;n to&agrave;n y&ecirc;n t&acirc;m khi đa nhiệm tr&ecirc;n Realme 9 Pro, việc chuyển đổi qua lại giữa c&aacute;c ứng dụng sẽ diễn ra ngay lập tức.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/mo-rong-RAM-Realme-9-Pro.jpg\" alt=\"RAM Realme 9 Pro\" /></p>\r\n<h3><span>M&agrave;n h&igrave;nh 120Hz mượt m&agrave; đến kh&oacute; tin</span></h3>\r\n<p>Realme 9 Pro với&nbsp;<a href=\"https://fptshop.com.vn/tin-tuc/danh-gia/toc-do-lam-tuoi-man-hinh-60hz-90hz-hoac-120hz-co-nghia-la-gi-113378\">tần số qu&eacute;t 120Hz</a>&nbsp;v&agrave; tần số cảm ứng 240Hz, mang tới trải nghiệm mượt m&agrave; trong từng chuyển động. C&aacute;c thao t&aacute;c vuốt chạm, chuyển cảnh, cuộn trang, lướt mạng x&atilde; hội đều diễn ra trong nh&aacute;y mắt. H&atilde;y thử cảm nhận m&agrave;n h&igrave;nh Realme 9 Pro, bạn sẽ thấy sự mượt m&agrave; đến kh&oacute; tin. Ngo&agrave;i ra,&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai/realme\">Realme</a>&nbsp;cũng c&oacute; 6 mức tốc độ kh&aacute;c nhau, được tự động t&ugrave;y chỉnh ph&ugrave; hợp cho từng t&aacute;c vụ, đảm bảo trải nghiệm mượt m&agrave;, lại vừa tiết kiệm pin hiệu quả.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/man-hinh-Realme-9-Pro.jpg\" alt=\"M&agrave;n h&igrave;nh Realme 9 Pro\" /></p>\r\n<h3><span>Sạc nhanh 33W, tiết kiệm thời gian tối ưu</span></h3>\r\n<p>Realme 9 Pro với sạc nhanh 33W, mang tới tốc độ sạc nhanh đến bất ngờ. Bạn sẽ tiết kiệm được thời gian tối ưu v&agrave; thực hiện nhiều hoạt động th&uacute; vị hơn cho cuộc sống năng động. Đồng thời, vi&ecirc;n pin 5.000mAh c&ograve;n cho thời lượng sử dụng cực k&igrave; thoải m&aacute;i. Bạn c&oacute; thể trải nghiệm Realme 9 Pro cả ng&agrave;y d&agrave;i m&agrave; kh&ocirc;ng lo cạn pin.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/sac-nhanh-Realme-9-Pro(1).jpg\" alt=\"Sạc nhanh Realme 9 Pro\" /></p>\r\n<h3><span>Chất lượng ảnh ho&agrave;n hảo</span></h3>\r\n<p>Realme 9 Pro mang trong m&igrave;nh bộ ba camera 64MP chất lượng, để bạn thỏa sức s&aacute;ng tạo v&agrave; ghi lại đầy đủ mọi khoảnh khắc trong cuộc sống.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/02/camera-Realme-9-Pro.jpg\" alt=\"Camera Realme 9 Pro\" /></p>\r\n<p>Đặc biệt camera ch&iacute;nh 64MP sẽ l&agrave; trợ l&yacute; đắc lực để cho c&aacute;c khung ảnh r&otilde; r&agrave;ng, gi&agrave;u chi tiết v&agrave; thu nhiều s&aacute;ng, d&ugrave; ng&agrave;y hay đ&ecirc;m.&nbsp;<a href=\"https://fptshop.com.vn/tin-tuc/danh-gia/ong-kinh-macro-la-gi-tim-hieu-ve-camera-macro-tren-smartphone-130840\">Camera macro</a>&nbsp;2MP ghi lại c&aacute;c chi tiết si&ecirc;u cận trong&nbsp;khoảng c&aacute;ch 2-4cm. Camera g&oacute;c rộng 8MP ph&ugrave; hợp chụp khung cảnh thi&ecirc;n nhi&ecirc;n rộng lớn hay c&ocirc;ng tr&igrave;nh kiến tr&uacute;c đồ sộ.&nbsp;</p>\r\n<p>Đồng thời, hệ thống lấy n&eacute;t tr&ecirc;n Realme 9 Pro cũng cải tiến, gi&uacute;p bạn dễ d&agrave;ng thay đổi ti&ecirc;u cự theo &yacute; muốn. Giờ đ&acirc;y với c&aacute;c thao t&aacute;c vuốt, chạm đơn giản, bạn đ&atilde; c&oacute; thể thu ph&oacute;ng tầm nh&igrave;n, thu mọi chi tiết.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/02/camera-Realme-9-Pro-5.jpg\" alt=\"Camera Realme 9 Pro\" /></p>\r\n<h3><span>Trở th&agrave;nh t&acirc;m điểm của mỗi khung h&igrave;nh</span></h3>\r\n<p>Realme 9 Pro với hai chế độ Giờ cao điểm v&agrave; chế độ T&ocirc; s&aacute;ng ảnh, gi&uacute;p bạn trở th&agrave;nh t&acirc;m điểm của mỗi khung h&igrave;nh. Chế độ Giờ cao điểm cho ph&eacute;p bạn trở th&agrave;nh trung t&acirc;m với&nbsp;đ&aacute;m đ&ocirc;ng&nbsp;n&aacute;o nhiệt. Đối với chế độ T&ocirc; s&aacute;ng ảnh, bạn c&oacute; thể hiệu chỉnh h&igrave;nh ảnh bằng c&aacute;ch vẽ th&ecirc;m m&agrave;u. Mỗi khung h&igrave;nh của bạn sẽ lu&ocirc;n nổi bật v&agrave; đầy phong c&aacute;ch.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/02/camera-Realme-9-Pro-1.jpg\" alt=\"Camera Realme 9 Pro\" /></p>\r\n<h3><span>Bộ lọc m&agrave;u y&ecirc;u th&iacute;ch nhất</span></h3>\r\n<p>Những bộ lọc m&agrave;u được y&ecirc;u th&iacute;ch nhất bởi cộng đồng Realme đ&atilde;&nbsp;c&oacute; mặt tr&ecirc;n Realme 9 Pro. H&atilde;y tưởng tượng những tấm ảnh sau khi chụp xong sẽ được phủ th&ecirc;m lớp m&agrave;u đậm n&eacute;t cổ điển, trở về những năm 90 với bộ lọc 90s Pop, ảnh của bạn sẽ trở n&ecirc;n nghệ thuật hơn bao giờ.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/02/camera-Realme-9-Pro-2.jpg\" alt=\"Camera Realme 9 Pro\" /></p>\r\n<h3><span>Chế độ si&ecirc;u chụp đ&ecirc;m, mang &aacute;nh s&aacute;ng cho m&agrave;n đ&ecirc;m</span></h3>\r\n<p>Đừng để m&agrave;n đ&ecirc;m l&agrave; trở ngại khi chụp ảnh. H&atilde;y bật ngay chế độ Si&ecirc;u chụp đ&ecirc;m tr&ecirc;n Realme 9 Pro để bạn thỏa sức s&aacute;ng tạo ảnh trong đ&ecirc;m tối. Chế độ Si&ecirc;u chụp đ&ecirc;m cho ph&eacute;p điện thoại thu nhiều s&aacute;ng, lấy nhiều chi tiết nhất c&oacute; thể v&agrave; giảm nhiễu tối ưu, mang tới tấm ảnh đ&ecirc;m ưng &yacute;.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/02/camera-Realme-9-Pro-3.jpg\" alt=\"Camera Realme 9 Pro\" /></p>\r\n<h3><span>Bộ lọc m&agrave;u đ&ecirc;m th&ecirc;m lung linh</span></h3>\r\n<p>Realme 9 Pro t&iacute;ch hợp sẵn c&aacute;c bộ lọc m&agrave;u d&agrave;nh ri&ecirc;ng cho những tấm ảnh đ&ecirc;m lung linh, huyền ảo. Ảnh đ&ecirc;m được chụp bởi Realme 9 Pro kh&ocirc;ng chỉ sắc n&eacute;t m&agrave; c&ograve;n ngập tr&agrave;n sự s&aacute;ng tạo, khắc họa trọn vẹn cảnh đ&ecirc;m đầy th&uacute; vị.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/02/camera-Realme-9-Pro-4.jpg\" alt=\"Camera Realme 9 Pro\" /></p>\r\n<h3><span>Ảnh ch&acirc;n dung đ&ecirc;m tỏa s&aacute;ng</span></h3>\r\n<p>Phần ảnh ch&acirc;n dung đ&ecirc;m cũng được t&ocirc; điểm v&agrave; tỏa s&aacute;ng với Hiệu ứng Bokeh động v&agrave; Bộ lọc m&agrave;u ch&acirc;n dung. Bạn sẽ thật sự tỏa s&aacute;ng trong khung h&igrave;nh v&agrave;o c&aacute;c buổi party đ&ecirc;m nhiều niềm vui hay những tấm ảnh đường phố đầy nghệ thuật. H&atilde;y thử ngay v&agrave; cảm nhận ảnh đ&ecirc;m ch&acirc;n dung ấn tượng tr&ecirc;n Realme 9 Pro v&agrave; sẵn s&agrave;ng tạo ra những trend chụp đ&ecirc;m mới nhất tr&ecirc;n mạng x&atilde; hội.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/02/camera-Realme-9-Pro-6.jpg\" alt=\"Camera Realme 9 Pro\" /></p>\r\n<h3><span>Cảm biến v&acirc;n tay n&uacute;t nguồn&nbsp;si&ecirc;u nhạy</span></h3>\r\n<p>Việc bảo mật tr&ecirc;n Realme 9 Pro trở n&ecirc;n y&ecirc;n t&acirc;m v&agrave; tiện lợi hơn với cảm biến v&acirc;n tay ngay n&uacute;t nguồn. Chỉ cần chạm v&agrave;o n&uacute;t nguồn, điện thoại sẽ mở kh&oacute;a ngay lập tức, sẵn s&agrave;ng cho bạn kh&aacute;m ph&aacute; mọi trải nghiệm th&uacute; vị tr&ecirc;n Realme 9 Pro.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/02/mo-khoa-Realme-9-Pro.jpg\" alt=\"Mở kh&oacute;a Realme 9 Pro\" /></p>', 1, '7690000', '519e153543.jpg', 1),
(20, 'Asus ROG Phone 5S 5G', 1, 12, '<p><strong>Với chip xử l&yacute; Qualcomm Snapdragon 888+ 5G cực mạnh, m&agrave;n h&igrave;nh AMOLED tần số qu&eacute;t 144Hz/1ms v&agrave; vi&ecirc;n pin dung lượng pin 6.000 mAh, chiếc điện thoại&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai/asus-rog-5s\">Asus ROG Phone 5S</a>&nbsp;sẽ đem đến trải nghiệm gaming m&agrave; kh&ocirc;ng một mẫu smartphone n&agrave;o tr&ecirc;n thị trường c&oacute; thể đạt được. Ngo&agrave;i ra, loạt t&iacute;nh năng như AirTrigger 5 kết hợp c&ugrave;ng &acirc;m thanh GameFX cao cấp cũng hứa hẹn những trải nghiệm h&agrave;ng đầu thế giới hiện tại.</strong></p>\r\n<p><img id=\"Điện thoại Asus ROG Phone 5S\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/BaoPK/Lumia%20950XL/mo-ta-san-pham-asus-rog-5s-5g-8gb-128gb-1.JPG\" alt=\"Điện thoại Asus ROG Phone 5S\" /></p>\r\n<h3><strong>Chip xử l&yacute; mạnh mẽ xuất sắc h&agrave;ng đầu</strong></h3>\r\n<p>Sở hữu một trong những bộ vi xử l&yacute; h&agrave;ng đầu thị trường l&agrave; Snapdragon 888+ đi k&egrave;m GPU&nbsp;<a href=\"https://fptshop.com.vn/tin-tuc/danh-gia/danh-gia-adreno-660-619-612-va-610-bo-gpu-do-hoa-danh-cho-thiet-bi-android-tam-trung-134867\">Adreno 660</a>. Chip Snapdragon 888+ sở hữu l&otilde;i ch&iacute;nh 3.0 GHz (Cortex-X1) cải tiến hơn so với l&otilde;i 2.84 GHz tr&ecirc;n chip&nbsp;<a href=\"https://fptshop.com.vn/tin-tuc/danh-gia/tim-hieu-vi-xu-ly-snapdragon-888-129051\">Snapdragon 888</a>&nbsp;của Asus ROG Phone 5. Chipset n&agrave;y gi&uacute;p Asus ROG Phone 5S c&oacute; thể xử l&yacute; tốt mọi t&aacute;c vụ v&agrave; chiến mọi tựa game m&agrave; kh&ocirc;ng lo giật lag.</p>\r\n<p><img id=\"Chip xử l&yacute; Asus ROG Phone 5S\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/BaoPK/Lumia%20950XL/mo-ta-san-pham-asus-rog-5s-5g-8gb-128gb-2.JPG\" alt=\"Chip xử l&yacute; Asus ROG Phone 5S\" /></p>\r\n<h3><strong>Tản nhiệt hiệu quả, hiệu suất ổn định</strong></h3>\r\n<p>Asus ROG Phone 5S tối ưu hiệu năng tổng thể bằng c&aacute;ch sử dụng hệ thống tản nhiệt GameCool 5 với cấu tr&uacute;c bao bọc CPU v&agrave; chia t&aacute;ch hệ thống pin ra hai b&ecirc;n. Nhờ vậy, sản phẩm dễ d&agrave;ng ph&acirc;n t&aacute;ch nhiệt lượng ra to&agrave;n bộ khung m&aacute;y, duy tr&igrave; hiệu năng kh&ocirc;ng đổi trong qu&aacute; tr&igrave;nh vận h&agrave;nh.</p>\r\n<p>Trung t&acirc;m th&acirc;n m&aacute;y sẽ hạn chế t&iacute;ch tụ nhiệt nhờ sự hỗ trợ của buồng hơi t&aacute;i thiết kế k&egrave;m c&aacute;c tấm graphite mở rộng. Chiếc&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai/asus\">điện thoại Asus</a>&nbsp;c&ograve;n được trang bị k&egrave;m quạt tản nhiệt AeroActive Cooler 5 c&oacute; khả năng thổi kh&iacute; m&aacute;t v&agrave;o vị tr&iacute; đặt chip, từ đ&oacute; giảm tới 10&deg;C nhiệt độ th&acirc;n m&aacute;y.</p>\r\n<p><img id=\"Tản nhiệt Asus ROG Phone 5S\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/BaoPK/Lumia%20950XL/mo-ta-san-pham-asus-rog-5s-5g-8gb-128gb-3.JPG\" alt=\"Tản nhiệt Asus ROG Phone 5S\" /></p>\r\n<h3><strong>M&agrave;n h&igrave;nh si&ecirc;u n&eacute;t với tần số qu&eacute;t 144Hz</strong></h3>\r\n<p>Nhờ tốc độ phản hồi cao v&agrave; độ trễ cảm ứng cực thấp, m&agrave;n h&igrave;nh Asus ROG Phone&nbsp;5S 5G sẽ bắt kịp mọi thao t&aacute;c tay để bạn lu&ocirc;n đi trước đối thủ trong những t&igrave;nh huống quyết định thắng thua của game đấu. Tấm nền AMOLED&nbsp;<a href=\"https://fptshop.com.vn/tin-tuc/danh-gia/tan-so-quet-la-gi-giai-ma-cuoc-chay-dua-tan-so-quet-man-hinh-tren-smartphone-123132\">tần số qu&eacute;t</a>&nbsp;144Hz/1ms thể hiện khả năng hiển thị đỉnh cao, t&aacute;i hiện mọi chi tiết si&ecirc;u sắc n&eacute;t v&agrave; cho thấy độ ch&iacute;nh x&aacute;c gam m&agrave;u vượt trội. Bạn sẽ c&oacute; những trải nghiệm m&atilde;n nh&atilde;n nhất tr&ecirc;n chiếc gaming phone đặc trưng n&agrave;y.</p>\r\n<p><img id=\"M&agrave;n h&igrave;nh Asus ROG Phone 5S\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/BaoPK/Lumia%20950XL/mo-ta-san-pham-asus-rog-5s-5g-8gb-128gb-4.JPG\" alt=\"M&agrave;n h&igrave;nh Asus ROG Phone 5S\" /></p>\r\n<h3><strong>Bộ loa k&eacute;p cho &acirc;m thanh đẳng cấp</strong></h3>\r\n<p>Kh&ocirc;ng chỉ đảm bảo chất lượng h&igrave;nh ảnh ấn tượng, Asus ROG Phone 5S c&ograve;n khiến bạn th&iacute;ch th&uacute; với từng tiết tấu &acirc;m thanh trong game khi sở hữu bộ loa k&eacute;p 7 nam ch&acirc;m đối xứng, đem lại hiệu ứng stereo c&acirc;n bằng. Nhờ giữ lại jack 3.5 cao cấp, sản phẩm hỗ trợ tốt cho c&aacute;c d&ograve;ng&nbsp;<a href=\"https://fptshop.com.vn/phu-kien/tai-nghe\">tai nghe</a>&nbsp;ESS DAC hi-fi chất lượng cao. C&ocirc;ng nghệ GameFX được nghi&ecirc;n cứu để trau chuốt từng sắc th&aacute;i &acirc;m thanh trong bối cảnh game m&agrave; hiếm c&oacute; một smartphone n&agrave;o đạt được.</p>\r\n<p><img id=\"Bộ loa k&eacute;p Asus ROG Phone 5S\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/BaoPK/Lumia%20950XL/mo-ta-san-pham-asus-rog-5s-5g-8gb-128gb-5.JPG\" alt=\"Bộ loa k&eacute;p Asus ROG Phone 5S\" /></p>\r\n<h3><strong>Cảm biến si&ecirc;u &acirc;m v&agrave; kiểm so&aacute;t chuyển động</strong></h3>\r\n<p>Tr&igrave;nh điều khiển chuyển động m&agrave; Asus trang bị cho ROG Phone 5S bao gồm 10 cảm biến kh&aacute;c nhau, bạn sẽ dễ d&agrave;ng lựa chọn c&aacute;c thao t&aacute;c cử chỉ cho ph&ugrave; hợp với tựa game m&agrave; m&igrave;nh đang trải nghiệm. Những c&uacute; xoay nhẹ để drift khi đua xe kết hợp c&ugrave;ng động t&aacute;c đẩy&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai\">điện thoại</a>&nbsp;về ph&iacute;a trước v&agrave; tăng tốc sẽ khiến cho bạn nhập t&acirc;m hơn nhiều v&agrave;o tr&ograve; chơi m&igrave;nh thưởng thức. Ngo&agrave;i ra, Asus ROG Phone 5S c&ograve;n được t&iacute;ch hợp cảm biến vật l&yacute; đặc biệt ở cạnh b&ecirc;n, mang tới thao t&aacute;c cử chỉ như đang cầm trong tay một m&aacute;y chơi game console thực thụ.</p>\r\n<p><img id=\"Cảm biến Asus ROG Phone 5S\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/BaoPK/Lumia%20950XL/mo-ta-san-pham-asus-rog-5s-5g-8gb-128gb-6.JPG\" alt=\"Cảm biến Asus ROG Phone 5S\" /></p>\r\n<h3><strong>Pin d&agrave;i l&acirc;u, thoải m&aacute;i chinh chiến</strong></h3>\r\n<p>Với ROG Phone 5S 5G, trải nghiệm game của bạn sẽ chẳng ngại gi&aacute;n đoạn nhờ vi&ecirc;n pin dung lượng &ldquo;khủng&rdquo; 6.000 mAh đi k&egrave;m c&ocirc;ng nghệ sạc HyperCharge 65W. C&aacute;c t&iacute;nh năng n&acirc;ng cao như ổn định d&ograve;ng sạc cho ph&eacute;p bạn d&ugrave;ng nguồn trực tiếp trong qu&aacute; tr&igrave;nh chơi game, từ đ&oacute; dễ d&agrave;ng k&eacute;o d&agrave;i tuổi thọ pin hơn so với việc chơi game tr&ecirc;n những loại smartphone b&igrave;nh thường kh&aacute;c.</p>\r\n<p><img id=\"Pin Asus ROG Phone 5S\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/BaoPK/Lumia%20950XL/mo-ta-san-pham-asus-rog-5s-5g-8gb-128gb-7.JPG\" alt=\"Pin Asus ROG Phone 5S\" /></p>\r\n<h3><strong>Thiết kế phục vụ cho trải nghiệm game</strong></h3>\r\n<p>Asus ROG Phone 5S đem đến hệ thống LED Aura RGB kết hợp th&agrave;nh logo đặc trưng của thương hiệu. Khả năng ph&aacute;t s&aacute;ng v&agrave; chuyển sắc gradient với nhiều chủ đề sẽ đa dạng chắc chắn sẽ khiến bạn th&iacute;ch th&uacute;. Ngo&agrave;i ra, Asus c&ograve;n thể hiện sự chiều l&ograve;ng game thủ khi tạo n&ecirc;n những g&oacute;i giao diện đặc biệt b&ecirc;n trong với h&igrave;nh ảnh lấy cảm hứng từ những tr&ograve; chơi được y&ecirc;u th&iacute;ch nhất hiện tại.</p>\r\n<p><img id=\"Thiết kế Asus ROG Phone 5S\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/BaoPK/Lumia%20950XL/mo-ta-san-pham-asus-rog-5s-5g-8gb-128gb-8.JPG\" alt=\"Thiết kế Asus ROG Phone 5S\" /></p>\r\n<h3><strong>Kết nối si&ecirc;u ổn định nhờ 5G v&agrave; Wi-Fi 6E</strong></h3>\r\n<p>Mọi game thủ đều hiểu r&otilde; tầm quan trọng của kết nối ổn định trong qu&aacute; tr&igrave;nh chơi game khi m&agrave; thắng thua đ&ocirc;i l&uacute;c chỉ quyết định trong một khoảnh khắc. Asus ROG Phone 5S đảm bảo lu&ocirc;n duy tr&igrave; t&iacute;n hiệu nhanh nhạy mọi l&uacute;c nhờ hỗ trợ c&aacute;c&nbsp;phương thức tương t&aacute;c h&agrave;ng đầu hiện nay kết nối&nbsp;<a href=\"https://fptshop.com.vn/tin-tuc/danh-gia/mang-5g-la-gi-tong-quan-ve-mang-khong-day-nhanh-nhat-the-gioi-hien-nay-50246\">mạng 5G</a>&nbsp;v&agrave; Wi-Fi 6E nhằm gia tăng t&iacute;nh ổn định. Kh&ocirc;ng chỉ vậy, c&ocirc;ng nghệ đa anten với c&ocirc;ng nghệ HyperFusion sẽ mang tới khả năng chuyển s&oacute;ng th&ocirc;ng minh để t&igrave;m kiếm hệ thống mạng c&oacute; s&oacute;ng mạnh hơn.</p>\r\n<p><img id=\"Kết nối Asus ROG Phone 5S\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/BaoPK/Lumia%20950XL/mo-ta-san-pham-asus-rog-5s-5g-8gb-128gb-9.JPG\" alt=\"Kết nối Asus ROG Phone 5S\" /></p>\r\n<h3><strong>Camera chất lượng với cảm biến h&agrave;ng đầu</strong></h3>\r\n<p>Cụm ba camera m&agrave; Asus ROG Phone 5S sở hữu c&oacute; thể tạo được những khu&ocirc;n h&igrave;nh chất lượng sắc n&eacute;t trong mọi g&oacute;c độ. Trong đ&oacute;, camera ch&iacute;nh cảm biến Sony IMX686 64MP sẽ kết hợp c&ugrave;ng camera si&ecirc;u rộng 13MP c&ugrave;ng camera macro.</p>\r\n<p>To&agrave;n bộ th&ocirc;ng tin m&agrave; camera truyền về sẽ được xử l&yacute; bởi hệ thống phần mềm một c&aacute;ch hiệu quả nhằm tạo ra c&aacute;c bức ảnh/video cao cấp nhất. C&aacute;c t&iacute;nh năng như Free Zoom, Wind Filter v&agrave; Acoustic Focus gi&uacute;p chủ nh&acirc;n chiếc điện thoại thoải m&aacute;i căn chỉnh cho những t&aacute;c vụ như livestream hay selfie.</p>\r\n<p><img id=\"Camera Asus ROG Phone 5S\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/BaoPK/Lumia%20950XL/mo-ta-san-pham-asus-rog-5s-5g-8gb-128gb-a.JPG\" alt=\"Camera Asus ROG Phone 5S\" /></p>', 1, '16990000', '24b249942e.jpg', 1),
(21, 'Xiaomi 11T Pro 5G', 1, 4, '<p><strong><a href=\"https://fptshop.com.vn/dien-thoai/xiaomi-11t-pro\">Xiaomi 11T Pro</a>&nbsp;l&agrave; chiếc&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai\">điện thoại</a>&nbsp;cao cấp c&oacute; mức gi&aacute; tốt nhất hiện nay. Bạn sẽ nhận được camera chuy&ecirc;n nghiệp 108MP, sạc si&ecirc;u nhanh 120W, m&agrave;n h&igrave;nh AMOLED 120Hz mượt m&agrave; hỗ trợ Dolby Vision v&agrave; bộ vi xử l&yacute; đầu bảng Snapdragon 888, một loạt những t&iacute;nh năng đỉnh cao sẽ mang tới trải nghiệm th&uacute; vị hơn bao giờ hết.</strong></p>\r\n<p><strong><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/xiaomi-11t-pro-1.jpg\" alt=\"Xiaomi 11T Pro\" /></strong></p>\r\n<h3><strong>Camera độ ph&acirc;n giải khủng 108MP, ghi lại h&igrave;nh ảnh theo c&aacute;ch ho&agrave;n hảo nhất</strong></h3>\r\n<p>Xiaomi 11T Pro mang tr&ecirc;n m&igrave;nh 3 camera sau, trong đ&oacute; camera chịu tr&aacute;nh nhiệm chụp h&igrave;nh ch&iacute;nh độ ph&acirc;n giải l&ecirc;n tới 108MP c&ugrave;ng th&ocirc;ng số phần cứng ấn tượng. K&iacute;ch thước điểm ảnh lớn 0,7 um, c&ocirc;ng nghệ si&ecirc;u điểm ảnh 9 trong 1 tạo n&ecirc;n điểm ảnh 2,1 um c&ugrave;ng khẩu độ f/1.75 cho khả năng thu s&aacute;ng ho&agrave;n hảo. Bạn c&oacute; thể chụp ảnh xuất sắc trong mọi điều kiện &aacute;nh s&aacute;ng với độ chi tiết đến kh&oacute; tin, m&agrave;u sắc sống động v&agrave; giữ được sự ch&acirc;n thực của h&igrave;nh ảnh.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/xiaomi-11t-pro-14.jpg\" alt=\"camera Xiaomi 11T Pro\" /></p>\r\n<h3><strong>Camera chuy&ecirc;n nghiệp</strong></h3>\r\n<p>Kh&ocirc;ng chỉ camera ch&iacute;nh, tất cả ống k&iacute;nh camera c&oacute; mặt tr&ecirc;n Xiaomi 11T Pro đều hết sức chuy&ecirc;n nghiệp. Camera g&oacute;c si&ecirc;u rộng 8MP cho g&oacute;c chụp rộng tới 120 độ, bắt trọn những khung h&igrave;nh rộng lớn; camera Telemacro 5MP c&oacute; thể chụp ảnh những vật thể si&ecirc;u nhỏ đầy th&uacute; vị; camera selfie 16MP cho bạn tự tin tỏa s&aacute;ng trong mọi khung h&igrave;nh với những bức ảnh chất lượng từ camera trước.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/xiaomi-11t-pro-9.png\" alt=\"camera Xiaomi 11T Pro\" /></p>\r\n<h3><strong>Mọi khoảnh khắc đều trở n&ecirc;n diệu kỳ</strong></h3>\r\n<p>Chế độ điện ảnh Cinemagic ho&agrave;n to&agrave;n mới tr&ecirc;n Xiaomi 11T Pro gi&uacute;p biến những khoảnh khắc đời thường trở th&agrave;nh c&aacute;c thước phim điện ảnh đầy hấp dẫn. Bạn sẽ c&oacute; một loạt t&iacute;nh năng dựng phim điện to&aacute;n h&agrave;ng đầu, c&aacute;c hiệu ứng chuy&ecirc;n dụng để chỉnh sửa v&agrave; xuất bản nhanh ch&oacute;ng. T&iacute;nh năng quay video HDR 10+ mang tới những đoạn phim si&ecirc;u sống động với hơn 1 tỷ m&agrave;u sắc, h&igrave;nh ảnh s&aacute;ng tối v&agrave; &aacute;nh s&aacute;ng được t&aacute;i hiện ch&acirc;n thực, tha hồ để bạn ghi lại h&agrave;nh tr&igrave;nh những chuyến đi một c&aacute;ch ấn tượng.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/xiaomi-11t-pro-11.jpg\" alt=\"chụp ảnh Xiaomi 11T Pro\" /></p>\r\n<h3><strong>Sức mạnh h&agrave;ng đầu, trường tồn theo thời gian</strong></h3>\r\n<p>Sở hữu bộ vi xử l&yacute; mạnh bậc nhất thế giới smartphone hiện nay, hiệu năng của Xiaomi 11T Pro kh&ocirc;ng hề c&oacute; điểm g&igrave; để ch&ecirc;. Snapdragon 888 được x&acirc;y dựng tr&ecirc;n tiến tr&igrave;nh 5nm h&agrave;ng đầu, tăng cường sức mạnh 25% CPU v&agrave; 35% GPU so với bộ vi xử l&yacute; cao cấp thế hệ trước. Bạn vừa c&oacute; sức mạnh đột ph&aacute;, lại vừa c&oacute; khả năng sử dụng năng lượng hiệu quả. Chơi game nặng, livestream, quay video 4K hay mở những tệp tin dung lượng cao chưa bao giờ l&agrave; vấn đề tr&ecirc;n Xiaomi 11T Pro.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/xiaomi-11t-pro-3.jpg\" alt=\"cấu h&igrave;nh Xiaomi 11T Pro\" /></p>\r\n<h3><strong>Đến với kỷ nguy&ecirc;n 5G</strong></h3>\r\n<p>Snapdragon 888 5G l&agrave; một trong những con chip 5G tốc độ cao nhất hiện nay. Khả năng tương th&iacute;ch với nhiều băng tần, nhiều nh&agrave; mạng, thu s&oacute;ng ổn định v&agrave; tốc độ kh&ocirc;ng tưởng gi&uacute;p trải nghiệm Internet 5G tr&ecirc;n Xiaomi 11T Pro trở n&ecirc;n kh&oacute; qu&ecirc;n. Bạn c&oacute; thể xem video 4K trực tuyến, tải tệp tin nặng trong chớp mắt hay chơi game online m&agrave; kh&ocirc;ng c&oacute; độ trễ với mạng 5G, mạng kh&ocirc;ng d&acirc;y di động nhanh nhất thế giới.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/xiaomi-11t-pro-7.jpg\" alt=\"5G Xiaomi 11T Pro\" /></p>\r\n<h3><strong>C&ocirc;ng nghệ sạc nhanh đột ph&aacute; 120W, sạc đầy 100% chỉ sau 17 ph&uacute;t</strong></h3>\r\n<p>Xiaomi 11T Pro l&agrave; chiếc smartphone c&oacute; tốc độ sạc nhanh nhất hiện nay với c&ocirc;ng nghệ sạc si&ecirc;u khủng Xiaomi HyperCharge 120W. Thật kh&oacute; c&oacute; thể tin được vi&ecirc;n pin dung lượng cao 5000mAh của Xiaomi 11T Pro lại chỉ c&oacute; thể sạc đầy từ 0 l&ecirc;n 100% trong vỏn vẹn 17 ph&uacute;t. H&atilde;y qu&ecirc;n đi việc cắm sạc qua đ&ecirc;m, v&igrave; chỉ cần sạc một l&uacute;c trong khi ăn s&aacute;ng, điện thoại của bạn đ&atilde; tr&agrave;n đầy năng lượng cho cả ng&agrave;y.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/xiaomi-11t-pro-8.jpg\" alt=\"sạc nhanh Xiaomi 11T Pro\" /></p>\r\n<h3><strong>Thưởng thức h&igrave;nh ảnh tuyệt mỹ</strong></h3>\r\n<p>Thế giới h&igrave;nh ảnh tr&ecirc;n chiếc&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai/xiaomi\">Xiaomi</a>&nbsp;n&agrave;y&nbsp;trở n&ecirc;n sống động hơn bao giờ hết với m&agrave;n h&igrave;nh chất lượng đỉnh cao. Trước mắt bạn l&agrave; tấm nền AMOLED 6,67 inch độ tương phản 5.000.000:1; độ s&aacute;ng tối đa 1.000 nits; hỗ trợ một tỉ m&agrave;u c&ocirc;ng nghệ m&agrave;u sắc ch&iacute;nh x&aacute;c TrueColor v&agrave; c&ocirc;ng nghệ HDR10+, cho trải nghiệm m&atilde;n nh&atilde;n, đặc biệt l&agrave; khi xem phim. B&ecirc;n cạnh đ&oacute; l&agrave; c&ocirc;ng nghệ Dolby Vision v&agrave; Dolby Atmos thường được sử dụng trong rạp chiếu phim, loa k&eacute;p Harman Kardon, tất cả sẽ mang đến trải nghiệm h&igrave;nh ảnh c&ugrave;ng &acirc;m thanh tuyệt hảo.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/xiaomi-11t-pro-2.jpg\" alt=\"m&agrave;n h&igrave;nh Xiaomi 11T Pro\" /></p>\r\n<h3><strong>Mượt m&agrave; trong từng c&uacute; chạm</strong></h3>\r\n<p>Ngo&agrave;i khả năng hiển thị xuất sắc, m&agrave;n h&igrave;nh Xiaomi 11T Pro c&ograve;n c&oacute; tốc độ l&agrave;m mới 120Hz v&agrave; tốc độ lấy mẫu cảm ứng 480Hz. Từng thao t&aacute;c vuốt chạm, hiệu ứng chuyển cảnh, cuộn trang đều mượt m&agrave; đ&aacute;ng kinh ngạc. H&atilde;y thử cảm nhận m&agrave;n h&igrave;nh Xiaomi 11T Pro, bạn sẽ thấy sự kh&aacute;c biệt chỉ sau v&agrave;i c&uacute; chạm.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/xiaomi-11t-pro-4.jpg\" alt=\"tần số qu&eacute;t Xiaomi 11T Pro\" /></p>\r\n<h3><strong>Thể hiện đẳng cấp</strong></h3>\r\n<p>Thiết kế Xiaomi 11T Pro vẫn đi theo ng&ocirc;n ngữ cao cấp v&agrave; tinh tế. C&aacute;c chi tiết được l&agrave;m tối giản, t&iacute;nh to&aacute;n tỉ mỉ để điện thoại vừa c&oacute; t&iacute;nh thẩm mỹ cao, lại vừa ho&agrave;n hảo tr&ecirc;n tay người d&ugrave;ng. Hai phi&ecirc;n bản Xanh Thi&ecirc;n H&agrave; v&agrave; Trắng &Aacute;nh Trăng được bao phủ một lớp phủ nh&uacute;ng chống l&oacute;a, trong khi đ&oacute; phi&ecirc;n bản X&aacute;m Thi&ecirc;n Thạch được ho&agrave;n thiện từ kim loại đ&aacute;nh b&oacute;ng, tạo những n&eacute;t c&aacute; t&iacute;nh ri&ecirc;ng biệt.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/xiaomi-11t-pro-6.jpg\" alt=\"thiết kế Xiaomi 11T Pro\" /></p>', 0, '12490000', '71c589fce2.jpg', 1),
(22, 'Xiaomi 12', 1, 4, '<p><span>Với&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai/xiaomi-12\">Xiaomi 12</a>&nbsp;trong tay, bạn sẽ dễ d&agrave;ng l&agrave;m chủ mọi cuộc vui của ri&ecirc;ng m&igrave;nh. D&ugrave; l&agrave; chụp ảnh, chơi game hay đắm ch&igrave;m trong những bộ phim, điện thoại sẽ lu&ocirc;n cho bạn những trải nghiệm h&agrave;ng đầu. Chiếc flagship n&agrave;y sẽ l&agrave; một trong những lựa chọn cực hot d&agrave;nh cho bạn ngay h&ocirc;m nay.&nbsp;</span></p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/xiaomi-12(1).jpg\" alt=\"Điện thoại Xiaomi 12 (1)\" /></p>\r\n<h3><span>Thiết kế thanh lịch, quyến rũ trong l&ograve;ng b&agrave;n tay</span></h3>\r\n<p>Với trọng lượng 180g v&agrave; m&agrave;n h&igrave;nh chỉ rộng 69,9mm, Xiaomi 12 sử dụng bề mặt bo cong đối xứng ở cả hai cạnh trước v&agrave; sau, cho kiểu d&aacute;ng bo tr&ograve;n tinh tế để mang lại cảm gi&aacute;c cầm nắm mượt m&agrave;, nhẹ nh&agrave;ng trong l&ograve;ng b&agrave;n tay.&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai\">Điện thoại</a>&nbsp;c&oacute; ba phi&ecirc;n bản m&agrave;u bao gồm X&aacute;m, Xanh v&agrave; T&iacute;m kết hợp với chất liệu k&iacute;nh mờ, to&aacute;t l&ecirc;n vẻ thanh lịch v&agrave; quyến rũ khi sử dụng n&oacute;. Thoải m&aacute;i khi cầm, Xiaomi 12 sẽ l&agrave;m cho mọi tương t&aacute;c trở n&ecirc;n th&uacute; vị.&nbsp;</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/xiaomi-12-2.jpg\" alt=\"Xiaomi 12 - Thiết kế thanh lịch\" /></p>\r\n<h3><span>Chụp chuyển động sắc n&eacute;t, r&otilde; r&agrave;ng</span></h3>\r\n<p>Xiaomi ProFocus l&agrave; một c&ocirc;ng nghệ lấy n&eacute;t ho&agrave;n to&agrave;n mới được t&iacute;ch hợp v&agrave;o Xiaomi 12 với thuật to&aacute;n theo d&otilde;i v&agrave; t&igrave;m hiểu chuyển động của chủ thể một c&aacute;ch th&ocirc;ng minh, cho ph&eacute;p bạn chụp c&aacute;c đối tượng chuyển động r&otilde; r&agrave;ng hơn. C&ocirc;ng nghệ n&agrave;y cũng bao gồm khả năng lấy n&eacute;t tự động bằng nhận diện mắt v&agrave; khu&ocirc;n mặt.&nbsp;</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/xiaomi-12-3(1).jpg\" alt=\"Xiaomi 12 - Chụp chuyển động sắc n&eacute;t\" /></p>\r\n<h3><span>Chụp v&agrave; quay video đ&ecirc;m đỉnh cao</span></h3>\r\n<p>Với sự trợ gi&uacute;p từ c&aacute;c thuật to&aacute;n AI, video được ghi lại tr&ecirc;n Xiaomi 12 sẽ tự động l&agrave;m s&aacute;ng cảnh v&agrave; giữ lại nhiều chi tiết hơn, ngay cả khi quay trong b&oacute;ng tối. Kh&ocirc;ng chỉ c&oacute; khả năng quay video mạnh mẽ trong điều kiện &aacute;nh s&aacute;ng yếu, chất lượng h&igrave;nh ảnh cũng rất tuyệt vời. T&iacute;nh năng Chế độ ban đ&ecirc;m tr&ecirc;n m&aacute;y ảnh g&oacute;c rộng v&agrave;&nbsp;<a href=\"https://fptshop.com.vn/tin-tuc/danh-gia/ong-kinh-goc-sieu-rong-tren-smartphone-la-gi-134612\">g&oacute;c si&ecirc;u rộng</a>&nbsp;gi&uacute;p bạn chụp &aacute;nh s&aacute;ng v&agrave; b&oacute;ng đ&ecirc;m sống động hơn.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/xiaomi-12-4.jpg\" alt=\"Xiaomi 12 - Chụp v&agrave; quay video đ&ecirc;m đỉnh cao\" /></p>\r\n<h3><span>S&aacute;ng tạo kh&ocirc;ng giới hạn</span></h3>\r\n<p>Với cụm ba camera tr&ecirc;n điện thoại Xiaomi 12, bạn sẽ dễ d&agrave;ng s&aacute;ng tạo theo c&aacute;ch ri&ecirc;ng của m&igrave;nh. Cảm biến IMX766 50MP sẽ đem đến những bức ảnh chi tiết v&agrave; sống động, trong khi camera g&oacute;c si&ecirc;u rộng 13MP v&agrave; telemacro 5MP sẽ cho bạn th&ecirc;m nhiều g&oacute;c độ v&agrave; nhiều chế độ chụp kh&aacute;c nhau. Với c&aacute;c chế độ kh&aacute;c như Ch&acirc;n dung ban đ&ecirc;m v&agrave; One-click AI cinema, điện thoại sẽ cho ra những bức ảnh ch&acirc;n dung v&agrave; c&aacute;c đoạn phim ngắn đậm chất nghệ thuật.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/xiaomi-12-5.jpg\" alt=\"Xiaomi 12 - S&aacute;ng tạo kh&ocirc;ng giới hạn\" /></p>\r\n<h3><span>Hiệu suất đ&aacute;ng kinh ngạc</span></h3>\r\n<p>Với việc sử dụng bộ xử l&yacute;&nbsp;<a href=\"https://fptshop.com.vn/tin-tuc/tin-moi/qualcomm-snapdragon-8-gen-141279\">Snapdragon 8 Gen 1</a>, hiệu suất tr&ecirc;n Xiaomi 12 đ&atilde; được n&acirc;ng l&ecirc;n một tầm cao mới nhờ vi xử l&yacute; tiến tr&igrave;nh 4nm ti&ecirc;n tiến kết hợp&nbsp;<a href=\"https://fptshop.com.vn/tin-tuc/danh-gia/toc-do-mang-5g-nhanh-den-muc-nao-129093\">tốc độ 5G</a>. Việc kết xuất đồ họa v&agrave; hiệu quả năng lượng được n&acirc;ng cao sẽ gi&uacute;p cho c&aacute;c t&aacute;c vụ của bạn d&ugrave; l&agrave; chụp ảnh, chơi game hay xem nội dung được xử l&yacute; với tốc độ mạnh mẽ đến đ&aacute;ng kinh ngạc, mở ra kỷ nguy&ecirc;n mới về hiệu suất tr&ecirc;n smartphone Android.&nbsp;</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/xiaomi-12-6.jpg\" alt=\"Xiaomi 12 - Hiệu suất đ&aacute;ng kinh ngạc\" /></p>\r\n<h3><span>Bữa tiệc m&agrave;u sắc, trải nghiệm nội dung mượt m&agrave;</span></h3>\r\n<p>Xiaomi 12 sử dụng m&agrave;n h&igrave;nh cong tr&agrave;n cạnh với k&iacute;ch thước 6.28 inch, sử dụng&nbsp;<a href=\"https://fptshop.com.vn/tin-tuc/danh-gia/man-hinh-amoled-la-gi-34866\">tấm nền AMOLED</a>&nbsp;FHD+. Đ&acirc;y l&agrave; một m&agrave;n h&igrave;nh hiển thị ho&agrave;n hảo về độ r&otilde; n&eacute;t, m&agrave;u sắc v&agrave; độ mịn, đạt điểm A+ đến từ cơ quan đ&aacute;nh gi&aacute; DisplayMate, hỗ trợ&nbsp;<a href=\"https://fptshop.com.vn/tin-tuc/danh-gia/dolby-vision-la-gi-khac-biet-gi-so-voi-hdr10-101940\">HDR10+ v&agrave; Dolby Vision</a>.&nbsp;</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/xiaomi-12-7.jpg\" alt=\"Xiaomi 12 - trải nghiệm nội dung mượt m&agrave;\" /></p>\r\n<p>Chưa dừng lại ở đ&oacute;,&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai/xiaomi\">Xiaomi</a>&nbsp;đ&atilde; cung cấp cho Xiaomi 12 tốc độ l&agrave;m mới AdaptiveSync 120Hz mượt m&agrave;, m&agrave;u sắc phong ph&uacute; với DCI-P3, hơn 68 tỷ m&agrave;u v&agrave; độ s&aacute;ng cực cao l&ecirc;n đến 1.100 nits, cho bạn &aacute;nh s&aacute;ng v&agrave; m&agrave;u sắc được hiển thị cực ấn tượng d&ugrave; sử dụng ở trong nh&agrave; hay ngo&agrave;i trời.&nbsp;</p>\r\n<h3><span>&Acirc;m thanh kh&ocirc;ng gian đắm ch&igrave;m</span></h3>\r\n<p>Với thiết kế loa k&eacute;p đối xứng v&agrave; được t&iacute;ch hợp bởi Harman Kardon cũng như hỗ trợ bởi&nbsp;<a href=\"https://fptshop.com.vn/tin-tuc/danh-gia/cong-nghe-am-thanh-dolby-la-gi-56873\">Dolby Atmos</a>, Xiaomi 12 đảm bảo trải nghiệm &acirc;m thanh nổi đắm ch&igrave;m hơn, gi&uacute;p bạn thưởng thức phim, &acirc;m nhạc hoặc trải nghiệm &acirc;m thanh tr&ograve; chơi nhiều hơn với &acirc;m thanh mượt m&agrave;, chất lượng cao.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/xiaomi-12-8.jpg\" alt=\"Xiaomi 12 - &Acirc;m thanh kh&ocirc;ng gian đắm ch&igrave;m\" /></p>\r\n<h3><span>Sử dụng liền mạch</span></h3>\r\n<p>D&ugrave; c&oacute; th&acirc;n m&aacute;y nhỏ gọn nhưng điện thoại Xiaomi 12 vẫn sở hữu vi&ecirc;n pin 4500mAh mạnh mẽ v&agrave; bạn sẽ kh&ocirc;ng bao giờ phải lo lắng về việc hết pin với khả năng sạc c&oacute; d&acirc;y l&ecirc;n đến 67W. Điện thoại chỉ mất 39 ph&uacute;t để nạp đầy năng lượng, chỉ đủ thời gian để bạn thư gi&atilde;n cũng như nạp năng lượng cho bản th&acirc;n với một t&aacute;ch c&agrave; ph&ecirc;.&nbsp;</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/xiaomi-12-9.jpg\" alt=\"Xiaomi 12 - Sử dụng liền mạch\" /></p>', 0, '18990000', '44c4e70266.jpg', 1);
INSERT INTO `tbl_product` (`productId`, `productName`, `catId`, `brandId`, `product_description`, `type`, `price`, `image`, `status`) VALUES
(23, 'Xiaomi 12 Pro', 1, 4, '<p><span>Với việc li&ecirc;n tục ph&aacute;t triển v&agrave; cải tiến những c&ocirc;ng nghệ vượt bậc,&nbsp;<a href=\"https://fptshop.com.vn/dien-thoai/xiaomi\">Xiaomi</a>&nbsp;đ&atilde; đem đến một Xiaomi 12 Pro ho&agrave;n hảo, sẵn s&agrave;ng l&agrave;m chủ mọi cuộc chơi của bạn. Với bộ ba camera 50MP, m&agrave;n h&igrave;nh 2K+, sạc nhanh 120W v&agrave; bộ xử l&yacute; h&agrave;ng đầu, điện thoại sẽ l&agrave; lựa chọn h&agrave;ng đầu cho năm 2022.&nbsp;</span></p>\r\n<p><span><img src=\"https://fptshop.com.vn/Uploads/images/2015/xiaomi-12-pro-1(1).png\" alt=\"Điện thoại Xiaomi 12 Pro ch&iacute;nh h&atilde;ng\" /></span></p>\r\n<h3><span>Thiết kế b&oacute;ng bẩy, ho&agrave;n to&agrave;n kh&aacute;c biệt&nbsp;</span></h3>\r\n<p><a href=\"https://fptshop.com.vn/dien-thoai/xiaomi-12-pro\">Xiaomi 12 Pro</a>&nbsp;được tạo n&ecirc;n từ bộ khung kim loại cực gọn nhẹ, c&aacute;c đường n&eacute;t 3D tinh xảo tạo ra n&eacute;t mượt m&agrave; v&agrave; b&oacute;ng bẩy. Khung viền n&agrave;y l&agrave; điểm nhấn của điện thoại khi n&oacute; kh&aacute;c biệt ho&agrave;n to&agrave;n so với những chiếc flagship kh&aacute;c tr&ecirc;n thị trường, tạo ra một xu hướng mới về khung viền cong kết hợp m&agrave;n h&igrave;nh cong tr&agrave;n cạnh.&nbsp;</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/xiaomi-12-pro-2(1).png\" alt=\"Xiaomi 12 Pro - Thiết kế kh&aacute;c biệt&nbsp;\" /></p>\r\n<p>Kh&ocirc;ng chỉ sở hữu hệ thống nhiếp ảnh chất lượng, cụm camera sau c&ograve;n được bảo vệ bởi khung kim loại v&agrave; hiệu ứng v&ograve;ng tr&ograve;n h&agrave;o quang ở c&aacute;c cảm biến, đem đến sự cao cấp khi sử dụng. C&aacute;ch bố tr&iacute; c&aacute;c cảm biến cũng hết sức thẩm mỹ. Xiaomi 12 Pro cung cấp cho người d&ugrave;ng 3 lựa chọn về m&agrave;u sắc, ph&ugrave; hợp mọi c&aacute; t&iacute;nh từ thanh lịch, dịu d&agrave;ng đến mạnh mẽ.&nbsp;</p>\r\n<h3><span>Kh&aacute;m ph&aacute; thế giới trong tầm tay</span></h3>\r\n<p>Với bộ ba camera 50MP c&ugrave;ng những c&ocirc;ng nghệ chụp ảnh, quay video được cải tiến, Xiaomi 12 Pro gi&uacute;p bạn biến mọi khoảnh khắc v&agrave; sắc m&agrave;u tr&ecirc;n thế giới trở th&agrave;nh của m&igrave;nh khi dễ d&agrave;ng được ghi lại. Từ chụp g&oacute;c rộng, g&oacute;c si&ecirc;u rộng hay g&oacute;c si&ecirc;u cận, điện thoại đều cung cấp trải nghiệm tốt nhất. C&aacute;c chế độ như Chế độ ban đ&ecirc;m,&nbsp;<a href=\"https://fptshop.com.vn/tin-tuc/danh-gia/chup-anh-hdr-la-gi-20912\">HDR</a>&nbsp;v&agrave; ​​quay video 4K 60fps&hellip; đem đến sự đa dạng v&agrave; khai th&aacute;c tối đa s&aacute;ng tạo của người d&ugrave;ng.&nbsp;</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/xiaomi-12-pro-3(1).png\" alt=\"Xiaomi 12 Pro - Kh&aacute;m ph&aacute; thế giới trong tầm tay\" /></p>\r\n<h3><span>Thu s&aacute;ng tốt hơn, chụp đ&ecirc;m ấn tượng&nbsp;</span></h3>\r\n<p>Điện thoại Xiaomi 12 Pro thu được nhiều &aacute;nh s&aacute;ng hơn tới 120% v&agrave; nhiều chi tiết hơn khi chụp ảnh ban đ&ecirc;m nhờ v&agrave;o việc sử dụng cảm biến Sony IMX707 si&ecirc;u lớn. Chế độ ban đ&ecirc;m v&agrave; HDR được n&acirc;ng cấp sẽ gi&uacute;p bạn l&agrave;m chủ những cảnh phức tạp trong mọi m&ocirc;i trường trong khi Ch&acirc;n dung ban đ&ecirc;m sẽ biến chủ thể trở n&ecirc;n tự nhi&ecirc;n v&agrave; r&otilde; n&eacute;t hơn d&ugrave; chụp v&agrave;o ban đ&ecirc;m.&nbsp;</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/xiaomi-12-pro-4(1).png\" alt=\"Xiaomi 12 Pro - Chụp đ&ecirc;m ấn tượng&nbsp;\" /></p>\r\n<h3><span>Cung cấp những thước phim mờ ảo trong m&agrave;n đ&ecirc;m</span></h3>\r\n<p>Kh&ocirc;ng chỉ mạnh mẽ về th&ocirc;ng số, thuật to&aacute;n AI được Xiaomi cải tiến sẽ mang đến khả năng quay video ấn tượng hơn nữa. Với việc n&acirc;ng cấp bằng t&iacute;nh năng giảm thiểu video được hỗ trợ bởi Xiaomi AI v&agrave; nhiều chế độ tăng độ s&aacute;ng, giờ đ&acirc;y bạn c&oacute; thể quay phim chất lượng cao ngay cả khi m&ocirc;i trường c&oacute; &aacute;nh s&aacute;ng yếu, tạo ra những thước phim ảo diệu trong m&agrave;n đ&ecirc;m.&nbsp;</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/xiaomi-12-pro-5.png\" alt=\"Xiaomi 12 Pro - Cung cấp những thước phim mờ ảo trong m&agrave;n đ&ecirc;m\" /></p>\r\n<h3><span>Bậc thầy về chụp v&agrave; quay chuyển động&nbsp;</span></h3>\r\n<p>Bằng c&aacute;ch &aacute;p dụng c&ocirc;ng nghệ theo d&otilde;i Xiaomi ProFocus, Xiaomi 12 Pro c&oacute; thể chụp c&aacute;c đối tượng chuyển động r&otilde; r&agrave;ng hơn. C&ocirc;ng nghệ n&agrave;y c&oacute; khả năng x&aacute;c định v&agrave; theo d&otilde;i c&aacute;c chủ thể th&ocirc;ng minh, từ đ&oacute; mang đến bức ảnh r&otilde; n&eacute;t. N&oacute; cũng bao gồm khả năng lấy n&eacute;t tự động bằng nhận diện mắt v&agrave; khu&ocirc;n mặt, tăng khả năng tập trung v&agrave;o chủ thể khi chuyển động.&nbsp;</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/xiaomi-12-pro-6.png\" alt=\"Xiaomi 12 Pro - Bậc thầy về chụp v&agrave; quay chuyển động\" /></p>\r\n<h3><span>Đại tiệc sắc m&agrave;u, l&agrave;m chủ mọi thao t&aacute;c</span></h3>\r\n<p>Kh&ocirc;ng chỉ tối ưu người d&ugrave;ng về nhiếp ảnh, hệ thống m&agrave;n h&igrave;nh của Xiaomi 12 Pro mang lại bữa tiệc thị gi&aacute;c cho người d&ugrave;ng. Với độ s&aacute;ng tối đa 1500 nits, độ tương phản 8.000.000:1, đạt A+ DisplayMate, hỗ trợ&nbsp;<a href=\"https://fptshop.com.vn/tin-tuc/danh-gia/dolby-vision-la-gi-khac-biet-gi-so-voi-hdr10-101940\">HDR10+ v&agrave; Dolby Vision</a>&nbsp;sẽ gi&uacute;p cho bạn dễ d&agrave;ng trải nghiệm ở mọi m&ocirc;i trường &aacute;nh s&aacute;ng m&agrave; kh&ocirc;ng gặp kh&oacute; khăn n&agrave;o.&nbsp;</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/xiaomi-12-pro-7.png\" alt=\"Xiaomi 12 Pro - Đại tiệc sắc m&agrave;u, l&agrave;m chủ mọi thao t&aacute;c\" /></p>\r\n<p>Xiaomi 12 Pro c&oacute; thiết kế m&agrave;n h&igrave;nh cong tr&agrave;n cạnh v&agrave; l&agrave; một bậc thầy về độ r&otilde; n&eacute;t, độ mịn v&agrave; mọi thao t&aacute;c trải nghiệm lu&ocirc;n dẫn đầu khi sử dụng&nbsp;<a href=\"https://fptshop.com.vn/tin-tuc/danh-gia/man-hinh-amoled-la-gi-34866\">tấm nền AMOLED</a>&nbsp;6.73 inch WQHD+ v&agrave; tần số qu&eacute;t 120Hz với c&ocirc;ng nghệ AdaptiveSync Pro - được tận dụng để c&oacute; thể điều chỉnh th&ocirc;ng minh m&agrave;n h&igrave;nh LTPO động trong khoảng từ 1Hz đến 120Hz dựa tr&ecirc;n nội dung tr&ecirc;n m&aacute;y.</p>\r\n<h3><span>&Acirc;m thanh nổi h&agrave;ng đầu</span></h3>\r\n<p>Hệ thống &acirc;m thanh tr&ecirc;n Xiaomi 12 Pro t&iacute;ch hợp SOUND BY Harman Kardon v&agrave; hỗ trợ bởi&nbsp;<a href=\"https://fptshop.com.vn/tin-tuc/danh-gia/cong-nghe-am-thanh-dolby-la-gi-56873\">Dolby Atmos</a>, mang lại giải ph&aacute;p &acirc;m thanh nổi bốn loa cực sống động, dễ d&agrave;ng trải nghiệm &acirc;m thanh chuẩn điện ảnh với loa tweeter k&eacute;p v&agrave; loa trầm k&eacute;p được t&iacute;ch hợp sẵn, cung cấp cho bạn c&aacute;c thanh &acirc;m r&otilde; c&ugrave;ng độ phủ &acirc;m thanh đa dạng.&nbsp;</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/xiaomi-12-pro-8.png\" alt=\"Xiaomi 12 Pro - &Acirc;m thanh nổi h&agrave;ng đầu\" /></p>\r\n<h3><span>Sự đột ph&aacute; về hiệu năng</span></h3>\r\n<p>Kh&ocirc;ng chỉ l&agrave; bước nhảy vọt về hiệu suất, Xiaomi 12 Pro c&ograve;n giữ cho bạn năng lượng hoạt động l&acirc;u d&agrave;i. Với việc sử dụng&nbsp;<a href=\"https://fptshop.com.vn/tin-tuc/tin-moi/qualcomm-snapdragon-8-gen-141279\">Snapdragon 8 Gen 1</a>, bộ vi xử l&yacute; 4nm h&agrave;ng đầu sẽ l&agrave;m chủ tất cả mọi thứ với sức mạnh h&agrave;ng đầu v&agrave; kh&ocirc;ng bị n&oacute;ng. Khả năng kết xuất đồ họa v&agrave; hiệu quả năng lượng cũng được tăng đ&aacute;ng kể so với người tiền nhiệm. Hơn nữa,&nbsp;<a href=\"https://fptshop.com.vn/tin-tuc/danh-gia/mang-5g-la-gi-tong-quan-ve-mang-khong-day-nhanh-nhat-the-gioi-hien-nay-50246\">tốc độ 5G</a>&nbsp;sẽ cho khả năng kết nối si&ecirc;u tốc, tạo n&ecirc;n một tổng h&ograve;a mạnh mẽ đến đ&aacute;ng kinh ngạc.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/xiaomi-12-pro-9.png\" alt=\"Xiaomi 12 Pro - Sự đột ph&aacute; về hiệu năng\" /></p>\r\n<h3><span>Tự tin l&agrave;m mọi thứ bạn muốn</span></h3>\r\n<p>Xiaomi 12 Pro c&oacute; vi&ecirc;n pin lớn 4600mAh d&ugrave; c&oacute; một thiết kế mỏng nhẹ, n&oacute; sẽ lu&ocirc;n giữ cho bạn năng lượng để l&agrave;m những g&igrave; bạn cần. Đặc biệt, với c&ocirc;ng nghệ sạc nhanh Xiaomi HyperCharge 120W, bạn chỉ cần bỏ ra chưa tới 20 ph&uacute;t mỗi s&aacute;ng để nạp đầy năng lượng cho cả ng&agrave;y d&agrave;i. Hơn hết, t&iacute;nh năng theo d&otilde;i th&oacute;i quen sạc của người d&ugrave;ng được t&iacute;ch hợp sẽ đảm bảo tuổi thọ vi&ecirc;n pin cũng như đảm bảo qu&aacute; tr&igrave;nh sạc được diễn ra một c&aacute;ch an to&agrave;n.&nbsp;</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/xiaomi-12-pro-10.png\" alt=\"Xiaomi 12 Pro - Tự tin l&agrave;m mọi thứ bạn muốn\" /></p>', 1, '25690000', 'ad32264f17.jpg', 1),
(24, 'Laptop Asus Gaming Zephyrus GA401QH-K2091W', 2, 12, '<p><strong>Tạo ấn tượng với diện mạo c&aacute; t&iacute;nh v&agrave; tr&agrave;n đầy cảm hứng nghệ thuật, chiếc&nbsp;<a href=\"https://fptshop.com.vn/may-tinh-xach-tay/asus-gaming-zephyrus-ga401qh-k2091w-r7-5800hs\">laptop Asus Gaming Zephyrus GA401QH-K2091W</a>&nbsp;sẽ gi&uacute;p bạn c&oacute; được những trải nghiệm thực sự xuất sắc trong bộ khung vỏ nịnh mắt rất ấn tượng. Sản phẩm ghi nhận k&iacute;ch thước nhỏ nhắn, dễ d&agrave;ng đồng h&agrave;nh c&ugrave;ng bạn mọi l&uacute;c mọi nơi.</strong></p>\r\n<p><img id=\"Laptop Asus Gaming Zephyrus GA401QH-K2091W 11\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/BaoPK/Symbian/laptop-asus-gaming-zephyrus-ga401qh-k2091w-9.JPG\" alt=\"laptop Asus Gaming Zephyrus GA401QH-K2091W 1\" /></p>\r\n<h3><strong>Laptop gaming chưa khi n&agrave;o đẹp đến vậy</strong></h3>\r\n<p>L&agrave; sản phẩm thuộc ph&acirc;n kh&uacute;c cao cấp của&nbsp;<a href=\"https://fptshop.com.vn/may-tinh-xach-tay/asus\">Asus</a>, Zephyrus GA401QH-K2091W được đầu tư ho&agrave;n thiện chỉn chu về mặt diện mạo v&agrave; trang bị cho bộ khung vỏ chế tạo từ chất liệu hợp kim nh&ocirc;m kết hợp giữa nh&ocirc;m &ndash; magie, vừa gia tăng khả năng chịu lực, vừa gi&uacute;p hạn chế lưu mồ h&ocirc;i v&agrave; dấu v&acirc;n tay. Sản phẩm bạn đang theo d&otilde;i được kho&aacute;c l&ecirc;n m&agrave;u x&aacute;m Holographic Gray với họa tiết nhiều m&agrave;u chạy khắp mặt A, đem lại cảm nhận kh&aacute;c biệt so với số đ&ocirc;ng sản phẩm tr&ecirc;n thị trường.</p>\r\n<p><img id=\"Laptop gaming chưa khi n&agrave;o đẹp đến vậy 11\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/BaoPK/Symbian/laptop-asus-gaming-zephyrus-ga401qh-k2091w-0.jpg\" alt=\"laptop Asus Gaming Zephyrus GA401QH-K2091W 2\" /></p>\r\n<h3><strong>Gọn nhẹ v&agrave; thực sự cơ động</strong></h3>\r\n<p>Trong bối cảnh người d&ugrave;ng ng&agrave;y c&agrave;ng đề cao t&iacute;nh thời trang v&agrave; sự cơ động của một chiếc&nbsp;<a href=\"https://fptshop.com.vn/may-tinh-xach-tay\">laptop</a>, Zephyrus GA401QH-K2091W dễ d&agrave;ng đ&aacute;p ứng nhu cầu thẩm mỹ khi ghi nhận độ mỏng th&acirc;n m&aacute;y chỉ 1.8 cm v&agrave; c&oacute; trọng lượng tổng thể chỉ 1.6kg. Nhờ đ&oacute;, chủ nh&acirc;n chiếc m&aacute;y t&iacute;nh sẽ dễ d&agrave;ng cầm theo thiết bị trong mọi chuyến h&agrave;nh tr&igrave;nh m&agrave; kh&ocirc;ng lo đến vấn đề vướng v&iacute;u.</p>\r\n<p><img id=\"Gọn nhẹ v&agrave; thực sự cơ động 11\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/BaoPK/Symbian/laptop-asus-gaming-zephyrus-ga401qh-k2091w-2.JPG\" alt=\"laptop Asus Gaming Zephyrus GA401QH-K2091W 3\" /></p>\r\n<h3><strong>Chip xử l&yacute; AMD Ryzen 7 5800HS xuất sắc</strong></h3>\r\n<p>Ẩn b&ecirc;n trong một cấu tr&uacute;c thiết kế nhỏ nhẹ l&agrave; bộ cấu h&igrave;nh mang tới sức mạnh đ&aacute;ng nể trong mọi t&aacute;c vụ. Asus Gaming Zephyrus GA401QH-K2091W được trang bị chip xử l&yacute; Ryzen 7 5800HS &ndash; đ&acirc;y l&agrave; bộ vi xử l&yacute; thế hệ mới từ AMD gồm 8 nh&acirc;n v&agrave; 16 luồng v&agrave; chế t&aacute;c tr&ecirc;n tiến tr&igrave;nh 7nm, đem lại tốc độ xử l&yacute; ấn tượng nhưng vẫn ti&ecirc;u tốn &iacute;t điện năng trong qu&aacute; tr&igrave;nh vận h&agrave;nh. Nhờ đ&oacute;, chiếc laptop dễ d&agrave;ng xử l&yacute; c&aacute;c tựa game nặng, chạy giả lập hoặc thực hiện t&aacute;c vụ render một c&aacute;ch mượt m&agrave;.</p>\r\n<p><img id=\"Chip xử l&yacute; AMD Ryzen 7 5800HS xuất sắc 11\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/BaoPK/Symbian/laptop-asus-gaming-zephyrus-ga401qh-k2091w-4.JPG\" alt=\"laptop Asus Gaming Zephyrus GA401QH-K2091W 4\" /></p>\r\n<h3><strong>Trải nghiệm card đồ họa RTX 3050 mạnh mẽ</strong></h3>\r\n<p>Laptop Zephyrus GA401QH-K2091W được t&iacute;ch hợp NVIDIA GeForce GTX 1650 &ndash; đ&acirc;u l&agrave; bộ&nbsp;<a href=\"https://fptshop.com.vn/linh-kien/vga\">card đồ họa</a>&nbsp;rời chuy&ecirc;n d&ugrave;ng trong c&aacute;c c&ocirc;ng việc li&ecirc;n quan đến thiết kế đồ họa chuy&ecirc;n nghiệp v&agrave; phục vụ rất tốt cho t&aacute;c vụ chơi game. Bộ GPU n&agrave;y được NVIDIA thiết kế theo kiến tr&uacute;c Turing v&agrave; hướng tới khả năng &eacute;p xung tốt, vận h&agrave;nh &ecirc;m &aacute;i, c&acirc;n bằng giữa hiệu năng v&agrave; điện năng ti&ecirc;u thụ. Mọi d&ograve;ng game hot hiện nay như PUBG hay Apex Legends.</p>\r\n<p><img id=\"Trải nghiệm card đồ họa RTX 3050 mạnh mẽ 11\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/BaoPK/Symbian/laptop-asus-gaming-zephyrus-ga401qh-k2091w-7.JPG\" alt=\"laptop Asus Gaming Zephyrus GA401QH-K2091W 5\" /></p>\r\n<h3><strong>Thể hiện tốc độ ấn tượng trong mọi t&aacute;c vụ</strong></h3>\r\n<p>Asus Zephyrus GA401QH-K2091W sẽ gi&uacute;p bạn cảm nhận được tốc độ ấn tượng trong mọi t&aacute;c vụ sản phẩm thực hiện, từ việc khởi động thiết bị, truyền tệp dữ liệu nặng hay khởi chạy c&aacute;c t&aacute;c vụ phức tạp, tất cả đều được r&uacute;t gọn nhanh ch&oacute;ng nhờ ổ cứng 512GB SSD NVME hiện đại. Kh&ocirc;ng chỉ vậy, việc sở hữu 8GB RAM DDR4 3200 MHz c&ograve;n tạo điều kiện l&yacute; tưởng nhất để sản phẩm vận h&agrave;nh đa nhiệm hiệu quả. Nhờ tốc độ&nbsp;<a href=\"https://fptshop.com.vn/linh-kien/ram\">RAM</a>&nbsp;nhanh nhạy, qu&aacute; tr&igrave;nh livestream hay duyệt web sẽ được thực hiện theo c&aacute;ch mượt m&agrave; nhất.</p>\r\n<p><img id=\"Thể hiện tốc độ ấn tượng trong mọi t&aacute;c vụ 11\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/BaoPK/Symbian/laptop-asus-gaming-zephyrus-ga401qh-k2091w-1.JPG\" alt=\"laptop Asus Gaming Zephyrus GA401QH-K2091W 6\" /></p>\r\n<h3><strong>Ngắm nh&igrave;n kh&ocirc;ng gian game m&atilde;n nh&atilde;n đẹp mắt</strong></h3>\r\n<p>Kh&ocirc;ng để c&aacute;c game thủ phải thất vọng, Asus trang bị cho Zephyrus GA401QH-K2091W kh&ocirc;ng gian hiển thị h&igrave;nh ảnh thuộc h&agrave;ng tốt nhất thế giới hiện nay. Tấm nền Full HD sắc sảo được tinh chỉnh m&agrave;u sắc để mang lại những trải nghiệm đồ họa tốt nhất. Theo những th&ocirc;ng số nh&agrave; sản xuất h&eacute; lộ, m&agrave;n h&igrave;nh 14 inch của Zephyrus GA401QH-K2091W đạt chuẩn m&agrave;u 100% DCI-P3, ghi nhận độ tương phản 1000:1 v&agrave; đạt mức tần số qu&eacute;t 120Hz. Độ ph&acirc;n giải 2.560 x 1.440 pixels gi&uacute;p bạn thoải m&aacute;i tận hưởng những khu&ocirc;n h&igrave;nh sắc n&eacute;t v&agrave; ho&agrave;n hảo nhất.</p>\r\n<p><img id=\"Ngắm nh&igrave;n kh&ocirc;ng gian game m&atilde;n nh&atilde;n đẹp mắt 11\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/BaoPK/Symbian/laptop-asus-gaming-zephyrus-ga401qh-k2091w-3.JPG\" alt=\"laptop Asus Gaming Zephyrus GA401QH-K2091W 7\" /></p>\r\n<h3><strong>&Acirc;m thanh v&ograve;m s&ocirc;i động đầy ch&acirc;n thực</strong></h3>\r\n<p>Sẽ thật thiếu s&oacute;t nếu ch&uacute;ng ta tận hưởng c&aacute;c nội dung giải tr&iacute; đa phương tiện m&agrave; kh&ocirc;ng c&oacute; d&agrave;n &acirc;m thanh đủ chất. Hiểu được điều n&agrave;y, Asus đem đến cho Zephyrus GA401QH-K2091W c&ocirc;ng nghệ Dolby Atmos với chất &acirc;m đa chiều s&ocirc;i động. Trong đ&oacute;, hai loa trầm c&oacute; nhiệm vụ khuếch &acirc;m th&ocirc;ng minh, phản xạ &acirc;m thanh xuống mặt b&agrave;n nhằm thể hiện những tiết tấu đầy nội lực, c&ograve;n hai loa bổng sẽ truyền &acirc;m tới tai bạn để tạo hiệu ứng &acirc;m thanh 360 độ cuốn h&uacute;t.</p>\r\n<p><img id=\"&Acirc;m thanh v&ograve;m s&ocirc;i động đầy ch&acirc;n thực 11\" src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/BaoPK/Symbian/laptop-asus-gaming-zephyrus-ga401qh-k2091w-8.JPG\" alt=\"laptop Asus Gaming Zephyrus GA401QH-K2091W 8\" /></p>', 0, '25990000', '35d41cd64f.jpg', 1),
(25, 'Laptop Asus Zenbook Duo UX482EA-KA274T', 2, 12, '<p><strong><a href=\"https://fptshop.com.vn/may-tinh-xach-tay/asus-zenbook-duo-ux482ea-ka274t-i5-1135g7\">ASUS ZenBook Duo 14 UX482EA</a>&nbsp;l&agrave; chiếc laptop hai m&agrave;n h&igrave;nh si&ecirc;u độc khi bạn c&oacute; thể trải nghiệm đa nhiệm, s&aacute;ng tạo ho&agrave;n to&agrave;n kh&aacute;c biệt so với những g&igrave; bạn từng biết trước đ&acirc;y. Với ZenBook Duo, bạn kh&ocirc;ng chỉ kh&aacute;m ph&aacute; thế giới c&ocirc;ng nghệ của tương lai m&agrave; c&ograve;n c&oacute; thể ph&aacute;t huy hết tiềm năng của bản th&acirc;n nhờ sức s&aacute;ng tạo kh&ocirc;ng giới hạn.</strong></p>\r\n<p><strong><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/Asus-ZenBook-Duo-14-UX482EG-7.jpg\" alt=\"\" /></strong></p>\r\n<h3><strong>Bạn sẽ l&agrave;m được nhiều hơn với 2 m&agrave;n h&igrave;nh</strong></h3>\r\n<p>Kh&ocirc;ng chỉ một m&agrave; c&oacute; tới 2 m&agrave;n h&igrave;nh tr&ecirc;n ASUS ZenBook Duo 14 UX482EA. B&ecirc;n cạnh m&agrave;n h&igrave;nh ch&iacute;nh 14 inch độ ph&acirc;n giải Full HD đạt chuẩn PANTONE Validated về độ ch&iacute;nh x&aacute;c m&agrave;u chuy&ecirc;n nghiệp, ZenBook Duo 14 c&ograve;n c&oacute; m&agrave;n h&igrave;nh phụ ScreenPad Plus với thiết kế nghi&ecirc;ng mới. M&agrave;n h&igrave;nh phụ n&agrave;y c&oacute; khả năng cảm ứng, nghi&ecirc;ng theo g&oacute;c 7 độ, vừa dễ d&agrave;ng thao t&aacute;c, lại vừa n&acirc;ng cao trải nghiệm xem. Với 2&nbsp;<a href=\"https://fptshop.com.vn/man-hinh\">m&agrave;n h&igrave;nh</a>&nbsp;c&ugrave;ng l&uacute;c, năng suất l&agrave;m việc của bạn được n&acirc;ng cao đ&aacute;ng kể nhờ tăng kh&ocirc;ng gian hiển thị v&agrave; tăng cường khả năng đa nhiệm.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/Asus-ZenBook-14-Duo-UX482-3.jpg\" alt=\"\" /></p>\r\n<h3><strong>S&aacute;ng tạo vượt giới hạn</strong></h3>\r\n<p>M&agrave;n h&igrave;nh phụ Screenpad Plus mang đến trải nghiệm ho&agrave;n to&agrave;n mới cho những c&ocirc;ng việc s&aacute;ng tạo như chỉnh sửa ảnh hay bi&ecirc;n tập video. Bạn c&oacute; thể hiển thị ảnh gốc ở một m&agrave;n h&igrave;nh, m&agrave;n h&igrave;nh c&ograve;n lại l&agrave; ảnh đang chỉnh để dễ d&agrave;ng đối chiếu về m&agrave;u sắc; th&ecirc;m nhiều kh&ocirc;ng gian hơn để đặt c&aacute;c c&ocirc;ng cụ bi&ecirc;n tập &acirc;m thanh v&agrave; video. Chỉ với một chiếc&nbsp;<a href=\"https://fptshop.com.vn/may-tinh-xach-tay/khoang-14-inch\">laptop 14 inch</a>&nbsp;nhỏ gọn, nhưng ASUS ZenBook Duo 14 UX482EA dường như cho bạn tận hưởng cả một studio chuy&ecirc;n nghiệp.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/Asus-ZenBook-Duo-14-UX482EG-4.jpg\" alt=\"\" /></p>\r\n<h3><strong>Đa nhiệm chưa bao giờ trực quan đến thế</strong></h3>\r\n<p>Với m&agrave;n h&igrave;nh phụ được thiết kế nghi&ecirc;ng v&agrave; trang bị phần mềm ScreenXpert 2 để li&ecirc;n kết ho&agrave;n hảo với m&agrave;n h&igrave;nh ch&iacute;nh, bạn sẽ c&oacute; rất nhiều thao t&aacute;c s&aacute;ng tạo. Bạn c&oacute; thể k&eacute;o thả c&aacute;c nội dung giữa 2 m&agrave;n h&igrave;nh; mở 2 ứng dụng c&ugrave;ng l&uacute;c tr&ecirc;n 2 m&agrave;n h&igrave;nh; tối ưu h&oacute;a trải nghiệm xem với sự kết nối giữa 2 m&agrave;n h&igrave;nh; sử dụng m&agrave;n h&igrave;nh phụ l&agrave;m một bảng ứng dụng để mở nhanh hay thậm ch&iacute; biến th&agrave;nh b&agrave;n di chuột cảm ứng khổng lồ. Tận dụng c&ugrave;ng l&uacute;c 2 m&agrave;n h&igrave;nh tr&ecirc;n ZenBook Duo gi&uacute;p c&ocirc;ng việc của bạn trở n&ecirc;n dễ d&agrave;ng v&agrave; trực quan hơn.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/Asus-ZenBook-Duo-14-UX482EG-3.jpg\" alt=\"\" /></p>\r\n<h3><strong>B&uacute;t cảm ứng cho c&aacute;c c&ocirc;ng việc hội họa, kiến tr&uacute;c</strong></h3>\r\n<p>Đưa khả năng s&aacute;ng tạo, thiết kế v&agrave; hội họa của bạn l&ecirc;n một tầm cao mới với b&uacute;t cảm ứng stylus. Bạn c&oacute; thể trực tiếp vẽ l&ecirc;n m&agrave;n h&igrave;nh phụ, c&oacute; đầy đủ c&ocirc;ng cụ chỉnh sửa trực quan tr&ecirc;n m&agrave;n h&igrave;nh ch&iacute;nh. C&ocirc;ng việc bi&ecirc;n tập đồ họa sẽ trở n&ecirc;n ch&iacute;nh x&aacute;c v&agrave; dễ d&agrave;ng tr&ecirc;n Zenbook Duo 14.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/Asus-ZenBook-14-Duo-UX482-16.jpg\" alt=\"\" /></p>\r\n<h3><strong>Bảng điều khiển chuy&ecirc;n dụng</strong></h3>\r\n<p><a href=\"https://fptshop.com.vn/may-tinh-xach-tay/asus\">ASUS</a>&nbsp;đ&atilde; li&ecirc;n kết với c&aacute;c ứng dụng phổ biến như Adobe Photoshop, Lightroom Classic, Premier Pro v&agrave; After Effects để biến m&agrave;n h&igrave;nh phụ Screenpad Plus th&agrave;nh một c&ocirc;ng cụ điều khiển Control Panel đầy th&uacute; vị. Control Panel sẽ l&agrave; một b&agrave;n điều khiển cảm ứng với những n&uacute;t thay đổi k&iacute;ch thước, quay, trượt, cuộn v&agrave; đầy đủ thao t&aacute;c để bạn l&agrave;m việc linh hoạt hơn bao giờ hết. H&atilde;ng ASUS cũng sẽ cập nhật Control Panel cho th&ecirc;m nhiều ứng dụng phổ biến kh&aacute;c trong thời gian tới.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/Asus-ZenBook-Duo-14-UX482EG-5.jpg\" alt=\"\" /></p>\r\n<h3><strong>Hiệu năng mạnh mẽ cho mọi t&aacute;c vụ</strong></h3>\r\n<p>ASUS ZenBook Duo 14 UX482EA c&oacute; đủ sức mạnh để đ&aacute;p ứng ho&agrave;n hảo mọi c&ocirc;ng việc của bạn. Bộ vi xử l&yacute; Intel Core i5 1135G7 thế hệ thứ 11 c&oacute; tới 4 nh&acirc;n 8 luồng, tốc độ tối đa l&ecirc;n đến 4.20GHz, t&iacute;ch hợp GPU đồ họa Intel Iris Xe cực mạnh, kh&ocirc;ng chỉ chạy tốt những ứng dụng phổ biến m&agrave; c&ograve;n ho&agrave;n th&agrave;nh xuất sắc c&aacute;c t&aacute;c vụ đồ họa chuy&ecirc;n nghiệp. ZenBook Duo 14 UX482EA c&ograve;n c&oacute; 8GB RAM DDR4 4266MHz v&agrave; ổ cứng 512GB SSD PCIe đảm bảo đa nhiệm tuyệt vời, tốc độ si&ecirc;u nhanh.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/Asus-ZenBook-14-Duo-UX482-1.jpg\" alt=\"\" /></p>\r\n<h3><strong>Đề cao trải nghiệm người d&ugrave;ng</strong></h3>\r\n<p>D&ugrave; t&iacute;ch hợp tới 2 m&agrave;n h&igrave;nh v&agrave; một cấu h&igrave;nh tuyệt vời nhưng&nbsp;<a href=\"https://fptshop.com.vn/may-tinh-xach-tay/asus-zenbook\">ASUS ZenBook</a>&nbsp;lại mỏng nhẹ đ&aacute;ng kinh ngạc. Thiết kế nghi&ecirc;ng của m&aacute;y cũng được cải thiện để tăng cường khả năng l&agrave;m m&aacute;t, đồng thời n&acirc;ng cao trải nghiệm g&otilde; ph&iacute;m v&agrave; g&oacute;c nh&igrave;n của m&agrave;n h&igrave;nh phụ. Thời lượng pin được n&acirc;ng cấp l&ecirc;n tới 17 giờ. Tất cả đều hướng tới sứ mệnh tạo n&ecirc;n chiếc&nbsp;<a href=\"https://fptshop.com.vn/may-tinh-xach-tay/mong-nhe\">laptop mỏng nhẹ</a>&nbsp;v&ocirc; c&ugrave;ng di động, c&oacute; thể phục vụ bạn l&yacute; tưởng ở bất cứ đ&acirc;u.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/Asus-ZenBook-14-Duo-UX482-8.jpg\" alt=\"\" /></p>\r\n<h3><strong>Đăng nhập nhanh ch&oacute;ng bằng nhận diện khu&ocirc;n mặt</strong></h3>\r\n<p>Một camera hồng ngoại tr&ecirc;n ASUS ZenBook Duo 14 UX482EA cho ph&eacute;p bạn đăng nhập ngay lập tức th&ocirc;ng qua nhận diện khu&ocirc;n mặt. Ngay cả trong b&oacute;ng tối, laptop vẫn nhận ra bạn v&agrave; mở kh&oacute;a nhanh ch&oacute;ng.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/Asus-ZenBook-Duo-14-UX482EG-8.jpg\" alt=\"\" /></p>\r\n<h3><strong>&Acirc;m thanh Harman Kardon cao cấp</strong></h3>\r\n<p>&Acirc;m thanh tr&ecirc;n ASUS ZenBook Duo 14 được ph&aacute;t triển bởi đội ngũ ASUS Golden Ear v&agrave; c&aacute;c chuy&ecirc;n gia của Harman Kardon, mang đến chất lượng &acirc;m thanh cao cấp. Sự kết hợp t&ugrave;y chỉnh giữa phần cứng &ndash; trong đ&oacute; c&oacute; c&ocirc;ng nghệ bộ khuếch đại th&ocirc;ng minh cho &acirc;m lượng lớn hơn m&agrave; kh&ocirc;ng bị m&eacute;o tiếng &ndash; v&agrave; phần mềm được thiết kế kh&eacute;o l&eacute;o để bạn c&oacute; được trải nghiệm &acirc;m thanh h&agrave;ng đầu.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/Asus-ZenBook-Duo-14-UX482EG-11.jpg\" alt=\"\" /></p>\r\n<h3><strong>Bền bỉ vượt thời gian</strong></h3>\r\n<p>ASUS ZenBook Duo 14 UX482EA đ&atilde; vượt qua chuẩn qu&acirc;n đội MIL-STD-810G, c&oacute; độ bền đủ để hoạt động được trong những m&ocirc;i trường khắc nghiệt, bao gồm độ cao, nhiệt độ v&agrave; độ ẩm. Kết quả l&agrave; laptop bền bỉ vượt trội so với những m&aacute;y t&iacute;nh th&ocirc;ng thường kh&aacute;c, c&ugrave;ng bạn vượt qua mọi thử th&aacute;ch.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/Asus-ZenBook-14-Duo-UX482-6.jpg\" alt=\"\" /></p>\r\n<h3><strong>Tập hợp những kết nối nhanh nhất</strong></h3>\r\n<p>Những kết nối tr&ecirc;n ASUS ZenBook Duo 14 UX482EA sẽ l&agrave;m bạn phải cho&aacute;ng ngợp. Chiếc m&aacute;y t&iacute;nh si&ecirc;u mỏng n&agrave;y sở hữu những cổng kết nối ti&ecirc;n tiến nhất hiện nay, trong đ&oacute; nổi bật l&agrave; 2 cổng Thunderbolt 4 USB-C thế hệ mới, tốc độ băng th&ocirc;ng l&ecirc;n tới 40Gbps, hỗ trợ Power Delivery v&agrave; DisplayPort, c&oacute; khả năng xuất h&igrave;nh l&ecirc;n tới 8K hoặc 2 m&agrave;n h&igrave;nh 4K. Laptop cũng c&oacute; sẵn c&aacute;c cổng kết nối phổ biến như USB 3.2 Gen 1 Type-A, đầu đọc thẻ microSD, jack tai nghe, cổng HDMI 1.4. Ngo&agrave;i ra với mạng Wi-Fi 6 c&ugrave;ng c&ocirc;ng nghệ Wi-Fi Master Premium của ASUS, tốc độ Wi-Fi được cải thiện đ&aacute;ng kể, tối đa l&ecirc;n tới 2.4Gbps v&agrave; v&ocirc; c&ugrave;ng ổn định cho c&ocirc;ng việc.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/Asus-ZenBook-14-Duo-UX482-2.jpg\" alt=\"\" /></p>', 0, '29590000', '37106f5498.jpg', 1),
(26, 'Laptop Asus Vivobook Pro M3500QC-L1388W', 2, 12, '<p><strong><a href=\"https://fptshop.com.vn/may-tinh-xach-tay/asus-vivobook-pro-m3500qc-l1388w-r5-5600h\">Asus Vivobook Pro M3500QC-L1388W</a>&nbsp;l&agrave; chiếc laptop văn ph&ograve;ng cao cấp với c&ocirc;ng nghệ m&agrave;n h&igrave;nh OLED xuất sắc, thiết kế ấn tượng, cấu h&igrave;nh mạnh mẽ v&agrave; được trang bị nhiều c&ocirc;ng nghệ phục vụ hiệu quả cho c&ocirc;ng việc.</strong></p>\r\n<p><strong><img src=\"https://fptshop.com.vn/Uploads/images/2015/0511/ASUS-Vivobook-Pro-15-OLED-M3500-bac-6.jpg\" alt=\"Laptop Asus Vivobook Pro M3500QC-L1388W 1\" /></strong></p>\r\n<h3><span>Sống động rực rỡ với m&agrave;n h&igrave;nh OLED cao cấp</span></h3>\r\n<p>Asus Vivobook Pro M3500QC được trang bị m&agrave;n h&igrave;nh OLED cao cấp với k&iacute;ch thước 15.6 inch c&ugrave;ng độ ph&acirc;n giải Full HD 1920 x 1080 Pixels cho ra h&igrave;nh ảnh sắc n&eacute;t. C&ocirc;ng nghệ m&agrave;n h&igrave;nh OLED c&oacute; độ s&aacute;ng rất cao l&ecirc;n tới 600 nits đem đến khả năng l&agrave;m việc trong điều kiện &aacute;nh sắng mạnh rất tốt. Tỷ lệ&nbsp;<a href=\"https://fptshop.com.vn/man-hinh\">m&agrave;n h&igrave;nh</a>&nbsp;tr&ecirc;n th&acirc;n m&aacute;y 84% n&ecirc;n k&iacute;ch thước hiển thị rộng v&agrave; tạo n&ecirc;n vẻ đẹp cho chiếc laptop. M&aacute;y đạt chuẩn PANTONE Validated về độ ch&iacute;nh x&aacute;c m&agrave;u chuẩn chuy&ecirc;n nghiệp, độ bao phủ dải m&agrave;u DCI-P3 100% v&agrave; sRGB 133% cho m&agrave;u sắc si&ecirc;u sống động, ch&acirc;n thực, c&aacute;c chi tiết hiển thị xuất sắc v&agrave; độ ch&iacute;nh x&aacute;c m&agrave;u cực kỳ cao. Vivobook Pro M3500QC c&ograve;n đạt chứng chỉ T&Uuml;V Rheinland về mức độ ph&aacute;t &aacute;nh s&aacute;ng xanh thấp gi&uacute;p giảm tới 70% &aacute;nh s&aacute;ng xanh c&oacute; hại v&agrave; cải thiện chất lượng giấc ngủ của bạn. Thời gian phản hồi chỉ 0.2 ms, gần như kh&ocirc;ng c&oacute; độ trễ hiển thị tr&ecirc;n chiếc&nbsp;<a href=\"https://fptshop.com.vn/may-tinh-xach-tay\">laptop</a>&nbsp;n&agrave;y.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/0511/ASUS-Vivobook-Pro-15-OLED-M3500-bac-11.jpg\" alt=\"Laptop Asus Vivobook Pro M3500QC-L1388W 2\" /></p>\r\n<h3><span>Thiết kế mỏng nhẹ, ấn tượng</span></h3>\r\n<p>Vivobook Pro 3500QC c&oacute; thiết kế tối giản với m&agrave;u sắc bạc, mặt lưng được l&agrave;m bằng chất liệu kim loại sang trọng. Điểm nhấn l&agrave; phần logo&nbsp;<a href=\"https://fptshop.com.vn/may-tinh-xach-tay/asus\">Asus</a>&nbsp;được in 3D tạo n&ecirc;n vẻ tinh tế v&agrave; phấn kh&iacute;ch từ c&aacute;i nh&igrave;n đầu ti&ecirc;n. Tổng thể m&aacute;y chỉ nặng 1,65 kg, độ d&agrave;y chưa đến 20 mm dễ d&agrave;ng theo bạn l&agrave;m việc ở bất cứ đ&acirc;u.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/0511/ASUS-Vivobook-Pro-15-OLED-M3500-bac-10.jpg\" alt=\"Laptop Asus Vivobook Pro M3500QC-L1388W 3\" /></p>\r\n<h3><span>Cấu h&igrave;nh xuất sắc với Ryzen 5000 series v&agrave; RTX 3050</span></h3>\r\n<p>L&agrave; một chiếc&nbsp;<a href=\"https://fptshop.com.vn/may-tinh-xach-tay/van-phong\">laptop văn ph&ograve;ng</a>&nbsp;nhưng lại được trang bị cấu h&igrave;nh thường thấy tr&ecirc;n một chiếc laptop gaming với chip AMD Ryzen 5 5600H c&oacute; tốc độ tối đa l&ecirc;n đến 4.20 GHz v&agrave; card đồ họa rời NVIDIA RTX 3050 4 GB, c&oacute; thể n&oacute;i mọi t&aacute;c vụ từ c&ocirc;ng việc văn ph&ograve;ng, đồ họa, kỹ thuật cho đến chơi game giải tr&iacute;, Asus Vivobook Pro M3500QC đều sẽ kh&ocirc;ng l&agrave;m bạn thất vọng. Th&ecirc;m v&agrave;o đ&oacute;, m&aacute;y c&ograve;n sở hữu 16 GB DDR4 bus 3200 MHz đem tới khả năng đa nhiệm c&ugrave;ng l&uacute;c nhiều ứng dụng tốt.&nbsp;<a href=\"https://fptshop.com.vn/linh-kien/o-cung-ssd\">Ổ cứng SSD</a>&nbsp;dung lượng 512 GB cho tốc độ mở c&aacute;c ứng dụng nặng một c&aacute;ch nhanh ch&oacute;ng.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/0511/ASUS-Vivobook-Pro-15-OLED-M3500-bac-15.jpg\" alt=\"Laptop Asus Vivobook Pro M3500QC-L1388W 4\" /></p>\r\n<h3><span>Giữ vững hiệu suất với tản nhiệt IceCool Plus</span></h3>\r\n<p>Cấu h&igrave;nh đ&atilde; xuất sắc,&nbsp;<a href=\"https://fptshop.com.vn/may-tinh-xach-tay/asus-vivobook\">Asus Vivobook</a>&nbsp;c&ograve;n c&oacute; khả năng duy tr&igrave; nhiệt độ m&aacute;t mẻ, tr&aacute;nh hiện tượng hiệu năng bị giảm do m&aacute;y n&oacute;ng l&ecirc;n. Điều n&agrave;y nhờ v&agrave;o hệ thống&nbsp;<a href=\"https://fptshop.com.vn/linh-kien/tan-nhiet\">quạt tản nhiệt</a>&nbsp;k&eacute;p v&agrave; t&iacute;nh năng tản nhiệt n&acirc;ng cao cực kỳ hiệu quả. B&ecirc;n cạnh đ&oacute;, c&ocirc;ng nghệ Asus IceCool Plus c&oacute; 3 chế độ quạt cho bạn lựa chọn th&ocirc;ng qua ứng dụng MyAsus gi&uacute;p tối đa h&oacute;a khả năng tản nhiệt, đem lại hiệu suất tốt nhất mọi l&uacute;c mọi nơi.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/0511/ASUS-Vivobook-Pro-15-OLED-M3500-bac-14.jpg\" alt=\"Laptop Asus Vivobook Pro M3500QC-L1388W 5\" /></p>\r\n<h3><span>Hội họp hiệu quả nhờ c&ocirc;ng nghệ khử tiếng ồn Asus AI</span></h3>\r\n<p>Với xu hướng hiện nay, c&aacute;c cuộc hội thảo, học tập online v&agrave; l&agrave;m việc từ xa c&agrave;ng l&uacute;c c&agrave;ng phổ biến th&igrave; chất lượng thu &acirc;m cũng l&agrave; một yếu tố rất quan trọng của một chiếc laptop văn ph&ograve;ng. V&igrave; vậy, Asus đ&atilde; trang bị c&ocirc;ng nghệ Asus AI cho d&ograve;ng sản phẩm Vivobook Pro của m&igrave;nh. C&ocirc;ng nghệ n&agrave;y sử dụng thuật to&aacute;n để c&ocirc; lập &acirc;m thanh kh&ocirc;ng mong muốn trong c&aacute;c cuộc hội thoại giữa con người với con người nhằm đem lại chất lượng thu &acirc;m tới người kh&aacute;c một c&aacute;ch trong trẻo nhất. C&aacute;c t&iacute;nh năng bổ trợ ClearVoice Mic, Multi-Presenter, ClearVoice Speaker cho khả năng lọc &acirc;m hiệu quả cho từng trường hợp sử dụng cụ thể.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/0511/ASUS-Vivobook-Pro-15-OLED-M3500-bac-7.jpg\" alt=\"Laptop Asus Vivobook Pro M3500QC-L1388W 6\" /></p>\r\n<h3><span>An to&agrave;n v&agrave; bảo mật, &acirc;m thanh Harman Kardon</span></h3>\r\n<p>Asus Vivobook Pro M3500QC t&iacute;ch hợp tr&ecirc;n webcam của m&igrave;nh nắp che để bảo vệ quyền ri&ecirc;ng tư của bạn, tr&aacute;nh những soi m&oacute;i v&agrave; sự cố đ&aacute;ng tiếc c&oacute; thể xảy ra. B&ecirc;n cạnh đ&oacute;, để tăng t&iacute;nh bảo mật, m&aacute;y c&ograve;n được trang bị cảm biến v&acirc;n tay một chạm ngay tr&ecirc;n n&uacute;t nguồn gi&uacute;p bạn c&oacute; thể dễ d&agrave;ng mở m&aacute;y m&agrave; kh&ocirc;ng cần nhập mật khẩu v&agrave; độ an to&agrave;n cũng cao hơn.</p>\r\n<p>Với hệ thống &acirc;m thanh của Harman Kardon trứ danh, Vivobook Pro M3500QC mang lại &acirc;m thanh sống động, chất &acirc;m tốt gi&uacute;p bạn c&oacute; những giờ ph&uacute;t tuyệt vời đắm ch&igrave;m trong c&aacute;c bộ phim v&agrave; tựa game.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/0511/ASUS-Vivobook-Pro-15-OLED-M3500-bac-5.jpg\" alt=\"Laptop Asus Vivobook Pro M3500QC-L1388W 7\" /></p>\r\n<h3><span>Kết nối to&agrave;n diện, thời lượng sử dụng pin tốt</span></h3>\r\n<p>Asus Vivobook Pro M3500QC-L1388W cho ph&eacute;p bạn kết nối một c&aacute;ch to&agrave;n diện với đầy đủ c&aacute;c cổng kết nối phổ biến hiện nay: 2 cổng USB 2.0, 1 cổng USB 3.2, 1 cổng USB Type-C, 1 cổng HDMI, 1 khe cắm thẻ nhớ v&agrave; 1 jack tai nghe 3.5 mm. Bạn c&oacute; thể dễ d&agrave;ng kết nối với c&aacute;c thiết bị ngoại vi, m&agrave;n h&igrave;nh rời, m&aacute;y chiếu để phục vụ c&ocirc;ng việc hay hội họp hiệu quả.</p>\r\n<p>Sở hữu pin dung lượng 63 Wh, Asus Vivobook Pro M3500QC đủ đ&aacute;p ứng cho bạn một ng&agrave;y l&agrave;m việc khi thời lượng sử dụng thực tế được khoảng 6 tiếng li&ecirc;n tục.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/0511/ASUS-Vivobook-Pro-15-OLED-M3500-bac-16.jpg\" alt=\"Laptop Asus Vivobook Pro M3500QC-L1388W 8\" /></p>', 0, '24690000', '5695f6b9e7.jpg', 1),
(27, 'Laptop Asus Gaming ROG Flow Z13 GZ301Z-LD110W', 2, 12, '<p><strong><a href=\"https://fptshop.com.vn/may-tinh-xach-tay/asus-rog-flow-z13-gz301z-ld110w-i7-12700h\">ASUS ROG Flow Z13</a>&nbsp;cho bạn một c&aacute;i nh&igrave;n ho&agrave;n to&agrave;n kh&aacute;c biệt về laptop gaming, nơi bạn được tận hưởng sự linh hoạt đến kh&oacute; tin với chiếc m&aacute;y t&iacute;nh trọng lượng chỉ 1,1 kg, c&oacute; thể th&aacute;o rời b&agrave;n ph&iacute;m để sử dụng đa năng. Tất nhi&ecirc;n b&ecirc;n trong ROG Flow Z13 l&agrave; cấu h&igrave;nh đỉnh nhất hiện nay khi trang bị bộ vi xử l&yacute; Intel thế hệ thứ 12.</strong></p>\r\n<p><strong><img src=\"https://fptshop.com.vn/Uploads/images/2015/0511/ASUS-ROG-Flow-Z13-1.jpg\" alt=\"ASUS ROG Flow Z13 (ảnh 1)\" /></strong></p>\r\n<h3><strong>Si&ecirc;u phẩm laptop v&agrave; tablet gaming trong một thiết bị duy nhất</strong></h3>\r\n<p>Với khả năng th&aacute;o rời b&agrave;n ph&iacute;m, bạn c&oacute; thể chơi game với vai tr&ograve; laptop hoặc tablet t&ugrave;y th&iacute;ch tr&ecirc;n ASUS ROG Flow Z13. D&ugrave; l&agrave; ở bất cứ dạng n&agrave;o, đ&acirc;y cũng l&agrave; một sản phẩm v&ocirc; c&ugrave;ng mạnh mẽ. Chiến những tựa game PC với b&agrave;n ph&iacute;m v&agrave; chuột th&ocirc;ng thường hoặc chơi game mượt m&agrave; qua m&agrave;n h&igrave;nh cảm ứng, ROG Flow Z13 mang đến trải nghiệm ho&agrave;n to&agrave;n kh&aacute;c biệt. Nhờ trọng lượng chỉ 1,18kg v&agrave; độ mỏng 12mm, ROG Flow Z13 l&agrave;&nbsp;<a href=\"https://fptshop.com.vn/may-tinh-xach-tay/gaming-do-hoa\">m&aacute;y t&iacute;nh&nbsp;chơi game</a>&nbsp;linh hoạt nhất m&agrave; bạn từng thấy, dễ d&agrave;ng để lu&ocirc;n đồng h&agrave;nh c&ugrave;ng bạn đi bất cứ đ&acirc;u.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/0511/ASUS-ROG-Flow-Z13-fpt-2.jpg\" alt=\"ASUS ROG Flow Z13 (ảnh 2)\" /></p>\r\n<h3><strong>Thiết kế tinh xảo đến từ tương lai</strong></h3>\r\n<p>L&agrave; một chiếc laptop gọn g&agrave;ng nhưng ASUS ROG Flow Z13 ẩn chứa rất nhiều b&iacute; mật qua những họa tiết tinh xảo trong thiết kế. Ng&ocirc;n ngữ thiết kế của Flow Z13 theo phong c&aacute;ch h&agrave;ng kh&ocirc;ng vũ trụ với loạt họa tiết độc đ&aacute;o tr&ecirc;n bề mặt hợp kim nh&ocirc;m. Lớp phủ chống v&acirc;n tay cao cấp v&agrave; c&aacute;c họa tiết được cắt CNC tuyệt đẹp đi k&egrave;m đ&egrave;n LED RGB tạo n&ecirc;n sự đẳng cấp, kh&aacute;c biệt m&agrave; c&aacute;c game thủ sẽ rất th&iacute;ch th&uacute;.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/0511/ASUS-ROG-Flow-Z13-fpt-7.jpg\" alt=\"ASUS ROG Flow Z13 (ảnh 3)\" /></p>\r\n<h3><strong>Trải nghiệm chơi game ho&agrave;n hảo</strong></h3>\r\n<p>ASUS ROG Flow Z13 được x&acirc;y dựng cho hoạt động chơi game chuy&ecirc;n nghiệp với loạt t&iacute;nh năng ho&agrave;n hảo.&nbsp;<a href=\"https://fptshop.com.vn/phu-kien/ban-phim\">B&agrave;n ph&iacute;m</a>&nbsp;t&iacute;ch hợp ki&ecirc;m tấm bảo vệ m&agrave;n h&igrave;nh, h&agrave;nh tr&igrave;nh ph&iacute;m 1,7mm, thiết kế chuy&ecirc;n dụng cho game thủ cho ph&eacute;p bạn thao t&aacute;c ch&iacute;nh x&aacute;c v&agrave; thoải m&aacute;i, ch&acirc;n đế c&oacute; khả năng điều chỉnh 170 độ lu&ocirc;n đưa bạn v&agrave;o g&oacute;c nh&igrave;n thuận lợi nhất. ROG Flow Z13 c&ograve;n hỗ trợ kết nối 4 tay cầm chơi game c&ugrave;ng l&uacute;c, để bạn thỏa sức chơi game với bạn b&egrave;. Tr&ecirc;n Z13 c&oacute; đầy đủ c&aacute;c cổng kết nối USB Type-C đa năng v&agrave; USB Type-A phổ biến để bạn kết nối với nhiều thiết bị phục vụ cho c&ocirc;ng việc, chơi game chuy&ecirc;n nghiệp hơn.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/0511/ASUS-ROG-Flow-Z13-4.jpg\" alt=\"ASUS ROG Flow Z13 (ảnh 4)\" /></p>\r\n<h3><strong>Sức mạnh vượt trội, xứng tầm đẳng cấp</strong></h3>\r\n<p>Trong một thiết kế si&ecirc;u di động, ASUS Gaming ROG Flow Z13 GZ301Z-LD110W vẫn t&iacute;ch hợp cấu h&igrave;nh cực khủng với bộ vi xử l&yacute; Intel Core i7 12700H. Đ&acirc;y l&agrave; con chip thuộc thế hệ thứ 12&nbsp;<a href=\"https://fptshop.com.vn/linh-kien/cpu-intel-alder-lake\">Alder Lake</a>&nbsp;mới nhất, c&oacute; tới 14 l&otilde;i 20 luồng, tốc độ Turbo tối đa 4.70 GHz, 24MB Intel Smart Cache. Sức mạnh của Intel Core i7 12700H c&oacute; thể đ&aacute;p ứng được những c&ocirc;ng việc chuy&ecirc;n nghiệp v&agrave; c&aacute;c tựa game bom tấn AAA mới nhất.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/0511/ASUS-ROG-Flow-Z13-fpt-1.jpg\" alt=\"ASUS ROG Flow Z13 (ảnh 5)\" /></p>\r\n<h3><strong>Đồ họa tuyệt vời với RTX 3050</strong></h3>\r\n<p>Bạn c&oacute; nghĩ một chiếc Table Gaming lại c&oacute; thể t&iacute;ch hợp GPU đồ họa RTX 3050 được kh&ocirc;ng, điều kh&ocirc;ng thể đ&atilde; trở th&agrave;nh sự thực với ASUS ROG Flow Z13. Nhờ GPU thế hệ mới nhất, ASUS Gaming ROG Flow Z13 GZ301Z-LD110W cho bạn trải nghiệm h&igrave;nh ảnh tr&ograve; chơi đắm ch&igrave;m với c&ocirc;ng nghệ d&ograve; tia Ray Tracing ch&acirc;n thực; hiệu năng được tăng tốc nhờ AI qua c&ocirc;ng nghệ DLSS; đồng thời kiến tr&uacute;c Max-Q sẽ tối ưu h&oacute;a hiệu suất v&agrave; nhiệt độ để một chiếc&nbsp;<a href=\"https://fptshop.com.vn/may-tinh-xach-tay\">laptop nhỏ gọn</a>&nbsp;c&oacute; thể t&iacute;ch hợp được cấu h&igrave;nh mạnh mẽ.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/0511/ASUS-ROG-Flow-Z13-fpt-20.jpg\" alt=\"ASUS ROG Flow Z13 (ảnh 6)\" /></p>\r\n<h3><strong>Tương th&iacute;ch với GPU đồ họa rời XG Mobile</strong></h3>\r\n<p>Nếu cần một sức mạnh đồ họa thực sự đột ph&aacute;, bạn c&oacute; thể sử dụng GPU đồ họa rời XG Mobile cho ASUS ROG Flow Z13. Chỉ mất v&agrave;i ph&uacute;t kết nối, bạn sẽ được tận hưởng sức mạnh của &ldquo;con qu&aacute;i vật&rdquo; RTX 3080 hoặc AMD RX 6850M XT, những si&ecirc;u&nbsp;<a href=\"https://fptshop.com.vn/linh-kien/vga\">card m&agrave;n h&igrave;nh</a>&nbsp;kh&ocirc;ng ng&aacute;n bất cứ tựa game n&agrave;o hiệu nay.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/0511/ASUS-ROG-Flow-Z13-7.jpg\" alt=\"ASUS ROG Flow Z13 (ảnh 7)\" /></p>\r\n<h3><strong>Tăng tốc c&ugrave;ng RAM LPDDR5</strong></h3>\r\n<p>ROG Flow Z13 c&oacute; sẵn 16GB RAM LPDDR5 5200MHz kh&ocirc;ng chỉ tốc độ nhanh hơn, đa nhiệm hiệu quả hơn m&agrave; c&ograve;n tiết kiệm điện năng, cho bạn năng suất l&agrave;m việc cao nhất nhưng vẫn rất tiết kiệm pin.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/0511/ASUS-ROG-Flow-Z13-fpt-8.jpg\" alt=\"ASUS ROG Flow Z13 (ảnh 8)\" /></p>\r\n<h3><strong>Hệ thống tản nhiệt đột ph&aacute;</strong></h3>\r\n<p>Để trang bị được sức mạnh của bộ vi xử l&yacute; Intel Core i7 12700H v&agrave; GPU RTX 3050 trong một thiết kế nhỏ gọn, ASUS ROG Flow Z13 cần c&oacute; một giải ph&aacute;p tản nhiệt ho&agrave;n to&agrave;n mới. Lợi thế từ một thiết kế kh&ocirc;ng c&oacute; bản lề gi&uacute;p ROG Flow Z13 c&oacute; th&ecirc;m kh&ocirc;ng gian b&ecirc;n trong m&aacute;y để thiết kế c&aacute;nh quạt lớn hơn v&agrave; buồng kh&iacute; rộng hơn, đồng thời c&aacute;c&nbsp;<a href=\"https://fptshop.com.vn/linh-kien\">linh kiện</a>&nbsp;ph&aacute;t nhiệt của m&aacute;y sẽ được bố tr&iacute; cao hơn mặt b&agrave;n khi hoạt động, do vậy luồng kh&iacute; lưu th&ocirc;ng sẽ hiệu quả hơn c&aacute;c mẫu laptop truyền thống. Sự kết hợp của keo tản nhiệt kim loại lỏng, tản nhiệt buồng hơi, c&ocirc;ng nghệ 0dB, quạt Arc Flow thế hệ mới, l&aacute; tản nhiệt si&ecirc;u mỏng 0,15mm gi&uacute;p ASUS ROG Flow Z13 tản nhiệt một c&aacute;ch dễ d&agrave;ng v&agrave; y&ecirc;n tĩnh, ngay cả khi bạn hoạt động ở cường độ cao.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/0511/ASUS-ROG-Flow-Z13-fpt-7.jpg\" alt=\"ASUS ROG Flow Z13 (ảnh 9)\" /></p>\r\n<h3><strong>M&agrave;n h&igrave;nh tuyệt đỉnh cho game thủ</strong></h3>\r\n<p>M&agrave;n h&igrave;nh&nbsp;<a href=\"https://fptshop.com.vn/may-tinh-xach-tay/asus-rog\">laptop ASUS ROG</a>&nbsp;kh&ocirc;ng chỉ c&oacute; chất lượng hiển thị tuyệt vời m&agrave; c&ograve;n si&ecirc;u mượt với tần số qu&eacute;t 120Hz, đồng thời hỗ trợ cảm ứng cực nhạy, đ&aacute;p ứng ho&agrave;n hảo nhu cầu chơi game của game thủ. Tỉ lệ rộng 16:10, tấm nền IPS Glossy cao cấp, độ s&aacute;ng tối đa l&ecirc;n tới 500 nits, viền mỏng 4 cạnh gi&uacute;p những tựa game được thể hiện một c&aacute;ch sinh động, trực quan đầy hứng khởi. D&ugrave; l&agrave; chơi game bằng b&agrave;n ph&iacute;m, tay cầm hay thao t&aacute;c vuốt chạm trực tiếp l&ecirc;n m&agrave;n h&igrave;nh, ROG Flow Z13 cũng đ&aacute;p ứng ho&agrave;n hảo.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/0511/ASUS-ROG-Flow-Z13-6.jpg\" alt=\"ASUS ROG Flow Z13 (ảnh 10)\" /></p>\r\n<h3><strong>V&ocirc; c&ugrave;ng di động</strong></h3>\r\n<p>B&ecirc;n cạnh thiết kế mỏng nhẹ, ASUS ROG Flow Z13 c&ograve;n được tối ưu cho khả năng di động qua mạng WiFi 6E mới nhất, giảm độ trễ v&agrave; tăng sự ổn định kết nối, ngay cả khi bạn đang truy cập nguồn mạng WiFi đ&ocirc;ng người. Tốc độ nhanh hơn 3 lần, độ trễ thấp hơn 75% v&agrave; tăng cường khả năng kết nối tới 4 lần so với WiFi 5 trước đ&acirc;y, bạn ho&agrave;n to&agrave;n y&ecirc;n t&acirc;m về kết nối Internet. ROG Flow Z13 cũng hỗ trợ c&ocirc;ng nghệ sạc nhanh với bộ nguồn 100W, sạc được 50% pin chỉ sau 30 ph&uacute;t. Đặc biệt,&nbsp;<a href=\"https://fptshop.com.vn/linh-kien/nguon-may-tinh\">bộ nguồn</a>&nbsp;n&agrave;y cũng rất nhỏ gọn khi chỉ nặng 300 gram, dễ d&agrave;ng cho v&agrave;o t&uacute;i x&aacute;ch.</p>\r\n<p><img src=\"https://fptshop.com.vn/Uploads/images/2015/0511/ASUS-ROG-Flow-Z13-5.jpg\" alt=\"ASUS ROG Flow Z13 (ảnh 11)\" /></p>', 1, '47490000', 'dba29860d0.jpg', 1);
